INSERT INTO topic_documents (topic_id, text, weight) VALUES 
(1, 'Bộ Công an công bố kế hoạch tổng thể về bảo đảm an ninh trật tự xã hội và phòng chống tội phạm xuyên suốt năm 2024.', 0.95),
