INSERT INTO topic_documents (topic_id, text, weight) VALUES
(2, 'Interpol đã phát lệnh truy nã đỏ đối với một trùm tội phạm ma túy khét tiếng người Colombia sau cuộc điều tra kéo dài 6 tháng.', 0.85),
