INSERT INTO topic_documents (topic_id, text, weight) VALUES
(3, 'Cảnh sát giao thông Hà Nội phạt nguội hơn 500 trường hợp vi phạm tốc độ và lấn làn chỉ trong tuần đầu tháng 12.', 0.9),
