INSERT INTO topic_documents (topic_id, text, weight) VALUES
(4, 'Tàu hộ vệ tên lửa 016-Quang Trung thuộc Hải quân Việt Nam đã hoàn thành tốt đẹp chuyến thăm hữu nghị tại cảng Changi, Singapore.', 0.85),
