INSERT INTO topic_documents (topic_id, text, weight) VALUES
(5, 'Bộ Quốc phòng công bố nhiều điểm mới trong quy chế tuyển sinh quân sự năm 2025 nhằm thu hút nhân tài cho các học viện, nhà trường quân đội.', 0.9),
