INSERT INTO topic_documents (topic_id, text, weight) VALUES
(6, 'Cảnh sát PCCC TP.HCM dập tắt thành công đám cháy lớn tại kho chứa vật liệu dễ cháy trong khu công nghiệp, ngăn chặn cháy lan.', 0.95),
