INSERT INTO topic_documents (topic_id, text, weight) VALUES
(7, 'Bộ Y tế đưa ra các khuyến nghị về chế độ dinh dưỡng cân bằng và hợp lý cho người dân, đặc biệt trong mùa dịch bệnh.', 0.95),
