(8, 'Cục Quản lý thị trường vừa triệt phá một cơ sở sản xuất yến sào giả, sử dụng tinh bột và hóa chất để làm tăng trọng lượng sản phẩm.', 0.94),
(8, 'Người tiêu dùng cần nắm rõ cách phân biệt yến sào thật và yến giả qua mùi vị, độ đàn hồi và màu sắc khi ngâm nước.', 0.91),
(8, 'Các mô hình nuôi yến nhà tại miền Tây áp dụng công nghệ thông minh để kiểm soát nhiệt độ và độ ẩm, nâng cao chất lượng tổ yến.', 0.85),
(8, 'Sản phẩm yến sào chưng sẵn tiện lợi đang trở thành xu hướng tiêu dùng được giới văn phòng và người già ưa chuộng.', 0.82),
(8, 'Hiệp hội Yến sào Việt Nam đề nghị siết chặt quy trình kiểm định chất lượng yến sào xuất khẩu nhằm đáp ứng tiêu chuẩn quốc tế.', 0.9),
(8, 'Nghiên cứu khoa học chứng minh yến sào có khả năng hỗ trợ tăng cường hệ miễn dịch và cải thiện chức năng hô hấp.', 0.89),
(8, 'Tỉnh Khánh Hòa, nơi được mệnh danh là thủ phủ yến sào, tổ chức lễ hội quảng bá giá trị dinh dưỡng của sản phẩm.', 0.78),
(8, 'Cảnh báo về việc mua yến sào trôi nổi, không tem nhãn, không rõ nguồn gốc xuất xứ tại các khu du lịch.', 0.93),
(8, 'Các công ty yến sào lớn cam kết truy xuất nguồn gốc từ khâu thu hoạch đến khâu đóng gói thành phẩm.', 0.86),
(8, 'Phân tích hàm lượng dinh dưỡng cho thấy yến sào đảo thiên nhiên có giá trị cao hơn so với yến nhà nuôi.', 0.84),
(8, 'Giá trị kinh tế của ngành nuôi yến Việt Nam đạt mức kỷ lục trong năm qua, đóng góp lớn vào kim ngạch xuất khẩu nông sản.', 0.92),
(8, 'Báo cáo chỉ ra yến sào có tác dụng tốt trong việc làm đẹp da và chống lão hóa cho phụ nữ tuổi trung niên.', 0.79),
(8, 'Các hộ nuôi yến phải đối mặt với khó khăn do chi phí đầu tư nhà yến và công nghệ dẫn dụ chim yến tăng cao.', 0.75),
(8, 'Sản phẩm yến sào chế biến với các loại hạt và thảo dược đang thu hút sự quan tâm của thị trường quà biếu Tết.', 0.81),
(8, 'Một công ty yến sào bị phạt nặng vì quảng cáo sai sự thật về công dụng chữa bệnh của sản phẩm.', 0.9),
(8, 'Quy trình thu hoạch yến sào tại các hang đảo tự nhiên phải tuân thủ nghiêm ngặt các quy định bảo tồn chim yến.', 0.87),
(8, 'Yến sào đóng lọ nhỏ gọn được người tiêu dùng lựa chọn để bồi bổ sức khỏe hàng ngày thay cho các loại nước uống khác.', 0.83),
(8, 'Sự khác biệt về giá giữa yến sào huyết, hồng yến và bạch yến trên thị trường được giải thích dựa trên độ hiếm và hàm lượng dinh dưỡng.', 0.91),
(8, 'Các chuyên gia khuyến cáo không nên sử dụng yến sào quá liều lượng có thể gây lãng phí dưỡng chất.', 0.77),
(8, 'Thị trường yến sào xơ mướp (yến vụn) có giá thấp hơn nhưng vẫn đảm bảo giá trị dinh dưỡng cơ bản.', 0.76),
(8, 'Đề xuất thành lập Hiệp hội Bảo tồn và Phát triển Yến sào nhằm kiểm soát chất lượng và chống hàng giả.', 0.88),
(8, 'Các nhà nghiên cứu đang tìm cách tối ưu hóa môi trường nuôi yến nhà để cho ra sản phẩm chất lượng tương đương yến đảo.', 0.85),
(8, 'Cảnh báo về việc sử dụng yến sào kém chất lượng có thể gây hại cho sức khỏe, đặc biệt là trẻ nhỏ và người bệnh.', 0.94),
(8, 'Yến sào được chế biến thành các món ăn chay cao cấp, phục vụ nhu cầu ẩm thực đa dạng của người tiêu dùng.', 0.79),
(8, 'Chính sách hỗ trợ vay vốn cho các hộ dân có nhu cầu xây nhà yến mới tại các vùng ven biển tiềm năng.', 0.75),
(8, 'Công ty yến sào A vừa đạt chứng nhận ISO 22000 về hệ thống quản lý an toàn thực phẩm, củng cố niềm tin khách hàng.', 0.84),
(8, 'Giá yến sào sơ chế (lông đã được làm sạch) luôn cao hơn yến thô khoảng 30-40% do chi phí nhân công làm sạch.', 0.81),
(8, 'Cục Thú y đang nghiên cứu các biện pháp kiểm soát dịch bệnh trên đàn chim yến nuôi để bảo vệ ngành công nghiệp này.', 0.86);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(9, 'Quán phở gia truyền tại phố cổ Hà Nội thu hút du khách quốc tế bởi hương vị nước dùng đậm đà, giữ nguyên công thức 70 năm.', 0.95),
(9, 'Món bún chả Hà Nội lọt vào danh sách 10 món ăn đường phố ngon nhất thế giới do tạp chí ẩm thực quốc tế bình chọn.', 0.92),
(9, 'Chuyên gia ẩm thực chia sẻ công thức nấu nước dùng phở bò chuẩn vị, bí quyết nằm ở khâu nướng gừng, hành và hầm xương.', 0.9),
(9, 'Phở cuốn và phở chiên phồng trở thành món ăn vặt được giới trẻ yêu thích, tạo nên sự biến tấu độc đáo từ món phở truyền thống.', 0.85),
(9, 'TP.HCM tổ chức Ngày hội Phở Việt, thu hút hàng chục nghìn lượt khách tham quan và thưởng thức các loại phở từ ba miền.', 0.88),
(9, 'Sợi bún tươi tại các chợ truyền thống đang bị kiểm tra gắt gao về việc sử dụng hàn the và các chất phụ gia độc hại.', 0.93),
(9, 'Đề xuất đưa Phở Việt Nam vào danh sách Di sản văn hóa phi vật thể của UNESCO nhằm quảng bá giá trị ẩm thực quốc gia.', 0.91),
(9, 'Nhiều quán bún đậu mắm tôm áp dụng mô hình kinh doanh nhượng quyền, mở rộng chuỗi cửa hàng ra các tỉnh thành.', 0.78),
(9, 'Công thức làm miến trộn kiểu Hàn Quốc kết hợp với nguyên liệu truyền thống Việt Nam đang tạo nên cơn sốt ẩm thực mới.', 0.75),
(9, 'Văn hóa ăn Phở tại Việt Nam không chỉ là bữa ăn mà còn là nét văn hóa giao tiếp và sinh hoạt cộng đồng.', 0.87),
(9, 'Giá nguyên liệu chế biến phở như thịt bò, xương và các loại gia vị tăng nhẹ, khiến nhiều quán ăn phải điều chỉnh giá bán.', 0.84),
(9, 'Bún bò Huế, với hương vị cay nồng đặc trưng, được đánh giá là một trong những món ăn đáng thử nhất khi đến miền Trung.', 0.86),
(9, 'Các đầu bếp trẻ tìm tòi, sáng tạo ra món phở chay sử dụng nấm và các loại rau củ, đáp ứng nhu cầu ăn chay ngày càng tăng.', 0.82),
(9, 'Vụ việc một quán hủ tiếu bị phạt vì vi phạm nghiêm trọng vệ sinh an toàn thực phẩm gây lo ngại trong dư luận.', 0.94),
(9, 'Nghiên cứu về lịch sử Phở Việt Nam khẳng định món ăn này có nguồn gốc từ Bắc Bộ và dần lan tỏa khắp cả nước.', 0.89),
(9, 'Món bún riêu cua đồng truyền thống đòi hỏi kỹ thuật chế biến cầu kỳ và nguồn nguyên liệu tươi ngon, đảm bảo độ chuẩn vị.', 0.81),
(9, 'Các hãng hàng không Việt Nam cân nhắc đưa phở và bún vào thực đơn phục vụ hành khách trên các chuyến bay quốc tế.', 0.83),
(9, 'Phở gà trộn khô là một biến thể hấp dẫn, được nhiều người lựa chọn thay vì phở nước truyền thống trong những ngày hè.', 0.79),
(9, 'Các trường dạy nấu ăn tổ chức lớp học chuyên đề về bí quyết làm bún mắm miền Tây chuẩn vị, đậm đà hải sản.', 0.77),
(9, 'Tiểu thương bán bún tại chợ Vĩnh Hưng cam kết chỉ sử dụng bún được sản xuất tại cơ sở có chứng nhận vệ sinh an toàn.', 0.85),
(9, 'Chuyên đề "Phở và sức khỏe" chỉ ra cách cân bằng dinh dưỡng khi thưởng thức món ăn này hàng ngày.', 0.76),
(9, 'Nhiều người nước ngoài sau khi thưởng thức đã quyết định mở quán phở Việt tại quê hương mình.', 0.88),
(9, 'Phở sắn Quãng, một món ăn đặc sản của Quảng Nam, được giới thiệu trong chương trình quảng bá ẩm thực địa phương.', 0.74),
(9, 'Việc sử dụng nước hầm xương ống có thể làm tăng cholesterol, chuyên gia khuyến cáo nên hạn chế ăn quá thường xuyên.', 0.80),
(9, 'Quán bún thang nổi tiếng với công thức nước dùng thanh tao, cầu kỳ trong việc chuẩn bị các loại topping.', 0.87),
(9, 'Công ty sản xuất phở ăn liền nỗ lực cải tiến công nghệ để giữ được hương vị phở truyền thống trong sản phẩm đóng gói.', 0.9),
(9, 'Khảo sát cho thấy bún chả là món ăn được đặt hàng qua ứng dụng giao thức ăn nhiều nhất trong quý vừa qua.', 0.83),
(9, 'Phở khô Gia Lai được xem là một đại diện tiêu biểu cho sự đa dạng của ẩm thực phở tại khu vực Tây Nguyên.', 0.79),
(9, 'Giá bán phở tại các nhà hàng cao cấp có xu hướng tăng nhẹ do chi phí thuê mặt bằng và nhân công tăng cao.', 0.75),
(9, 'Đài truyền hình quốc gia thực hiện phóng sự về hành trình của hạt gạo để tạo nên sợi bún, phở truyền thống Việt Nam.', 0.86);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(10, 'Giá sầu riêng xuất khẩu lập kỷ lục mới nhờ nhu cầu mạnh mẽ từ thị trường Trung Quốc, mang lại lợi nhuận cao cho nông dân.', 0.95),
(10, 'Thị trường rau xanh tăng giá mạnh do ảnh hưởng của đợt mưa lớn kéo dài tại các tỉnh miền Bắc, nguồn cung bị giảm sút.', 0.92),
(10, 'Bộ Nông nghiệp và Phát triển Nông thôn khuyến nghị nông dân chuyển đổi mô hình trồng rau hữu cơ áp dụng công nghệ nhà kính.', 0.9),
(10, 'Hải quan Trung Quốc cấp mã số vùng trồng cho thêm 50 khu vực trồng khoai lang, tạo điều kiện thuận lợi cho xuất khẩu chính ngạch.', 0.88),
(10, 'Các siêu thị lớn đồng loạt cam kết chỉ bán rau củ quả có chứng nhận VietGAP, tăng cường niềm tin cho người tiêu dùng.', 0.85),
(10, 'Cảnh báo về tình trạng rau củ quả chứa dư lượng thuốc bảo vệ thực vật vượt quá mức cho phép tại một số chợ đầu mối.', 0.93),
(10, 'Mô hình trồng dưa lưới trong nhà màng tại Lâm Đồng cho hiệu quả kinh tế cao và chất lượng sản phẩm đồng đều.', 0.87),
(10, 'Sản lượng và giá thanh long sụt giảm do khó khăn trong khâu vận chuyển và tiêu thụ, cần có giải pháp hỗ trợ nông dân.', 0.84),
(10, 'Việt Nam và EU ký kết thỏa thuận tăng cường xuất khẩu các loại trái cây nhiệt đới như chôm chôm, xoài, vải thiều.', 0.89),
(10, 'Các chuyên gia dinh dưỡng khuyến khích ăn nhiều loại rau có màu xanh đậm và rau ăn lá để bổ sung vitamin K và Folate.', 0.86),
(10, 'Hà Nội triển khai chương trình liên kết vùng, đưa rau sạch từ các tỉnh lân cận về tiêu thụ tại thủ đô với giá ổn định.', 0.83),
(10, 'Một công ty nông nghiệp ứng dụng trí tuệ nhân tạo (AI) trong việc giám sát và điều chỉnh chế độ tưới tiêu cho cây trồng.', 0.91),
(10, 'Người tiêu dùng tìm mua các loại rau quả lạ, nhập khẩu như việt quất, cherry và măng tây dù giá thành cao.', 0.78),
(10, 'Bộ Công Thương tổ chức các buổi đào tạo kỹ năng đóng gói và bảo quản rau quả sau thu hoạch cho nông dân.', 0.76),
(10, 'Cảnh báo về việc sử dụng chất kích thích tăng trưởng trái phép trong trồng trọt, ảnh hưởng nghiêm trọng đến sức khỏe.', 0.94),
(10, 'Giá mít Thái giảm sâu do nguồn cung dư thừa và gặp khó khăn trong việc xuất khẩu qua đường tiểu ngạch.', 0.82),
(10, 'Mô hình kinh tế tuần hoàn, sử dụng chất thải từ rau quả để sản xuất phân bón hữu cơ đang được nhân rộng.', 0.85),
(10, 'Thị trường rau củ quả tại các tỉnh phía Nam có xu hướng tăng vào dịp cuối năm do nhu cầu mua sắm Tết Nguyên đán.', 0.81),
(10, 'Các nhà khoa học nghiên cứu giống cây ăn quả chịu hạn, chịu mặn mới nhằm đối phó với biến đổi khí hậu.', 0.87),
(10, 'Cục Bảo vệ Thực vật tăng cường kiểm tra các lô hàng rau quả nhập khẩu nhằm ngăn chặn sâu bệnh và dịch hại.', 0.9),
(10, 'Báo cáo cho thấy giá trị xuất khẩu rau quả đã vượt mốc 5 tỷ USD, đóng góp quan trọng vào nền kinh tế nông nghiệp.', 0.93),
(10, 'Nông dân tại Đồng bằng sông Cửu Long nỗ lực phục hồi diện tích trồng cây ăn trái sau đợt hạn hán nghiêm trọng.', 0.79),
(10, 'Các siêu thị áp dụng chương trình khuyến mãi lớn cho các loại trái cây vào mùa thu hoạch như bơ, nhãn, chôm chôm.', 0.77),
(10, 'Tiêu chuẩn GlobalGAP giúp rau quả Việt Nam dễ dàng thâm nhập vào các thị trường khó tính như Mỹ, Nhật Bản.', 0.88),
(10, 'Cảnh báo về tình trạng mua bán hạt giống không rõ nguồn gốc, ảnh hưởng đến chất lượng và năng suất cây trồng.', 0.80),
(10, 'Các khu vực trồng dứa tại miền núi đang đối mặt với dịch bệnh, cần có biện pháp phòng trừ kịp thời.', 0.75),
(10, 'Sản phẩm nước ép rau củ đóng chai tự nhiên đang được nhiều người tiêu dùng ưu tiên lựa chọn thay cho nước ngọt.', 0.84),
(10, 'Chính sách hỗ trợ vốn vay ưu đãi cho các hợp tác xã sản xuất rau sạch theo chuỗi giá trị.', 0.81),
(10, 'Mô hình bán lẻ rau quả trực tuyến (online) phát triển mạnh, giúp nông dân tiếp cận trực tiếp với người tiêu dùng.', 0.86),
(10, 'Đề xuất thành lập trung tâm nghiên cứu giống cây trồng chuyên biệt cho rau quả nhiệt đới, tăng tính cạnh tranh quốc tế.', 0.89);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(11, 'Giá thịt heo hơi tại miền Bắc giảm mạnh xuống mức thấp nhất trong quý do nguồn cung tái đàn tăng và sức mua chậm lại.', 0.92),
(11, 'Bộ Nông nghiệp cảnh báo nguy cơ tái phát dịch tả heo châu Phi, yêu cầu các địa phương tăng cường tiêm phòng và kiểm soát dịch bệnh.', 0.95),
(11, 'Cảnh sát môi trường triệt phá một cơ sở chuyên bơm tạp chất Agar vào tôm nguyên liệu nhằm trục lợi, thu giữ hàng tấn tang vật.', 0.94),
(11, 'Xuất khẩu cá tra Việt Nam sang thị trường Mỹ tăng trưởng ấn tượng trong 6 tháng đầu năm nhờ nhu cầu cao và giá bán ổn định.', 0.91),
(11, 'Các hộ chăn nuôi bò sữa tại Lâm Đồng áp dụng tiêu chuẩn GlobalGAP, nâng cao chất lượng sữa tươi đạt chuẩn quốc tế.', 0.85),
(11, 'Thị trường thịt gà công nghiệp có dấu hiệu dư thừa nguồn cung, kéo giá bán lẻ tại các chợ truyền thống giảm nhẹ.', 0.87),
(11, 'Cục Thú y yêu cầu kiểm soát chặt chẽ việc vận chuyển gia cầm qua biên giới để phòng ngừa dịch cúm gia cầm H5N1.', 0.9),
(11, 'Ngư dân miền Trung bội thu tôm hùm xanh vụ đầu năm, giá bán tại cảng neo ở mức cao do nhu cầu từ Trung Quốc.', 0.88),
(11, 'Các chuyên gia khuyến cáo người tiêu dùng nên mua thịt có dấu tem truy xuất nguồn gốc rõ ràng tại các cửa hàng uy tín.', 0.84),
(11, 'Công ty chăn nuôi lớn cam kết không sử dụng chất cấm, tạo thịt nạc trong quá trình nuôi heo, đảm bảo an toàn thực phẩm.', 0.89),
(11, 'Giá cá biển tươi tăng mạnh do ngư dân gặp khó khăn trong việc đánh bắt xa bờ vì chi phí xăng dầu tăng cao.', 0.82),
(11, 'Chính sách hỗ trợ vốn vay cho các dự án nuôi cá lồng bè trên biển nhằm phát triển kinh tế thủy sản bền vững.', 0.78),
(11, 'Bộ Công Thương tổ chức xúc tiến thương mại, tìm kiếm thị trường mới cho sản phẩm thịt bò Úc nhập khẩu.', 0.76),
(11, 'Nhiều cơ sở sản xuất nước mắm truyền thống bị đình chỉ vì sử dụng cá không đảm bảo chất lượng để ủ chượp.', 0.93),
(11, 'Cục Quản lý Chất lượng Nông Lâm sản kiểm tra đột xuất các lô hàng cá đông lạnh nhập khẩu, phát hiện nhiều mẫu nhiễm khuẩn.', 0.91),
(11, 'Giá thịt dê và thịt cừu có xu hướng tăng vào mùa du lịch do nhu cầu tiêu thụ tại các khu nghỉ dưỡng.', 0.79),
(11, 'Nông dân tại Đồng Tháp áp dụng mô hình nuôi cá rô đồng xen lúa, mang lại lợi ích kinh tế kép.', 0.83),
(11, 'Cảnh báo về tình trạng thịt heo nhập khẩu giá rẻ được làm giả tem mác để bán dưới danh nghĩa thịt tươi trong nước.', 0.95),
(11, 'Các nhà khoa học nghiên cứu thành công vắc-xin phòng bệnh cho tôm nuôi, giảm thiểu thiệt hại cho ngành thủy sản.', 0.86),
(11, 'Hiệp hội Chế biến và Xuất khẩu Thủy sản Việt Nam kiến nghị giảm thuế nhập khẩu thức ăn chăn nuôi.', 0.81),
(11, 'Thịt lợn organic (hữu cơ) được bày bán tại các siêu thị cao cấp với mức giá cao hơn thịt thường 30-40%.', 0.77),
(11, 'Sản lượng cá ngừ đại dương của Việt Nam tiếp tục duy trì đà tăng trưởng, đẩy mạnh xuất khẩu sang Nhật Bản.', 0.88),
(11, 'Chính phủ yêu cầu các địa phương xử lý nghiêm các trường hợp xả thải gây ô nhiễm, ảnh hưởng đến vùng nuôi trồng thủy sản.', 0.9),
(11, 'Các chuyên gia khuyến cáo người dân không nên ăn tiết canh lợn để tránh nguy cơ nhiễm khuẩn và bệnh liên cầu khuẩn.', 0.85),
(11, 'Thị trường trứng gia cầm ổn định, không có biến động lớn về giá do nguồn cung được kiểm soát tốt.', 0.75),
(11, 'Mô hình nuôi ốc hương thương phẩm áp dụng quy trình khép kín, đạt hiệu quả kinh tế cao và đảm bảo môi trường.', 0.83),
(11, 'Các doanh nghiệp đẩy mạnh chế biến sâu các sản phẩm từ thịt bò để tăng giá trị xuất khẩu, thay vì chỉ xuất thịt thô.', 0.86),
(11, 'Lực lượng quản lý thị trường phát hiện và tiêu hủy hàng tạ cá khô không rõ nguồn gốc, bị tẩm hóa chất bảo quản.', 0.93),
(11, 'Giá thịt gà ta tại các tỉnh miền núi duy trì mức ổn định, phục vụ nhu cầu tiêu thụ truyền thống của người dân.', 0.79),
(11, 'Chương trình kiểm soát kháng sinh trong chăn nuôi nhằm đáp ứng yêu cầu nhập khẩu từ các thị trường khó tính.', 0.87);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(12, 'Giá gạo xuất khẩu 5% tấm của Việt Nam đạt mức kỷ lục, vượt qua Thái Lan, củng cố vị thế dẫn đầu thị trường thế giới.', 0.95),
(12, 'Nông dân Đồng bằng sông Cửu Long (ĐBSCL) bội thu vụ lúa Đông Xuân nhờ điều kiện thời tiết thuận lợi và giống lúa chất lượng cao.', 0.92),
(12, 'Bộ Nông nghiệp công bố giống lúa chịu mặn, chịu hạn mới, giúp nông dân ứng phó hiệu quả với tình trạng xâm nhập mặn.', 0.91),
(12, 'Các doanh nghiệp xuất khẩu gạo lớn cam kết xây dựng chuỗi cung ứng minh bạch, đạt tiêu chuẩn quốc tế về truy xuất nguồn gốc.', 0.9),
(12, 'Chính phủ ban hành chính sách hỗ trợ nông dân mua sắm máy móc, ứng dụng công nghệ tưới tiêu tiết kiệm nước trong sản xuất lúa.', 0.88),
(12, 'Giá lúa tại ruộng tăng cao, giúp nông dân có lợi nhuận tốt sau nhiều năm đối mặt với chi phí sản xuất tăng.', 0.87),
(12, 'Hiệp hội Lương thực Việt Nam (VFA) kiến nghị nới lỏng hạn ngạch xuất khẩu gạo nhằm tận dụng cơ hội thị trường.', 0.85),
(12, 'Mô hình cánh đồng lớn áp dụng cơ giới hóa đồng bộ giúp giảm chi phí sản xuất và nâng cao năng suất lúa gạo.', 0.84),
(12, 'Các nhà khoa học cảnh báo về sự xuất hiện của sâu bệnh hại lúa mới, yêu cầu các địa phương tăng cường giám sát.', 0.93),
(12, 'Gạo ST25, giống gạo ngon nhất thế giới, tiếp tục khẳng định thương hiệu, được các thị trường khó tính săn đón.', 0.94),
(12, 'Thị trường gạo nội địa ổn định, không có hiện tượng đầu cơ tích trữ, đảm bảo an ninh lương thực quốc gia.', 0.8),
(12, 'Bộ Công Thương tổ chức xúc tiến thương mại, đưa gạo hữu cơ và gạo đặc sản Việt Nam vào các siêu thị châu Âu.', 0.86),
(12, 'Nông dân chuyển đổi diện tích đất trồng lúa kém hiệu quả sang trồng các loại cây ăn trái có giá trị kinh tế cao hơn.', 0.78),
(12, 'Các doanh nghiệp xây dựng kho dự trữ lạnh để bảo quản gạo chất lượng cao, phục vụ xuất khẩu quanh năm.', 0.82),
(12, 'Giá gạo thành phẩm tăng mạnh sau quyết định cấm xuất khẩu của một số nước sản xuất lúa gạo lớn trong khu vực.', 0.95),
(12, 'Các chuyên gia đề xuất chiến lược phát triển bền vững ngành lúa gạo, chú trọng giảm phát thải khí nhà kính.', 0.89),
(12, 'Cục Bảo vệ Thực vật kiểm soát nghiêm ngặt dư lượng thuốc bảo vệ thực vật trên lúa gạo xuất khẩu.', 0.91),
(12, 'Mô hình kinh tế tuần hoàn: Tận dụng rơm rạ để sản xuất nấm và thức ăn chăn nuôi, tăng thu nhập cho nông dân.', 0.76),
(12, 'Giá trị xuất khẩu các sản phẩm chế biến từ gạo như bún, phở khô tăng trưởng mạnh mẽ trong năm qua.', 0.83),
(12, 'Nông dân ĐBSCL tập trung gieo sạ vụ Hè Thu sớm để tránh ảnh hưởng của mùa lũ và xâm nhập mặn.', 0.81),
(12, 'Thị trường gạo tẻ và gạo nếp có sự phân hóa rõ rệt về giá, tùy thuộc vào chủng loại và mục đích sử dụng.', 0.77),
(12, 'Chương trình phát triển lúa gạo chất lượng cao tại các tỉnh miền núi phía Bắc đang mang lại hiệu quả tích cực.', 0.79),
(12, 'Việt Nam đẩy mạnh hợp tác với Campuchia trong việc ổn định nguồn cung và giá lúa gạo khu vực.', 0.85),
(12, 'Bộ Công an triệt phá đường dây làm giả gạo thương hiệu nổi tiếng, gây hoang mang dư luận.', 0.94),
(12, 'Các nhà máy xay xát đầu tư công nghệ hiện đại để nâng cao chất lượng gạo, giảm tỷ lệ tấm trong xuất khẩu.', 0.88),
(12, 'Giá gạo đồ và gạo lứt tăng nhẹ do nhu cầu về thực phẩm tốt cho sức khỏe của người tiêu dùng tăng lên.', 0.75),
(12, 'Khoa học công nghệ giúp nông dân dự báo chính xác thời tiết, tối ưu hóa lịch gieo cấy lúa.', 0.82),
(12, 'Chính phủ yêu cầu các địa phương kiểm tra, giám sát nghiêm ngặt việc sử dụng đất trồng lúa, không để chuyển đổi mục đích trái phép.', 0.9),
(12, 'Việc mở rộng thị trường sang châu Phi và Trung Đông được coi là chiến lược quan trọng cho ngành gạo Việt Nam.', 0.86),
(12, 'Hiệp định thương mại tự do mới giúp gạo Việt Nam được hưởng thuế suất ưu đãi khi vào thị trường EU.', 0.87);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(13, 'Giá sầu riêng Ri6 và Monthong tại vườn đạt đỉnh lịch sử, nông dân Tây Nguyên và ĐBSCL phấn khởi bội thu.', 0.95),
(13, 'Bộ NN&PTNT cấp thêm hàng trăm mã số vùng trồng và cơ sở đóng gói, tạo điều kiện thuận lợi cho xuất khẩu chính ngạch sầu riêng sang Trung Quốc.', 0.94),
(13, 'Chuyên gia nông nghiệp chia sẻ kỹ thuật chăm sóc sầu riêng ra hoa nghịch vụ, giúp kéo dài mùa vụ và tránh rớt giá.', 0.92),
(13, 'Cảnh báo về tình trạng sầu riêng non bị thu hoạch sớm, ngâm hóa chất làm chín ép, gây ảnh hưởng đến uy tín xuất khẩu.', 0.95),
(13, 'Sầu riêng Việt Nam được đánh giá có ưu thế cạnh tranh vượt trội về chất lượng và thời gian thu hoạch so với Thái Lan và Malaysia.', 0.91),
(13, 'Các doanh nghiệp xuất khẩu lớn đầu tư kho lạnh và công nghệ bảo quản tiên tiến để duy trì chất lượng sầu riêng trong quá trình vận chuyển.', 0.88),
(13, 'Giá sầu riêng Thái (Monthong) nhập khẩu vào thị trường Việt Nam có xu hướng giảm nhẹ do nguồn cung dồi dào.', 0.84),
(13, 'Các nhà vườn chuyển đổi sang trồng các giống sầu riêng mới như Musang King và Black Thorn, có giá trị kinh tế cao.', 0.89),
(13, 'Lực lượng quản lý thị trường kiểm tra, xử lý nghiêm các trường hợp sử dụng chất cấm, chất bảo quản độc hại trên sầu riêng.', 0.93),
(13, 'Xuất khẩu sầu riêng chính ngạch mang lại kim ngạch kỷ lục, đóng góp lớn vào tăng trưởng ngành rau quả của Việt Nam.', 0.9),
(13, 'Nông dân gặp khó khăn trong việc kiểm soát sâu bệnh hại thân và rễ sầu riêng, cần có giải pháp phòng trừ hiệu quả.', 0.85),
(13, 'Hợp tác xã sầu riêng tại Đắk Lắk áp dụng quy trình trồng trọt GlobalGAP, đảm bảo an toàn và chất lượng sản phẩm.', 0.86),
(13, 'Sầu riêng là loại trái cây được đặt hàng qua các ứng dụng thương mại điện tử nhiều nhất trong quý vừa qua.', 0.78),
(13, 'Các chuyên gia khuyến cáo về việc sử dụng phân bón và thuốc bảo vệ thực vật đúng liều lượng, tránh dư lượng tồn đọng.', 0.87),
(13, 'Sầu riêng chế biến thành các sản phẩm như kem, bánh kẹo và sữa chua đang mở rộng thị trường nội địa.', 0.82),
(13, 'Giá sầu riêng Campuchia nhập khẩu cạnh tranh trực tiếp với sầu riêng Việt Nam tại một số tỉnh biên giới.', 0.76),
(13, 'Thị trường sầu riêng đông lạnh và sầu riêng tách múi đóng hộp đang phát triển mạnh mẽ để phục vụ xuất khẩu.', 0.9),
(13, 'Nhiều địa phương tổ chức hội thảo chuyển giao kỹ thuật trồng sầu riêng cho bà con nông dân các tỉnh miền núi.', 0.79),
(13, 'Các chuyên gia cảnh báo về rủi ro mất mùa sầu riêng do ảnh hưởng của El Nino, gây khô hạn kéo dài.', 0.91),
(13, 'Sầu riêng là một trong những mặt hàng nông sản chủ lực mà Việt Nam đang ưu tiên đàm phán mở rộng thị trường sang Ấn Độ.', 0.88),
(13, 'Việc sử dụng máy bay không người lái (drone) để phun thuốc bảo vệ thực vật cho cây sầu riêng được áp dụng phổ biến.', 0.83),
(13, 'Chính sách hỗ trợ vốn vay cho nông dân đầu tư hệ thống tưới tự động cho vườn sầu riêng.', 0.77),
(13, 'Phân biệt sầu riêng Ri6 và Monthong qua hình dáng múi, màu sắc cơm và độ ngọt béo khác nhau.', 0.81),
(13, 'Nhu cầu tiêu thụ sầu riêng trong nước tăng mạnh nhờ việc quảng bá ẩm thực và sự phổ biến của món ăn này.', 0.75),
(13, 'Các nhà nghiên cứu tìm cách lai tạo giống sầu riêng mới có hạt lép, cơm dày và khả năng kháng bệnh tốt hơn.', 0.87),
(13, 'Sầu riêng bị hỏng do vận chuyển sai quy cách bị trả về, gây thiệt hại lớn cho các doanh nghiệp logistics.', 0.93),
(13, 'Giá sầu riêng tại các chợ lẻ và siêu thị có sự chênh lệch lớn, phụ thuộc vào nguồn gốc và chất lượng.', 0.80),
(13, 'Bộ NN&PTNT yêu cầu các vùng trồng sầu riêng phải có nhật ký canh tác rõ ràng để đáp ứng tiêu chuẩn xuất khẩu.', 0.89),
(13, 'Các công ty du lịch tổ chức tour tham quan vườn sầu riêng và trải nghiệm thu hoạch trái cây tại chỗ.', 0.74),
(13, 'Giá sầu riêng Monthong loại A có thể đạt 150.000 - 200.000 VNĐ/kg tại thị trường nội địa vào thời điểm khan hiếm.', 0.86);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(14, 'Giá đậu nành nhập khẩu tăng cao ảnh hưởng trực tiếp đến chi phí sản xuất thức ăn chăn nuôi tại Việt Nam.', 0.93),
(14, 'Nhu cầu tiêu thụ sữa đậu nành và các sản phẩm từ đậu phụ tăng mạnh do người dân ưu tiên thực phẩm có nguồn gốc thực vật.', 0.91),
(14, 'Các chuyên gia dinh dưỡng khẳng định đậu tương là nguồn protein thực vật hoàn chỉnh, rất tốt cho người ăn chay.', 0.88),
(14, 'Cảnh báo về tình trạng đậu phụ chứa thạch cao và các chất phụ gia bị cấm, lực lượng quản lý thị trường vào cuộc kiểm tra.', 0.95),
(14, 'Giá đậu xanh và đậu đen ổn định, phục vụ nhu cầu chế biến bánh kẹo và các món chè truyền thống dịp lễ Tết.', 0.82),
(14, 'Công nghệ chế biến dầu đậu nành hiện đại giúp loại bỏ tạp chất, giữ được hàm lượng Omega 3 và 6 tự nhiên.', 0.85),
(14, 'Mô hình trồng đậu phộng (lạc) xen canh trên đất trống mang lại hiệu quả kinh tế cao cho nông dân các tỉnh miền Trung.', 0.78),
(14, 'Các doanh nghiệp Việt Nam đầu tư vùng nguyên liệu đậu nành hữu cơ, phục vụ sản xuất sữa đậu nành và nước tương cao cấp.', 0.89),
(14, 'Thị trường đậu Hà Lan và đậu thận nhập khẩu có xu hướng tăng nhẹ do nhu cầu của các nhà hàng Âu.', 0.75),
(14, 'Lợi ích sức khỏe của việc sử dụng các sản phẩm lên men từ đậu nành như tương bần, chao và miso.', 0.87),
(14, 'Giá bã đậu nành, nguyên liệu chính trong thức ăn chăn nuôi, có dấu hiệu hạ nhiệt sau khi nhập khẩu được tăng cường.', 0.86),
(14, 'Nghiên cứu khoa học cho thấy đậu nành có chứa Isoflavone, giúp cân bằng nội tiết tố nữ và ngăn ngừa loãng xương.', 0.9),
(14, 'Việc sử dụng quá nhiều sản phẩm đậu nành chế biến sẵn có thể gây đầy bụng, khó tiêu nếu không kiểm soát liều lượng.', 0.77),
(14, 'Các cơ sở sản xuất đậu phụ phải cam kết không sử dụng chất tẩy trắng và hàn the trong quá trình chế biến.', 0.92),
(14, 'Giá đỗ (giá) được sản xuất từ đậu xanh đang bị kiểm tra về việc sử dụng thuốc kích thích tăng trưởng.', 0.94),
(14, 'Việt Nam tăng cường nhập khẩu đậu tương từ các quốc gia Nam Mỹ để đáp ứng nhu cầu sản xuất trong nước.', 0.84),
(14, 'Các sản phẩm thức uống từ các loại hạt và đậu (như đậu đỏ, đậu đen) được ưa chuộng thay thế sữa bò.', 0.83),
(14, 'Hội thảo về các giống đậu mới có năng suất cao và khả năng kháng sâu bệnh tốt được tổ chức tại Viện Nông nghiệp.', 0.79),
(14, 'Giá trị xuất khẩu các sản phẩm chế biến từ đậu như bột đậu nành và đậu phụ đóng gói tăng trưởng ổn định.', 0.81),
(14, 'Cảnh báo về tình trạng mua bán đậu nành biến đổi gen không rõ nguồn gốc trên thị trường bán lẻ.', 0.91),
(14, 'Đậu phộng rang muối và các sản phẩm từ lạc là mặt hàng tiêu thụ mạnh vào dịp Tết Nguyên đán.', 0.76),
(14, 'Bộ Công Thương thúc đẩy việc xây dựng thương hiệu quốc gia cho các sản phẩm đậu truyền thống của Việt Nam.', 0.85),
(14, 'Các cơ sở chế biến đậu phụ áp dụng công nghệ máy móc tự động hóa để đảm bảo vệ sinh và nâng cao năng suất.', 0.87),
(14, 'Chính phủ hỗ trợ nông dân trồng đậu áp dụng các tiêu chuẩn an toàn thực phẩm như VietGAP.', 0.78),
(14, 'Sản phẩm tương ớt, tương cà có sử dụng chất bảo quản từ đậu nành được kiểm soát nghiêm ngặt về nồng độ.', 0.89),
(14, 'Giá đậu tương thế giới biến động mạnh do căng thẳng thương mại và thời tiết tại các nước sản xuất lớn.', 0.93),
(14, 'Đậu bắp, đậu que là các loại rau ăn quả được tiêu thụ mạnh tại thị trường nội địa và xuất khẩu sang Hàn Quốc.', 0.80),
(14, 'Nghiên cứu về việc sử dụng đậu tương thay thế một phần nguyên liệu thủy sản trong sản xuất thức ăn chăn nuôi tôm.', 0.86),
(14, 'Sự khác biệt về dinh dưỡng giữa đậu nành nguyên chất và đậu nành tách béo trong chế độ ăn kiêng.', 0.75),
(14, 'Thị trường tương hột (tương đậu nành nguyên hạt lên men) truyền thống đang dần được phục hồi và phát triển.', 0.82);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(15, 'Giá cà phê Robusta đạt mức cao nhất trong 30 năm, mang lại nguồn thu khổng lồ cho nông dân Tây Nguyên.', 0.95),
(15, 'Xuất khẩu cà phê của Việt Nam vượt mốc 4 tỷ USD, củng cố vị thế là nước xuất khẩu Robusta lớn nhất thế giới.', 0.93),
(15, 'Các chuyên gia cảnh báo về tình trạng nông dân ồ ạt chặt bỏ cây trồng khác để chuyển sang trồng cà phê, gây mất cân bằng sinh thái.', 0.91),
(15, 'Chuỗi cà phê nội địa áp dụng công nghệ rang xay hiện đại, đưa ra thị trường các sản phẩm cà phê đặc sản (Specialty Coffee).', 0.88),
(15, 'Bộ NN&PTNT và các tổ chức quốc tế đẩy mạnh chương trình phát triển cà phê bền vững, chống biến đổi khí hậu.', 0.9),
(15, 'Nông dân cà phê chịu lỗ nặng do sản lượng giảm sút nghiêm trọng vì hạn hán kéo dài tại khu vực Tây Nguyên.', 0.92),
(15, 'Cà phê Arabica Cầu Đất (Lâm Đồng) được cấp chỉ dẫn địa lý, nâng cao giá trị và thương hiệu trên thị trường quốc tế.', 0.85),
(15, 'Giá cà phê hòa tan và cà phê rang xay có sự cạnh tranh gay gắt, các hãng liên tục đưa ra chiến lược giảm giá và khuyến mãi.', 0.87),
(15, 'Chính phủ hỗ trợ nông dân vay vốn để tái canh cây cà phê già cỗi, thay thế bằng các giống mới có năng suất cao hơn.', 0.83),
(15, 'Thị trường cà phê nhân xô trong nước biến động mạnh theo giá cà phê London (Robusta) và New York (Arabica).', 0.89),
(15, 'Các quán cà phê take-away (mang đi) tiếp tục mở rộng mô hình kinh doanh, thu hút giới trẻ với các loại thức uống sáng tạo.', 0.78),
(15, 'Nghiên cứu về tác động của việc tiêu thụ cà phê đối với sức khỏe tim mạch và hệ thần kinh của người Việt Nam.', 0.84),
(15, 'Cà phê bột bị phát hiện chứa đậu nành và bắp rang cháy, lực lượng quản lý thị trường tiến hành xử phạt nghiêm.', 0.94),
(15, 'Các doanh nghiệp xuất khẩu chú trọng chế biến sâu, sản xuất các sản phẩm cà phê hữu cơ và cà phê decaf (tách caffeine).', 0.86),
(15, 'Tổ chức Thương mại Thế giới (WTO) cảnh báo về hàng rào kỹ thuật khắt khe của EU đối với cà phê nhập khẩu liên quan đến dư lượng hóa chất.', 0.91),
(15, 'Cà phê phin truyền thống vẫn giữ vị trí không thể thay thế trong văn hóa thưởng thức cà phê của người Việt.', 0.77),
(15, 'Hội Cà phê Buôn Ma Thuột tổ chức lễ hội cà phê, thu hút hàng chục nghìn du khách và nhà đầu tư quốc tế.', 0.81),
(15, 'Giá cà phê chè (Arabica) cao hơn nhiều so với cà phê vối (Robusta) do chi phí trồng trọt và chế biến phức tạp hơn.', 0.85),
(15, 'Các công ty khởi nghiệp ứng dụng công nghệ blockchain để truy xuất nguồn gốc cà phê từ nông trại đến tay người tiêu dùng.', 0.88),
(15, 'Mô hình trồng cà phê xen canh với hồ tiêu và cây ăn trái giúp nông dân đa dạng hóa thu nhập và bảo vệ đất.', 0.79),
(15, 'Nghiên cứu về giống cà phê đặc biệt có khả năng chống chọi tốt với sâu bệnh, giảm thiểu sử dụng thuốc bảo vệ thực vật.', 0.83),
(15, 'Thị trường cà phê viên nén (capsule) phát triển mạnh mẽ tại các văn phòng và hộ gia đình có thu nhập cao.', 0.75),
(15, 'Chính sách hỗ trợ giá cho cà phê chất lượng cao nhằm khuyến khích nông dân tập trung vào sản xuất bền vững.', 0.82),
(15, 'Các chuyên gia khuyến nghị thời điểm tốt nhất để bán cà phê là vào đầu vụ, tránh rủi ro biến động giá cuối năm.', 0.86),
(15, 'Giá thuê mặt bằng tăng cao là một trong những thách thức lớn đối với các chuỗi cà phê mở rộng quy mô tại đô thị.', 0.76),
(15, 'Lực lượng quản lý thị trường kiểm tra các kho chứa cà phê, ngăn chặn hành vi găm hàng chờ tăng giá.', 0.93),
(15, 'Việc sử dụng các loại máy móc tự động hóa trong khâu chế biến cà phê giúp nâng cao năng suất và chất lượng hạt.', 0.89),
(15, 'Chương trình phát triển du lịch nông nghiệp gắn với các đồi cà phê tại Đà Lạt thu hút khách tham quan.', 0.74),
(15, 'Thị trường cà phê xanh (Green Coffee) được quan tâm vì lợi ích giảm cân và chống oxy hóa.', 0.79),
(15, 'Sự khác biệt về hương vị giữa cà phê rang mộc và cà phê rang bơ được người sành cà phê phân tích chi tiết.', 0.81);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(16, 'Thị trường đồ gia dụng điện lạnh bùng nổ chương trình khuyến mãi và giảm giá sâu nhằm kích cầu tiêu dùng cuối năm.', 0.95),
(16, 'Các chuyên gia dự đoán xu hướng thiết bị nhà bếp thông minh sẽ chiếm lĩnh thị trường tiêu dùng trong năm 2025.', 0.92),
(16, 'Bộ Công Thương yêu cầu các doanh nghiệp công bố rõ ràng về tiêu chuẩn và nhãn năng lượng của các thiết bị điện gia dụng.', 0.9),
(16, 'Người tiêu dùng ưu tiên lựa chọn các sản phẩm gia dụng có độ bền cao và tính năng tiết kiệm điện năng vượt trội.', 0.88),
(16, 'Các vụ cháy nổ liên quan đến thiết bị điện kém chất lượng gia tăng, cơ quan chức năng tăng cường kiểm tra nguồn gốc hàng hóa.', 0.93),
(16, 'Sự cạnh tranh gay gắt giữa các thương hiệu điện tử Hàn Quốc và Nhật Bản trong phân khúc thiết bị gia đình cao cấp.', 0.87),
(16, 'Thị trường đồ dùng cá nhân như máy massage, thiết bị chăm sóc da có sự tăng trưởng mạnh mẽ, nhất là trong mùa dịch.', 0.84),
(16, 'Các mặt hàng đồ gia dụng mini, đa năng được giới trẻ và người sống độc thân tại các căn hộ nhỏ ưa chuộng.', 0.86),
(16, 'Chính phủ ban hành quy định mới về việc thu hồi và tái chế các thiết bị điện tử đã qua sử dụng, giảm thiểu rác thải.', 0.91),
(16, 'Công nghệ IoT (Internet of Things) được tích hợp ngày càng nhiều vào các vật dụng gia đình, tạo nên hệ sinh thái nhà thông minh.', 0.89),
(16, 'Nhiều chuỗi cửa hàng điện máy mở rộng dịch vụ bảo hành, sửa chữa tại nhà để nâng cao trải nghiệm khách hàng.', 0.79),
(16, 'Các thiết bị sử dụng năng lượng mặt trời như đèn và máy nước nóng đang dần trở nên phổ biến ở các vùng nông thôn.', 0.75),
(16, 'Cục Quản lý thị trường triệt phá một kho hàng lớn chứa hàng giả, nhái các thương hiệu đồ gia dụng nổi tiếng.', 0.94),
(16, 'Khảo sát cho thấy chi phí mua sắm đồ gia dụng của các gia đình Việt Nam chiếm khoảng 10-15% tổng chi tiêu hàng năm.', 0.81),
(16, 'Sự dịch chuyển xu hướng mua sắm đồ gia dụng từ các siêu thị truyền thống sang kênh thương mại điện tử.', 0.83);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(17, 'Nồi cơm điện cao tần Inverter sử dụng công nghệ từ tính (IH) giúp kiểm soát nhiệt độ chính xác, mang lại hạt cơm chín đều và dẻo ngon hơn.', 0.95),
(17, 'Thị trường nồi cơm điện chứng kiến sự cạnh tranh giữa các thương hiệu Nhật Bản (Zojirushi, Tiger) và Hàn Quốc (Cuckoo, Sharp).', 0.92),
(17, 'Công nghệ áp suất cao (Pressure Cooker) giúp nồi cơm điện nấu cơm nhanh hơn và giữ được dưỡng chất tối đa trong hạt gạo.', 0.91),
(17, 'Các mẫu nồi cơm điện thông minh tích hợp tính năng hẹn giờ, nấu cháo, làm bánh và giữ ấm lên đến 24 giờ.', 0.88),
(17, 'Hướng dẫn vệ sinh lòng nồi cơm điện đúng cách để tránh trầy xước lớp chống dính, bảo vệ sức khỏe người dùng.', 0.85),
(17, 'Nồi cơm điện mini dung tích nhỏ 1-1.5 lít được sinh viên và các gia đình ít người ưa chuộng vì tính tiện dụng.', 0.81),
(17, 'Các cửa hàng điện máy tung ra chương trình khuyến mãi "mua 1 tặng 1" nồi cơm điện cao cấp dịp Tết Nguyên đán.', 0.79),
(17, 'Cảnh báo về việc sử dụng nồi cơm điện có dây cắm và phích cắm bị hỏng, tiềm ẩn nguy cơ chập điện, cháy nổ.', 0.93),
(17, 'Nồi cơm điện sử dụng công nghệ Fuzzy Logic có khả năng tự động điều chỉnh nhiệt độ và thời gian nấu phù hợp với loại gạo.', 0.87),
(17, 'Khảo sát cho thấy người tiêu dùng Việt Nam sẵn sàng chi trả cao hơn cho nồi cơm điện có xuất xứ từ Nhật Bản và Hàn Quốc.', 0.9),
(17, 'So sánh khả năng tiết kiệm điện của nồi cơm điện Inverter so với các loại nồi cơm điện cơ truyền thống.', 0.84),
(17, 'Các nhà sản xuất giới thiệu mẫu nồi cơm điện có vỏ ngoài làm bằng thép không gỉ, tăng tính thẩm mỹ và độ bền.', 0.83),
(17, 'Nồi cơm điện tử có màn hình LCD hiển thị các chế độ nấu và thời gian còn lại, giúp người dùng dễ dàng theo dõi.', 0.86),
(17, 'Hướng dẫn cách chọn mua nồi cơm điện phù hợp với số lượng thành viên trong gia đình để tránh lãng phí.', 0.8),
(17, 'Thị trường nồi cơm điện áp suất điện tử được dự đoán sẽ tăng trưởng mạnh mẽ trong phân khúc cao cấp.', 0.89),
(17, 'Giá bán nồi cơm điện cao tần dao động từ 3 triệu đến 10 triệu đồng, tùy thuộc vào công nghệ và thương hiệu.', 0.78),
(17, 'Nồi cơm điện cơ truyền thống vẫn chiếm thị phần lớn tại các vùng nông thôn nhờ giá thành rẻ và dễ sử dụng.', 0.75),
(17, 'Các dòng nồi cơm điện mới tích hợp chế độ nấu gạo lứt và gạo nếp riêng biệt, đảm bảo độ chín hoàn hảo.', 0.82),
(17, 'Nghiên cứu về vật liệu chống dính an toàn (như gốm sứ hoặc than hoạt tính) thay thế Teflon trong lòng nồi cơm.', 0.94),
(17, 'Một mẫu nồi cơm điện đa năng vừa có thể nấu cơm, vừa hấp thực phẩm và làm sữa chua được giới thiệu ra thị trường.', 0.77),
(17, 'Lỗi thường gặp của nồi cơm điện (như cơm bị sống, không nhảy nút) và cách khắc phục đơn giản tại nhà.', 0.86),
(17, 'Nồi cơm điện của thương hiệu Việt Nam nâng cao chất lượng, cạnh tranh trực tiếp với hàng nhập khẩu.', 0.83),
(17, 'Các cửa hàng điện máy tổ chức chương trình đổi nồi cơm điện cũ lấy nồi cơm điện mới, giảm thiểu rác thải điện tử.', 0.79),
(17, 'Chính sách bảo hành và đổi trả linh hoạt là yếu tố quan trọng khi người tiêu dùng quyết định mua nồi cơm điện.', 0.81),
(17, 'Nồi cơm điện từ tính (IH) được đánh giá là công nghệ nấu cơm tốt nhất hiện nay về hương vị và độ an toàn.', 0.92),
(17, 'So sánh độ dày của lòng nồi cơm điện, ảnh hưởng trực tiếp đến khả năng giữ nhiệt và độ bền của sản phẩm.', 0.76),
(17, 'Các mẫu nồi cơm điện được ưa chuộng nhất dịp Black Friday có mức giảm giá lên đến 50%.', 0.85),
(17, 'Nhà sản xuất cảnh báo về việc làm sạch van thoát hơi của nồi cơm điện thường xuyên để tránh cơm bị nhão.', 0.88),
(17, 'Nồi cơm điện màu sắc pastel và thiết kế tối giản đang là xu hướng được nhiều người trẻ lựa chọn.', 0.74),
(17, 'Cục Quản lý thị trường tịch thu lô hàng nồi cơm điện không tem nhãn năng lượng, kém chất lượng.', 0.93);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(18, 'Xu hướng kính mắt gọng tròn cổ điển và gọng vuông oversized tiếp tục được giới trẻ săn đón trong năm 2024.', 0.92),
(18, 'Chuyên gia nhãn khoa cảnh báo về tác hại nghiêm trọng của việc đeo kính râm kém chất lượng, không có khả năng chống tia UV.', 0.95),
(18, 'Công nghệ tròng kính chiết suất cao (High Index) giúp làm mỏng tròng kính cận nặng, tăng tính thẩm mỹ và thoải mái khi đeo.', 0.91),
(18, 'Thị trường kính mắt thể thao (cycling, swimming) tăng trưởng mạnh nhờ sự phổ biến của các hoạt động ngoài trời.', 0.88),
(18, 'Các chuỗi cửa hàng kính mắt tổ chức chương trình khám mắt và đo thị lực miễn phí, thu hút hàng nghìn lượt khách hàng.', 0.85),
(18, 'Kính mắt chống ánh sáng xanh (Blue Light Filter) được khuyến nghị sử dụng cho dân văn phòng và người thường xuyên dùng máy tính.', 0.9),
(18, 'Bộ sưu tập kính mắt thời trang mới của Chanel sử dụng chất liệu titan siêu nhẹ và bền bỉ.', 0.87),
(18, 'Nghiên cứu về việc đeo kính áp tròng ban đêm (Ortho-K) giúp điều chỉnh tật khúc xạ không cần phẫu thuật.', 0.89),
(18, 'Cảnh báo về nguy cơ mua phải kính mắt giả, nhái thương hiệu nổi tiếng trên các sàn thương mại điện tử.', 0.94),
(18, 'Giá kính cận đa tròng (Progressive Lenses) có xu hướng giảm, giúp người lớn tuổi dễ dàng tiếp cận hơn.', 0.83),
(18, 'Kính mắt gọng trong suốt và gọng màu pastel là lựa chọn hàng đầu cho phong cách tối giản, nhẹ nhàng.', 0.81),
(18, 'Các bác sĩ khuyến cáo nên đo lại thị lực và thay kính định kỳ 6 tháng một lần để đảm bảo độ chính xác.', 0.86),
(18, 'Công nghệ tròng kính đổi màu (Photochromic) tự động đậm màu khi ra nắng, tiện lợi cho người sử dụng thường xuyên di chuyển.', 0.9),
(18, 'Một thương hiệu kính mắt Việt Nam ra mắt bộ sưu tập sử dụng vật liệu thân thiện với môi trường, làm từ nhựa tái chế.', 0.79),
(18, 'Thị trường kính mắt trẻ em tăng trưởng nhờ sự quan tâm của phụ huynh đến việc bảo vệ thị lực cho con em.', 0.84),
(18, 'Kính mát phân cực (Polarized) giúp loại bỏ ánh sáng chói, rất thích hợp cho người lái xe và đi biển.', 0.88),
(18, 'Phân biệt kính mắt thật và giả qua các dấu hiệu nhận biết như logo, bản lề và chất liệu gọng.', 0.93),
(18, 'Giá kính râm hàng hiệu cao cấp có thể lên đến hàng chục triệu đồng, phục vụ phân khúc khách hàng thượng lưu.', 0.77),
(18, 'Nghiên cứu về công nghệ tròng kính chống sương mù (Anti-fog) được ứng dụng mạnh mẽ trong ngành y tế và thể thao.', 0.85),
(18, 'Kính mắt cận thời trang không độ (Plano) trở thành phụ kiện làm đẹp được nhiều bạn trẻ sử dụng.', 0.75),
(18, 'Các bệnh viện mắt lớn công bố tỷ lệ cận thị ở học sinh Việt Nam đang có xu hướng gia tăng đáng báo động.', 0.91),
(18, 'Chính sách bảo hành tròng kính và gọng kính là yếu tố quan trọng khi người tiêu dùng lựa chọn cửa hàng.', 0.8),
(18, 'Kính áp tròng màu được sử dụng phổ biến trong các dịp lễ hội, nhưng cần đảm bảo vệ sinh nghiêm ngặt.', 0.78),
(18, 'Các mẫu gọng kính làm từ Acetate và Titanium được ưa chuộng nhờ độ bền, nhẹ và khả năng chống dị ứng.', 0.86),
(18, 'Thị trường kính mắt thương hiệu độc lập (Indie Eyewear) phát triển mạnh, mang đến nhiều lựa chọn thiết kế độc đáo.', 0.82),
(18, 'Đeo kính râm thường xuyên ngay cả khi trời râm có thể gây hại cho mắt, chuyên gia khuyến cáo nên sử dụng đúng cách.', 0.89),
(18, 'Các nhà sản xuất kính mắt đang thử nghiệm công nghệ tròng kính thông minh có thể tự điều chỉnh tiêu cự.', 0.92),
(18, 'Khung giờ khám mắt tốt nhất là vào buổi sáng, khi mắt chưa bị mỏi do làm việc trong ngày.', 0.76),
(18, 'Nhiều người trẻ đầu tư vào các loại kính mắt đắt tiền để nâng cao hình ảnh cá nhân và thể hiện đẳng cấp.', 0.74),
(18, 'Cục Quản lý thị trường tịch thu lô hàng kính mắt kém chất lượng, gây hại cho thị lực, không có giấy tờ kiểm định.', 0.95);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(19, 'Thị trường điều hòa không khí bùng nổ trước mùa hè, các hãng điện tử tung ra hàng loạt mẫu mã Inverter tiết kiệm điện.', 0.95),
(19, 'Công nghệ tủ lạnh Side-by-side tích hợp ngăn đông mềm và ngăn lấy nước ngoài đang là lựa chọn hàng đầu của gia đình hiện đại.', 0.92),
(19, 'Chuyên gia điện lạnh hướng dẫn cách vệ sinh lưới lọc máy lạnh định kỳ để tăng hiệu suất làm mát và bảo vệ sức khỏe.', 0.88),
(19, 'Tủ lạnh thông minh LG tích hợp màn hình cảm ứng, cho phép người dùng kiểm tra thực phẩm bên trong mà không cần mở cửa.', 0.9),
(19, 'Công nghệ Inverter giúp máy lạnh Daikin giảm đến 60% điện năng tiêu thụ so với các loại máy lạnh thông thường.', 0.91),
(19, 'Giá bán tủ đông và tủ mát công nghiệp tăng nhẹ do nhu cầu bảo quản hàng hóa tăng cao của các cửa hàng kinh doanh.', 0.87),
(19, 'Các nhà sản xuất chuyển dần sang sử dụng môi chất lạnh R32 và R600A cho tủ lạnh và điều hòa, thân thiện với môi trường.', 0.89),
(19, 'Cảnh báo về nguy cơ cháy nổ từ việc lắp đặt cục nóng máy lạnh sai kỹ thuật hoặc sử dụng dây dẫn kém chất lượng.', 0.94),
(19, 'Máy lạnh có công nghệ lọc không khí, khử khuẩn (Plasma, Nanoe-G) được người tiêu dùng ưu tiên chọn mua trong mùa dịch bệnh.', 0.9),
(19, 'Thị trường tủ lạnh ngăn đông dưới (Bottom-freezer) phát triển mạnh vì tiện lợi cho việc lấy thực phẩm thường xuyên.', 0.85),
(19, 'Hướng dẫn sử dụng máy lạnh kết hợp quạt để làm mát hiệu quả hơn, đồng thời tiết kiệm chi phí điện năng.', 0.82),
(19, 'Các hãng điện tử cam kết chính sách bảo hành máy nén (compressor) tủ lạnh lên đến 10 năm hoặc trọn đời.', 0.86),
(19, 'Chính phủ khuyến khích người dân thay thế các thiết bị điện lạnh cũ, tiêu thụ điện năng cao bằng các thiết bị Inverter mới.', 0.93),
(19, 'Tủ lạnh mini dung tích dưới 100 lít được sinh viên và người độc thân tìm mua nhiều nhất trong mùa tựu trường.', 0.78),
(19, 'Công nghệ làm lạnh đa chiều và làm lạnh nhanh (Super Cool) là các tính năng quan trọng khi chọn mua tủ lạnh.', 0.84),
(19, 'Khảo sát cho thấy Samsung và LG là hai thương hiệu tủ lạnh được người tiêu dùng Việt Nam đánh giá cao nhất.', 0.88),
(19, 'Máy lạnh có chế độ ngủ đêm (Sleep Mode) giúp tự động tăng nhiệt độ để bảo vệ sức khỏe và tiết kiệm điện.', 0.81),
(19, 'Các chuyên gia điện lạnh khuyến cáo nên bảo dưỡng máy lạnh định kỳ hai lần một năm để tránh hỏng hóc và rò rỉ gas.', 0.83),
(19, 'Tủ đông chuyên dụng cho việc bảo quản kem và thực phẩm đông lạnh số lượng lớn có sự đa dạng về dung tích.', 0.77),
(19, 'Công nghệ kháng khuẩn bằng ion bạc (Ag+) được tích hợp trong hầu hết các dòng tủ lạnh hiện đại của Panasonic.', 0.87),
(19, 'Máy lạnh di động (Portable Air Conditioner) được sử dụng phổ biến trong các không gian nhỏ và thuê trọ.', 0.75),
(19, 'So sánh chi phí lắp đặt, bảo trì và hiệu quả làm lạnh giữa máy lạnh Inverter và máy lạnh tiêu chuẩn.', 0.89),
(19, 'Các cửa hàng điện máy tổ chức chương trình thu cũ đổi mới, trợ giá cho khách hàng mua điều hòa.', 0.79),
(19, 'Máy lạnh hai chiều (làm nóng và làm lạnh) được người tiêu dùng miền Bắc ưu tiên sử dụng trong mùa đông.', 0.85),
(19, 'Cảnh báo về việc đặt tủ lạnh quá sát tường hoặc nơi ẩm ướt, làm giảm tuổi thọ và tiêu thụ điện năng nhiều hơn.', 0.91),
(19, 'Công nghệ làm lạnh không đóng tuyết (No Frost) đã trở thành tiêu chuẩn bắt buộc của hầu hết các dòng tủ lạnh mới.', 0.88),
(19, 'Tủ lạnh được trang bị công nghệ khử mùi bằng than hoạt tính và màng lọc Enzyme, loại bỏ mùi hôi thực phẩm.', 0.86),
(19, 'Giá máy lạnh 9000 BTU và 12000 BTU có sự chênh lệch đáng kể, người mua cần tính toán diện tích phòng trước khi chọn.', 0.76),
(19, 'Việc sử dụng tủ đông để bảo quản thực phẩm dự trữ giúp các gia đình giảm thiểu tần suất đi chợ.', 0.74),
(19, 'Lỗ hổng an ninh mạng trên một số mẫu tủ lạnh thông minh đã được các hãng sản xuất khắc phục kịp thời.', 0.92);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(21, 'Máy giặt lồng ngang với công nghệ giặt hơi nước (Steam) đang dẫn đầu thị trường nhờ khả năng diệt khuẩn và làm mềm vải.', 0.95),
(21, 'Đánh giá các dòng máy sấy bơm nhiệt (Heat Pump) tiết kiệm điện năng và bảo vệ sợi vải tốt hơn so với máy sấy ngưng tụ.', 0.93),
(21, 'Công nghệ Inverter và truyền động trực tiếp (Direct Drive) giúp máy giặt hoạt động êm ái, bền bỉ và tiết kiệm điện.', 0.92),
(21, 'Máy giặt lồng đứng vẫn giữ thị phần nhất định nhờ giá thành rẻ và dễ dàng thêm quần áo trong quá trình giặt.', 0.85),
(21, 'Các hãng điện tử giới thiệu máy giặt thông minh có khả năng tự động cân chỉnh lượng nước và bột giặt theo khối lượng quần áo.', 0.9),
(21, 'Người tiêu dùng ưu tiên lựa chọn máy giặt có tính năng giặt nước nóng để loại bỏ vết bẩn cứng đầu và tác nhân gây dị ứng.', 0.88),
(21, 'Hướng dẫn vệ sinh lồng giặt bằng dung dịch chuyên dụng hoặc baking soda để loại bỏ cặn bẩn và vi khuẩn gây mùi hôi.', 0.87),
(21, 'Máy sấy quần áo trở nên phổ biến hơn tại Việt Nam, đặc biệt là trong các căn hộ chung cư thiếu không gian phơi đồ.', 0.89),
(21, 'Cảnh báo về việc để máy giặt ngoài trời hoặc nơi ẩm ướt, gây hư hỏng bảng mạch điện tử và giảm tuổi thọ.', 0.91),
(21, 'Máy giặt cửa ngang Samsung tích hợp cửa phụ AddWash, cho phép thêm đồ giặt bỏ sót mà không cần tắt máy.', 0.86),
(21, 'Nghiên cứu về công nghệ giặt bong bóng siêu mịn (Eco Bubble) giúp bột giặt thẩm thấu sâu hơn vào sợi vải.', 0.9),
(21, 'Giá máy sấy bơm nhiệt cao hơn đáng kể so với máy sấy thông hơi, nhưng được bù lại bằng khả năng tiết kiệm điện vượt trội.', 0.84),
(21, 'Máy giặt kết hợp sấy (Combo Washer Dryer) được giới thiệu là giải pháp tối ưu cho những gia đình có không gian hạn chế.', 0.82),
(21, 'Các chuyên gia khuyến cáo nên phân loại quần áo theo chất liệu (cotton, len, lụa) trước khi giặt để bảo vệ sợi vải.', 0.83),
(21, 'Thị trường máy giặt công nghiệp và máy sấy công suất lớn phát triển mạnh mẽ tại các khách sạn và bệnh viện.', 0.78),
(21, 'Máy giặt lồng ngang LG tích hợp công nghệ trí tuệ nhân tạo (AI) để tối ưu hóa chu trình giặt dựa trên độ mềm của vải.', 0.94),
(21, 'Cục Quản lý thị trường tịch thu lô hàng máy giặt mini xách tay không rõ nguồn gốc, không có tem nhãn hợp quy.', 0.95),
(21, 'Các hãng điện máy áp dụng chính sách bảo hành động cơ máy giặt lên đến 12 năm, cam kết chất lượng sản phẩm.', 0.81),
(21, 'Máy giặt có tính năng tự động phân phối nước giặt, nước xả vải, giúp người dùng tiết kiệm thời gian và chính xác hơn.', 0.87),
(21, 'Nhu cầu mua máy giặt và máy sấy tăng cao vào mùa mưa và mùa nồm ẩm tại các tỉnh miền Bắc.', 0.88),
(21, 'Các chương trình khuyến mãi lớn cho máy giặt cửa ngang Inverter thường được áp dụng vào cuối quý.', 0.79),
(21, 'So sánh độ ồn khi hoạt động của máy giặt truyền động trực tiếp và máy giặt truyền động bằng dây curoa.', 0.84),
(21, 'Máy sấy quần áo sử dụng công nghệ khử trùng bằng tia UV được giới thiệu là giải pháp bảo vệ sức khỏe gia đình.', 0.86),
(21, 'Hãng Electrolux ra mắt máy giặt có thiết kế tối giản, phù hợp với xu hướng nội thất hiện đại.', 0.77),
(21, 'Thủ tục lắp đặt máy giặt và máy sấy phức tạp hơn máy giặt truyền thống do yêu cầu về đường thoát nước và ống thông hơi.', 0.80),
(21, 'Các chuyên gia khuyên nên sử dụng máy sấy bơm nhiệt cho các loại vải nhạy cảm như len và lụa.', 0.89),
(21, 'Máy giặt lồng ngang với khối lượng giặt lớn (trên 12kg) được các gia đình đông thành viên ưa chuộng.', 0.75),
(21, 'Cảnh báo về việc sử dụng quá nhiều bột giặt có thể gây tồn đọng cặn bẩn và làm hỏng máy giặt.', 0.91),
(21, 'Máy giặt có chu trình giặt siêu tốc 15 phút, phù hợp cho các loại quần áo ít bẩn hoặc cần giặt nhanh.', 0.76),
(21, 'Các doanh nghiệp phát triển ứng dụng di động để điều khiển và giám sát máy giặt/sấy từ xa.', 0.83);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(22, 'Xu hướng thiết kế nội thất tối giản (Minimalism) và thân thiện với môi trường đang chiếm lĩnh thị trường trong nước.', 0.95),
(22, 'Các chuyên gia dự đoán nội thất thông minh, đa năng sẽ là giải pháp tối ưu cho các căn hộ chung cư có diện tích nhỏ.', 0.92),
(22, 'Giá đồ nội thất gỗ óc chó và gỗ tự nhiên cao cấp tăng mạnh do nguồn cung khan hiếm và chi phí nhập khẩu tăng.', 0.9),
(22, 'Hiệp hội Nội thất Việt Nam tổ chức triển lãm quốc tế, giới thiệu các sản phẩm nội thất được sản xuất trong nước.', 0.88),
(22, 'Cảnh báo về chất lượng sơn và keo dán trong đồ nội thất gỗ công nghiệp, tiềm ẩn nguy cơ gây hại cho sức khỏe.', 0.93),
(22, 'Các thương hiệu nội thất lớn áp dụng công nghệ thiết kế 3D và thực tế ảo (VR) để khách hàng trải nghiệm sản phẩm.', 0.87),
(22, 'Thị trường đồ nội thất văn phòng có sự tăng trưởng ổn định nhờ nhu cầu thiết lập không gian làm việc tại nhà (work from home).', 0.85),
(22, 'Các kiến trúc sư khuyến nghị sử dụng nội thất màu trung tính và vật liệu thô mộc để tạo cảm giác ấm cúng, sang trọng.', 0.86),
(22, 'Chính phủ ban hành quy chuẩn về vật liệu xây dựng và nội thất an toàn, chống cháy cho các công trình công cộng.', 0.91),
(22, 'Nội thất phong cách Indochine và Bắc Âu (Scandinavian) tiếp tục được ưa chuộng trong các biệt thự và căn hộ cao cấp.', 0.89),
(22, 'Các doanh nghiệp nội thất đẩy mạnh sản xuất các mặt hàng xuất khẩu sang Mỹ và EU, tăng trưởng kim ngạch đáng kể.', 0.84),
(22, 'Việc sử dụng nội thất tích hợp đèn LED và sạc không dây đang trở thành xu hướng mới trong thiết kế phòng ngủ.', 0.82),
(22, 'Thị trường đồ nội thất thanh lý và đồ cũ có sự sôi động nhất định, thu hút những người có ngân sách hạn chế.', 0.75),
(22, 'Cục Quản lý thị trường triệt phá kho hàng lớn chứa đồ nội thất giả mạo thương hiệu nổi tiếng, kém chất lượng.', 0.94),
(22, 'Nội thất làm từ mây, tre, đan lát truyền thống đang được hiện đại hóa, thu hút sự quan tâm của thị trường quốc tế.', 0.8);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(23, 'Các mẫu tủ quần áo âm tường được thiết kế tối ưu không gian, giúp căn phòng ngủ trở nên gọn gàng và hiện đại.', 0.95),
(23, 'Giường ngủ thông minh tích hợp chức năng massage, điều chỉnh độ cao và hệ thống âm thanh đang là sản phẩm cao cấp được săn đón.', 0.92),
(23, 'So sánh độ bền và giá trị thẩm mỹ giữa giường ngủ gỗ tự nhiên (sồi, óc chó) và giường bọc da công nghiệp cao cấp.', 0.91),
(23, 'Xu hướng tủ quần áo không cánh (Walk-in Closet) được ưa chuộng trong các phòng ngủ master có diện tích lớn.', 0.88),
(23, 'Chuyên gia nội thất chia sẻ mẹo thiết kế tủ đựng đồ đa năng, kết hợp bàn làm việc và giá sách trong không gian nhỏ.', 0.85),
(23, 'Giường ngủ có ngăn kéo và hộc chứa đồ phía dưới là giải pháp lưu trữ thông minh được các gia đình có con nhỏ yêu thích.', 0.89),
(23, 'Giá giường ngủ gỗ công nghiệp MDF chống ẩm có sự chênh lệch lớn, phụ thuộc vào chất lượng cốt gỗ và lớp phủ bề mặt.', 0.84),
(23, 'Các bộ sưu tập nội thất phòng ngủ mới nhất của IKEA nhấn mạnh vào tính thực dụng và thiết kế theo phong cách Bắc Âu.', 0.86),
(23, 'Tủ quần áo cánh lùa (Sliding door) đang dần thay thế tủ cánh mở truyền thống, giúp tiết kiệm không gian mở cửa.', 0.87),
(23, 'Cảnh báo về việc mua giường tủ gỗ công nghiệp có hàm lượng Formaldehyde cao, gây ảnh hưởng đến sức khỏe hô hấp.', 0.94),
(23, 'Các nhà sản xuất giới thiệu giường ngủ có hệ thống đèn LED cảm ứng dưới gầm, tiện lợi cho việc di chuyển ban đêm.', 0.81),
(23, 'Thị trường đệm (nệm) lò xo và đệm cao su thiên nhiên cạnh tranh gay gắt, tập trung vào chất lượng giấc ngủ.', 0.83),
(23, 'Tủ đầu giường thông minh tích hợp sạc không dây và cổng USB là phụ kiện không thể thiếu trong phòng ngủ hiện đại.', 0.82),
(23, 'Thiết kế tủ quần áo kịch trần (tủ cao sát trần) giúp tối đa hóa không gian lưu trữ và tăng tính thẩm mỹ.', 0.9),
(23, 'Giường ngủ bệt theo phong cách Nhật Bản được ưa chuộng vì tạo cảm giác rộng rãi và thoáng đãng cho căn phòng.', 0.77),
(23, 'Giá tủ quần áo cánh kính có xu hướng tăng cao, trở thành điểm nhấn sang trọng cho nội thất phòng ngủ.', 0.79),
(23, 'Các công ty nội thất cam kết sử dụng phụ kiện (bản lề, ray trượt) giảm chấn cao cấp cho giường và tủ.', 0.88),
(23, 'Nghiên cứu về tầm quan trọng của việc chọn chiều cao giường ngủ phù hợp với độ tuổi và thể trạng người sử dụng.', 0.76),
(23, 'Giường tầng thông minh có thể gấp gọn thành sofa được giới thiệu là giải pháp tuyệt vời cho phòng trẻ em.', 0.75),
(23, 'Các mẫu tủ đựng giày dép thông minh có hệ thống khử mùi và sấy khô được người tiêu dùng quan tâm.', 0.85),
(23, 'Phân biệt giường gỗ tự nhiên và giường gỗ công nghiệp qua vân gỗ, trọng lượng và độ bền của vật liệu.', 0.93),
(23, 'Tủ đựng đồ nhà bếp với các khoang chứa được thiết kế khoa học, giúp tối ưu hóa không gian lưu trữ.', 0.81),
(23, 'Giường ngủ công thái học (Ergonomic) giúp hỗ trợ cột sống, giảm đau lưng cho người cao tuổi.', 0.86),
(23, 'Việc đặt tủ quần áo và giường ngủ theo phong thủy được nhiều gia đình Việt Nam quan tâm khi thiết kế nội thất.', 0.74),
(23, 'Các cửa hàng nội thất tổ chức chương trình giảm giá lớn cho các bộ combo giường và tủ quần áo.', 0.78),
(23, 'Công nghệ phủ Melamine và Laminate giúp tủ quần áo chống trầy xước và chống ẩm mốc hiệu quả.', 0.9),
(23, 'Tủ đựng hồ sơ và tài liệu văn phòng được thiết kế theo hướng module, dễ dàng lắp đặt và di chuyển.', 0.80),
(23, 'Giường ngủ có đệm cao su thiên nhiên được chứng minh là tốt nhất cho người bị đau lưng và thoái hóa cột sống.', 0.89),
(23, 'Lưu ý khi mua tủ quần áo tại các cửa hàng không uy tín, dễ mua phải hàng kém chất lượng, dễ bong tróc.', 0.91),
(23, 'Các mẫu giường ngủ kiểu dáng đơn giản, không đầu giường đang là xu hướng thiết kế nội thất hiện đại.', 0.73);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(24, 'Kinh nghiệm chọn sofa góc L và sofa văng phù hợp với diện tích phòng khách, tối ưu hóa không gian sử dụng.', 0.95),
(24, 'Thị trường bàn ăn thông minh có thể mở rộng, gấp gọn đang bùng nổ, là giải pháp lý tưởng cho các căn hộ nhỏ.', 0.92),
(24, 'Các mẫu ghế làm việc công thái học (Ergonomic) giúp hỗ trợ cột sống, giảm căng thẳng cho dân văn phòng.', 0.93),
(24, 'Sofa da thật có giá thành cao nhưng độ bền và tính thẩm mỹ vượt trội, là lựa chọn hàng đầu của phân khúc cao cấp.', 0.91),
(24, 'Xu hướng bàn ghế phòng khách làm từ gỗ óc chó và gỗ tự nhiên cao cấp tiếp tục khẳng định vị thế sang trọng.', 0.9),
(24, 'Các chuyên gia nội thất khuyến nghị sử dụng sofa vải nỉ và sofa nhung cho phòng khách có phong cách ấm cúng, cổ điển.', 0.85),
(24, 'Bàn ghế ăn mặt đá tự nhiên được ưa chuộng nhờ khả năng chịu nhiệt, chống trầy xước và dễ dàng vệ sinh.', 0.88),
(24, 'Giá sofa giường đa năng có xu hướng tăng nhẹ do nhu cầu về nội thất tích hợp chức năng ngày càng cao.', 0.84),
(24, 'Các thương hiệu nội thất tổ chức chương trình tri ân, giảm giá lớn cho các bộ sofa nhập khẩu từ Ý và Malaysia.', 0.87),
(24, 'Mẹo bảo quản và làm sạch sofa da, sofa vải nỉ đúng cách để tăng tuổi thọ và giữ được vẻ đẹp ban đầu.', 0.86),
(24, 'Thị trường ghế gaming và ghế làm việc có sự cạnh tranh gay gắt, tập trung vào thiết kế hiện đại và độ thoải mái.', 0.89),
(24, 'Các mẫu bàn làm việc có thể điều chỉnh độ cao (standing desk) được khuyến nghị để thay đổi tư thế làm việc, tốt cho sức khỏe.', 0.92),
(24, 'Bàn ghế cafe ngoài trời sử dụng vật liệu chống thấm nước và chống tia UV được các quán kinh doanh ưa chuộng.', 0.78),
(24, 'Sofa đơn (sofa armchair) được sử dụng linh hoạt để bổ sung chỗ ngồi hoặc tạo điểm nhấn trong phòng khách.', 0.77),
(24, 'Các nhà sản xuất cam kết sử dụng mút đệm cao cấp có độ đàn hồi cao cho sofa, đảm bảo không bị xẹp lún sau thời gian dài.', 0.83),
(24, 'Bàn ăn mặt kính cường lực và bàn ăn gỗ sồi đang là hai lựa chọn phổ biến nhất cho không gian phòng ăn hiện đại.', 0.81),
(24, 'Cảnh báo về việc mua sofa giá rẻ kém chất lượng, sử dụng khung gỗ tạp và mút đệm dễ xẹp lún.', 0.94),
(24, 'Các kiến trúc sư tư vấn chọn màu sắc sofa hài hòa với màu tường và sàn nhà để tạo sự đồng nhất cho không gian.', 0.82),
(24, 'Ghế bar và bàn cao được sử dụng phổ biến trong thiết kế quầy bếp đảo và khu vực ăn sáng nhanh.', 0.76),
(24, 'Nội thất phòng khách theo phong cách cổ điển thường sử dụng sofa bọc da hoặc vải nhung với hoa văn cầu kỳ.', 0.79),
(24, 'Sự khác biệt về giá và chất lượng giữa sofa da công nghiệp và sofa da thật được người tiêu dùng quan tâm.', 0.9),
(24, 'Bàn trà mặt đá và bàn trà mặt kính được ưa chuộng nhờ tính dễ vệ sinh và khả năng chống ẩm mốc.', 0.80),
(24, 'Các mẫu ghế ăn bọc da PVC có giá thành phải chăng, phù hợp với nhiều gia đình có ngân sách vừa phải.', 0.75),
(24, 'Ghế sofa thư giãn (Recliner) được tích hợp chế độ massage và điều chỉnh độ ngả lưng bằng điện.', 0.88),
(24, 'Công ty nội thất giới thiệu bộ sưu tập bàn ghế làm việc dành riêng cho không gian phòng ngủ nhỏ.', 0.74),
(24, 'Sofa module, có thể tùy chỉnh kích thước và hình dạng, là giải pháp linh hoạt cho mọi không gian sống.', 0.89),
(24, 'Việc đặt sofa và bàn trà theo hướng phong thủy được nhiều gia chủ tin tưởng để thu hút tài lộc.', 0.73),
(24, 'Các loại ghế nhựa đúc nguyên khối được sử dụng rộng rãi tại các quán ăn bình dân và khu ẩm thực.', 0.70),
(24, 'Giá bàn ghế ăn 6 ghế và 8 ghế có sự chênh lệch lớn, tùy thuộc vào chất liệu và kiểu dáng thiết kế.', 0.86),
(24, 'Thị trường ghế làm việc công thái học có xu hướng tăng trưởng liên tục do sự quan tâm đến sức khỏe cột sống.', 0.91);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(25, 'Bộ Công Thương dự báo mức tiêu thụ hàng hóa và dịch vụ sẽ tăng trưởng mạnh mẽ trong quý IV nhờ các dịp lễ lớn.', 0.95),
(25, 'Các chuyên gia phân tích hành vi mua sắm của người tiêu dùng Việt Nam đang chuyển dịch từ offline sang các kênh đa nền tảng.', 0.92),
(25, 'Tổng Công ty Bán lẻ Việt Nam công bố chiến lược mở rộng chuỗi cửa hàng ra các tỉnh thành cấp 2, cấp 3.', 0.9),
(25, 'Ngân hàng Nhà nước đẩy mạnh các giải pháp thúc đẩy thanh toán không dùng tiền mặt, tăng cường giao dịch điện tử.', 0.88),
(25, 'Thị trường bán lẻ Việt Nam tiếp tục thu hút sự quan tâm của các tập đoàn đa quốc gia nhờ quy mô dân số trẻ và thu nhập tăng.', 0.93),
(25, 'Chính phủ ban hành chính sách hỗ trợ các doanh nghiệp bán lẻ nhỏ và vừa trong việc chuyển đổi số và tối ưu hóa vận hành.', 0.87),
(25, 'Cảnh báo về tình trạng hàng giả, hàng nhái được bán tràn lan, gây ảnh hưởng đến niềm tin và quyền lợi của người tiêu dùng.', 0.94),
(25, 'Xu hướng mua sắm có trách nhiệm và ưu tiên các sản phẩm thân thiện với môi trường đang dần định hình lại thị trường bán lẻ.', 0.85),
(25, 'Các chuỗi cung ứng hàng hóa cơ bản được đảm bảo ổn định, không xảy ra tình trạng thiếu hàng hóa cục bộ dịp cuối năm.', 0.86),
(25, 'Công nghệ trí tuệ nhân tạo (AI) được ứng dụng trong việc quản lý kho bãi và dự báo nhu cầu tiêu dùng của khách hàng.', 0.89),
(25, 'Khảo sát cho thấy chi tiêu cho các mặt hàng thiết yếu như thực phẩm và nhu yếu phẩm vẫn chiếm tỷ trọng lớn nhất.', 0.83),
(25, 'Nhiều doanh nghiệp bán lẻ lớn tại Việt Nam nỗ lực cải thiện trải nghiệm mua sắm tích hợp (Omnichannel).', 0.81),
(25, 'Giá thuê mặt bằng tại các trung tâm thương mại lớn duy trì ổn định, thu hút các thương hiệu quốc tế mở rộng chi nhánh.', 0.78),
(25, 'Thị trường bán lẻ dịch vụ (như ăn uống, giải trí) có sự phục hồi mạnh mẽ sau khi các biện pháp giãn cách được nới lỏng.', 0.76),
(25, 'Các đơn vị bán lẻ tăng cường bảo mật thông tin khách hàng, đối phó với các nguy cơ rò rỉ dữ liệu và tấn công mạng.', 0.91);
