INSERT INTO topic_documents (topic_id, text, weight) VALUES
(26, 'Ngân hàng X ra mắt thẻ tín dụng du lịch cao cấp, tích hợp ưu đãi phòng chờ sân bay và hoàn tiền giao dịch quốc tế.', 0.95),
(26, 'Xu hướng thanh toán không tiếp xúc (contactless) qua thẻ chip Visa/Mastercard ngày càng phổ biến tại các chuỗi cửa hàng tiện lợi.', 0.92),
(26, 'Cảnh báo lừa đảo chiếm đoạt thông tin thẻ ngân hàng qua các tin nhắn giả mạo (smishing) và email lừa đảo (phishing).', 0.94),
(26, 'Ngân hàng Nhà nước quy định chặt chẽ hơn về việc mở thẻ tín dụng và giới hạn tín dụng, nhằm giảm thiểu nợ xấu.', 0.91),
(26, 'Các ngân hàng đẩy mạnh phát hành thẻ ghi nợ nội địa tích hợp công nghệ chip EMV để tăng cường bảo mật cho giao dịch.', 0.88),
(26, 'Chính sách hoàn tiền (cashback) và tích lũy điểm thưởng là yếu tố cạnh tranh hàng đầu giữa các loại thẻ tín dụng trên thị trường.', 0.9),
(26, 'Thẻ đồng thương hiệu (co-branded card) giữa ngân hàng và các chuỗi bán lẻ lớn mang lại nhiều ưu đãi độc quyền cho người sở hữu.', 0.87),
(26, 'Phí thường niên và lãi suất thẻ tín dụng có sự khác biệt lớn giữa các ngân hàng, người dùng cần so sánh kỹ trước khi mở thẻ.', 0.85),
(26, 'Công nghệ bảo mật 3D Secure 2.0 được áp dụng rộng rãi cho các giao dịch thanh toán thẻ trực tuyến, chống gian lận.', 0.89),
(26, 'Thẻ tín dụng ảo (virtual credit card) được khuyến khích sử dụng cho các giao dịch trực tuyến để bảo mật thông tin thẻ vật lý.', 0.83),
(26, 'Ngân hàng A ra mắt thẻ tín dụng dành riêng cho giới trẻ, có thiết kế độc đáo và tích hợp nhiều ưu đãi ăn uống, giải trí.', 0.81),
(26, 'Hướng dẫn sử dụng thẻ ngân hàng đúng cách để tránh bị nuốt thẻ tại các máy ATM và rò rỉ mã PIN.', 0.86),
