INSERT INTO topic_documents (topic_id, text, weight) VALUES
(27, 'Trung tâm thương mại Gigamall tại TP.HCM khai trương khu vui chơi giải trí mới, thu hút hàng chục nghìn lượt khách.', 0.95),
