INSERT INTO topic_documents (topic_id, text, weight) VALUES
(56, 'TV 8K của Sony và Samsung dẫn đầu thị trường TV cao cấp, cạnh tranh nhau về chất lượng hình ảnh và công nghệ xử lý.', 0.95),
(56, 'Các ứng dụng giải trí trực tuyến (Netflix, YouTube, FPT Play) tích hợp sẵn trên Smart TV, tạo nên hệ sinh thái giải trí đa dạng.', 0.92),
(56, 'Công nghệ MicroLED được dự đoán sẽ thay thế OLED trong tương lai nhờ khả năng đạt độ sáng và độ bền vượt trội hơn.', 0.93),
(56, 'Hướng dẫn chọn mua TV phù hợp với phòng khách: lưu ý khoảng cách xem, độ phân giải và công nghệ màn hình.', 0.88),
(56, 'Thị trường TV 4K có sự tăng trưởng mạnh mẽ, các sản phẩm giá rẻ hơn, dễ tiếp cận hơn với người tiêu dùng phổ thông.', 0.90),
(56, 'Các hãng TV giới thiệu công nghệ âm thanh vòm Dolby Atmos và DTS:X tích hợp sẵn, mang lại trải nghiệm điện ảnh tại nhà.', 0.87),
(56, 'Xu hướng TV thông minh (Smart TV) được tích hợp trợ lý ảo (Google Assistant, Alexa) và điều khiển bằng giọng nói tiếng Việt.', 0.86),
(56, 'Sự cạnh tranh gay gắt giữa TV OLED của LG và TV QLED của Samsung trong phân khúc TV cao cấp.', 0.91),
