(56, 'Sự khác biệt giữa TV thông minh sử dụng hệ điều hành Google TV, Android TV và WebOS.', 0.76);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(57, 'Đánh giá tai nghe không dây chống ồn (True Wireless ANC) mới nhất của Sony/Apple, với chất lượng âm thanh và khả năng chống ồn vượt trội.', 0.95),
