INSERT INTO topic_documents (topic_id, text, weight) VALUES
(58, 'Tập đoàn Vingroup công bố báo cáo tài chính quý 3 với lợi nhuận sau thuế tăng trưởng ấn tượng nhờ mảng công nghiệp và công nghệ.', 0.95),
