INSERT INTO topic_documents (topic_id, text, weight) VALUES
(65, 'Acecook Việt Nam tiếp tục dẫn đầu thị phần mì ăn liền với hơn 50% thị phần, nhờ mạng lưới phân phối rộng khắp và đa dạng sản phẩm.', 0.95),
(65, 'Acecook giới thiệu sản phẩm mì ăn liền hương vị địa phương mới, sử dụng nguyên liệu truyền thống để mở rộng phân khúc khách hàng.', 0.92),
(65, 'Các hoạt động kiểm soát chất lượng sản phẩm của Acecook được tăng cường, áp dụng tiêu chuẩn HACCP và ISO 22000 trong toàn bộ quy trình sản xuất.', 0.94),
(65, 'Chiến lược thị trường của Acecook tập trung vào phân khúc cao cấp, với các sản phẩm mì ăn liền được bổ sung dinh dưỡng và nguyên liệu tươi.', 0.88),
(65, 'Ngành mì ăn liền Việt Nam đối mặt với sự cạnh tranh gay gắt từ các thương hiệu nội địa và các sản phẩm mì nhập khẩu từ Hàn Quốc, Thái Lan.', 0.91),
(65, 'Acecook Việt Nam công bố kết quả kinh doanh quý 3 tăng trưởng ổn định, nhờ sự phục hồi của thị trường tiêu dùng nội địa.', 0.86),
(65, 'Các chuyên gia phân tích về xu hướng tiêu dùng mì ăn liền tại Việt Nam, cho thấy nhu cầu về sản phẩm tiện lợi và dinh dưỡng tăng cao.', 0.87),
(65, 'Acecook đầu tư vào dây chuyền sản xuất mới, ứng dụng công nghệ tự động hóa để tăng công suất và giảm chi phí sản xuất.', 0.90),
(65, 'Tập đoàn Acecook triển khai các hoạt động xã hội, từ thiện, tập trung vào hỗ trợ cộng đồng bị ảnh hưởng bởi thiên tai.', 0.85),
(65, 'Giá nguyên liệu đầu vào (bột mì, dầu cọ) có sự biến động lớn, gây áp lực lên giá thành sản phẩm mì ăn liền.', 0.93),
(65, 'Các sản phẩm mì ăn liền không chiên của Acecook được người tiêu dùng quan tâm, đáp ứng xu hướng ăn uống lành mạnh.', 0.84),
(65, 'Acecook tăng cường hợp tác với các nhà cung ứng nguyên liệu trong nước để đảm bảo chất lượng và nguồn gốc sản phẩm.', 0.89),
(65, 'Chiến dịch quảng cáo mới của Acecook sử dụng người nổi tiếng, tập trung vào việc truyền tải thông điệp về sự tiện lợi và quen thuộc.', 0.83),
(65, 'Ngành mì ăn liền Việt Nam nỗ lực xuất khẩu sản phẩm sang các thị trường khó tính như Mỹ và châu Âu.', 0.81),
(65, 'Các sản phẩm phở, hủ tiếu ăn liền của Acecook được người tiêu dùng nước ngoài đón nhận tích cực.', 0.88),
(65, 'Acecook đầu tư vào nghiên cứu và phát triển để tạo ra các loại gia vị và nước sốt độc quyền, tăng hương vị sản phẩm.', 0.87),
(65, 'Cảnh báo về các sản phẩm mì ăn liền giả, nhái thương hiệu Acecook được bày bán trên thị trường.', 0.95),
(65, 'Các chiến lược phân phối của Acecook qua kênh siêu thị, chợ truyền thống và sàn thương mại điện tử.', 0.80),
(65, 'Acecook Việt Nam tăng cường các hoạt động bảo vệ môi trường, tập trung vào giảm thiểu rác thải nhựa trong bao bì.', 0.84),
(65, 'Việc áp dụng công nghệ truy xuất nguồn gốc (traceability) cho nguyên liệu sản xuất mì ăn liền của Acecook.', 0.86),
(65, 'Thị trường mì ăn liền dành cho trẻ em có sự tăng trưởng nhẹ, Acecook giới thiệu các sản phẩm dinh dưỡng hơn.', 0.79),
(65, 'Các doanh nghiệp sản xuất mì ăn liền Việt Nam kiến nghị Chính phủ về chính sách thuế đối với nguyên liệu nhập khẩu.', 0.82),
(65, 'Acecook triển khai chương trình khuyến mãi lớn, giảm giá trực tiếp và tặng kèm sản phẩm để kích cầu tiêu dùng.', 0.90),
(65, 'Nghiên cứu về việc sử dụng các loại bột mì và tinh bột khác nhau để tạo ra các loại sợi mì đa dạng.', 0.83),
(65, 'Văn hóa ăn mì gói và vai trò của Acecook trong đời sống ẩm thực của người Việt Nam.', 0.77),
(65, 'Acecook mở rộng nhà máy sản xuất tại các khu công nghiệp, tạo thêm nhiều việc làm cho người lao động địa phương.', 0.89),
(65, 'Sự khác biệt trong chiến lược định giá sản phẩm mì ăn liền giữa Acecook và các đối thủ cạnh tranh.', 0.81),
(65, 'Acecook tăng cường kiểm soát khâu vận chuyển và bảo quản để đảm bảo chất lượng sản phẩm đến tay người tiêu dùng.', 0.85),
(65, 'Nhu cầu về mì ăn liền chay và các sản phẩm dành cho người ăn kiêng có xu hướng tăng, Acecook nắm bắt xu hướng này.', 0.87),
(65, 'Hiệp hội Mì ăn liền Việt Nam công bố sản lượng tiêu thụ mì gói tại Việt Nam đạt mức cao nhất khu vực.', 0.92);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(66, 'Heineken ra mắt sản phẩm bia không cồn mới, đáp ứng xu hướng tiêu dùng lành mạnh và các quy định về nồng độ cồn khi lái xe.', 0.95),
(66, 'Ngành công nghiệp bia đối mặt với thuế tiêu thụ đặc biệt tăng cao, gây áp lực lớn lên giá bán lẻ và lợi nhuận của các công ty.', 0.94),
(66, 'Sabeco công bố kết quả kinh doanh khả quan trong quý vừa qua, nhờ sự phục hồi của thị trường du lịch và dịch vụ ăn uống.', 0.92),
(66, 'Carlsberg tăng cường chiến lược Marketing, tập trung vào các sự kiện thể thao và âm nhạc để quảng bá thương hiệu.', 0.88),
(66, 'Thị trường bia Việt Nam có sự cạnh tranh gay gắt giữa bia nội địa (Sabeco) và bia ngoại (Heineken, Carlsberg, AB InBev).', 0.91),
(66, 'Xu hướng tiêu thụ bia tươi và bia thủ công (Craft Beer) tại các thành phố lớn có sự tăng trưởng đáng kể.', 0.87),
(66, 'Các công ty bia đầu tư vào công nghệ sản xuất hiện đại, giảm thiểu tiêu thụ năng lượng và nước trong quá trình ủ bia.', 0.90),
(66, 'Chính phủ Việt Nam kiểm soát chặt chẽ các chiến dịch quảng cáo bia rượu, đặc biệt là trên các phương tiện truyền thông đại chúng.', 0.93),
(66, 'Các hãng bia lớn cam kết giảm thiểu rác thải nhựa và sử dụng bao bì thân thiện với môi trường (lon, chai thủy tinh tái chế).', 0.85),
(66, 'Sabeco mở rộng thị trường xuất khẩu sang các nước châu Á và châu Phi, tăng cường sự hiện diện của bia Việt trên bản đồ thế giới.', 0.89),
(66, 'Heineken Việt Nam triển khai các hoạt động xã hội nhằm nâng cao ý thức lái xe an toàn và không uống rượu bia khi tham gia giao thông.', 0.84),
(66, 'Giá nguyên liệu sản xuất bia (malt, hoa bia) có sự biến động lớn, ảnh hưởng đến chi phí sản xuất của các nhà máy.', 0.83),
(66, 'Các công ty bia giới thiệu các sản phẩm bia với hương vị trái cây và các loại bia ít calo, phù hợp với người trẻ.', 0.81),
(66, 'Thị trường bia không cồn có sự phát triển mạnh mẽ, được các hãng bia lớn đầu tư nghiên cứu và phát triển sản phẩm mới.', 0.92),
(66, 'Carlsberg Việt Nam đẩy mạnh kênh phân phối trực tiếp và hợp tác với các chuỗi nhà hàng lớn để tăng doanh số.', 0.86),
(66, 'Nghiên cứu về tác động của các chính sách kiểm soát nồng độ cồn đối với doanh thu và lợi nhuận của ngành bia.', 0.90),
(66, 'Các hãng bia lớn tăng cường sử dụng năng lượng tái tạo (điện mặt trời) trong các nhà máy sản xuất bia.', 0.88),
(66, 'Sabeco công bố cổ tức hấp dẫn, thu hút sự quan tâm của các nhà đầu tư trên thị trường chứng khoán.', 0.85),
(66, 'Việc áp dụng công nghệ truy xuất nguồn gốc cho sản phẩm bia để đảm bảo chất lượng và nguồn gốc xuất xứ.', 0.82),
(66, 'Các hãng bia giới thiệu các loại bia phiên bản giới hạn (limited edition) trong các dịp lễ tết và sự kiện đặc biệt.', 0.79),
(66, 'Thị trường bia tại Việt Nam có sự phân hóa rõ rệt giữa phân khúc cao cấp, tầm trung và bình dân.', 0.77),
(66, 'Các công ty bia nỗ lực thâm nhập vào các kênh bán hàng trực tuyến và ứng dụng giao hàng nhanh.', 0.84),
(66, 'Cảnh báo về các sản phẩm bia giả, nhái thương hiệu được bày bán trên thị trường, gây ảnh hưởng đến uy tín các hãng.', 0.95),
(66, 'Heineken Việt Nam được vinh danh là doanh nghiệp phát triển bền vững hàng đầu trong ngành công nghiệp đồ uống.', 0.91),
(66, 'Các nhà máy bia tăng cường hệ thống xử lý nước thải để đáp ứng các tiêu chuẩn môi trường nghiêm ngặt.', 0.87),
(66, 'Sự khác biệt trong chiến lược định giá và phân phối sản phẩm giữa bia quốc tế và bia nội địa.', 0.83),
(66, 'Ngành bia đối diện với thách thức về lực lượng lao động kỹ thuật cao trong các khâu sản xuất và kiểm soát chất lượng.', 0.78),
(66, 'Các công ty bia mở rộng đầu tư vào các hệ thống lạnh và tủ trưng bày tại các điểm bán lẻ.', 0.80),
(66, 'Chính sách thuế nhập khẩu đối với các loại bia đặc biệt (specialty beer) từ châu Âu và Mỹ.', 0.76),
(66, 'Các tập đoàn bia lớn đẩy mạnh chiến lược M&A, mua lại các thương hiệu bia nhỏ và bia thủ công.', 0.89);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(70, 'Petrolimex công bố điều chỉnh giá xăng dầu theo chu kỳ 10 ngày, với mức tăng/giảm phụ thuộc vào giá dầu thế giới.', 0.95),
(70, 'Thị trường xăng dầu nội địa có những diễn biến phức tạp, các doanh nghiệp gặp khó khăn trong việc đảm bảo nguồn cung và chiết khấu.', 0.94),
(70, 'Các công ty xăng dầu đầu tư vào năng lượng sạch, mở rộng kinh doanh các loại nhiên liệu sinh học (E5 RON 92, B5) và trạm sạc điện.', 0.92),
(70, 'PVOil đẩy mạnh chiến lược phát triển trạm xăng thông minh (Smart Filling Station), áp dụng thanh toán không tiền mặt và tự động hóa.', 0.88),
(70, 'Chính phủ Việt Nam kiểm soát chặt chẽ giá bán lẻ xăng dầu để ổn định thị trường và kiểm soát lạm phát.', 0.91),
(70, 'Các doanh nghiệp xăng dầu đối mặt với thách thức về chi phí vận chuyển và bảo hiểm tăng cao, ảnh hưởng đến giá thành.', 0.90),
(70, 'Petrolimex và PVOil cạnh tranh gay gắt về thị phần bán lẻ, tập trung vào chất lượng dịch vụ và mạng lưới phân phối rộng.', 0.87),
(70, 'Xu hướng sử dụng xăng E5 RON 92 có sự giảm nhẹ do người tiêu dùng chuyển sang sử dụng xăng RON 95 cao cấp hơn.', 0.85),
(70, 'Các công ty xăng dầu lớn mở rộng kinh doanh sang lĩnh vực phi xăng dầu, như cửa hàng tiện lợi và dịch vụ rửa xe tại trạm xăng.', 0.86),
(70, 'Việc áp dụng hóa đơn điện tử tại các trạm xăng dầu được yêu cầu bắt buộc để tăng cường quản lý thuế.', 0.93),
(70, 'Các doanh nghiệp xăng dầu nhỏ kiến nghị Chính phủ về cơ chế phân phối và nguồn cung xăng dầu, tránh tình trạng độc quyền.', 0.89),
(70, 'Nghiên cứu về việc sử dụng công nghệ AI để dự báo nhu cầu tiêu thụ xăng dầu và tối ưu hóa tồn kho.', 0.84),
(70, 'Giá dầu thô thế giới có sự biến động mạnh do các căng thẳng địa chính trị và quyết định cắt giảm sản lượng của OPEC+.', 0.95),
(70, 'Các công ty xăng dầu đầu tư vào hệ thống kho chứa và vận tải hiện đại để đảm bảo an toàn và giảm thất thoát.', 0.81),
(70, 'Chính sách thuế môi trường đối với xăng dầu được điều chỉnh để khuyến khích sử dụng nhiên liệu sạch.', 0.88),
(70, 'Petrolimex mở rộng mạng lưới các trạm sạc điện cho xe ô tô điện tại các tuyến đường cao tốc và thành phố lớn.', 0.92),
(70, 'Các chuyên gia phân tích về vai trò của Quỹ Bình ổn giá xăng dầu trong việc điều tiết thị trường nội địa.', 0.83),
(70, 'PVOil tăng cường hợp tác với các đối tác nước ngoài để nhập khẩu nguồn dầu thô chất lượng cao.', 0.80),
(70, 'Các trạm xăng dầu được yêu cầu lắp đặt hệ thống phòng cháy chữa cháy và an toàn lao động nghiêm ngặt.', 0.86),
(70, 'Thị trường nhiên liệu diesel (DO) có sự tăng trưởng ổn định nhờ nhu cầu vận tải và logistics phục hồi.', 0.78),
(70, 'Các công ty xăng dầu đối mặt với thách thức về chuyển đổi năng lượng và xu hướng giảm dần nhiên liệu hóa thạch.', 0.91),
(70, 'Việc sử dụng các ứng dụng di động để tìm kiếm trạm xăng, kiểm tra giá và tích điểm khách hàng thân thiết.', 0.85),
(70, 'Giá xăng dầu Việt Nam chịu ảnh hưởng trực tiếp từ giá dầu thô Brent và WTI trên sàn giao dịch quốc tế.', 0.94),
(70, 'Các doanh nghiệp xăng dầu tăng cường kiểm soát chất lượng xăng dầu, chống pha trộn và buôn bán hàng giả.', 0.93),
(70, 'Nghiên cứu về việc sử dụng xăng sinh học E10 (10% ethanol) trong tương lai để giảm phát thải carbon.', 0.82),
(70, 'Các công ty xăng dầu đầu tư vào hệ thống camera giám sát và bảo mật tại các trạm bán lẻ.', 0.77),
(70, 'Thị trường nhiên liệu máy bay (Jet A-1) có sự tăng trưởng mạnh mẽ nhờ sự phục hồi của ngành hàng không.', 0.89),
(70, 'Petrolimex công bố chiến lược phát triển bền vững, tập trung vào việc bảo vệ môi trường và sử dụng năng lượng hiệu quả.', 0.84),
(70, 'Các quy định mới về tiêu chuẩn khí thải Euro 5 và Euro 6 đối với các loại nhiên liệu xăng dầu.', 0.87),
(70, 'Các công ty xăng dầu nhỏ kêu gọi Chính phủ hỗ trợ vốn để nhập khẩu và dự trữ xăng dầu trong nước.', 0.81);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(71, 'Long Châu mở rộng quy mô chuỗi nhà thuốc lên 1.000 cửa hàng trên toàn quốc, dẫn đầu về tốc độ phát triển trong ngành bán lẻ dược phẩm.', 0.95),
(71, 'Các công ty dược phẩm đẩy mạnh sản xuất thuốc generic (thuốc gốc), cung cấp thuốc điều trị chất lượng với giá thành hợp lý.', 0.92),
(71, 'Bộ Y tế kiểm soát chặt chẽ giá thuốc và dược phẩm, yêu cầu niêm yết giá công khai để đảm bảo quyền lợi người tiêu dùng.', 0.94),
(71, 'Pharmacity tập trung vào chuyển đổi số, áp dụng công nghệ vào quản lý kho và bán hàng trực tuyến (online pharmacy).', 0.88),
(71, 'Ngành dược phẩm Việt Nam nỗ lực đáp ứng tiêu chuẩn GMP-WHO để xuất khẩu thuốc sang các thị trường quốc tế.', 0.91),
(71, 'Các công ty dược đầu tư vào nghiên cứu và phát triển (R&D) các loại thuốc đặc trị và vắc xin mới.', 0.90),
(71, 'Xu hướng sử dụng thực phẩm chức năng và các sản phẩm chăm sóc sức khỏe có nguồn gốc từ thảo dược thiên nhiên.', 0.87),
(71, 'Các chuỗi nhà thuốc lớn cạnh tranh bằng việc mở rộng danh mục sản phẩm, bao gồm cả mỹ phẩm và thiết bị y tế.', 0.85),
(71, 'Chính phủ ban hành chính sách ưu đãi thuế cho các công ty dược phẩm đầu tư vào sản xuất dược liệu trong nước.', 0.86),
(71, 'Các công ty dược phẩm Việt Nam tăng cường hợp tác với các tập đoàn dược phẩm đa quốc gia để chuyển giao công nghệ.', 0.89),
(71, 'Long Châu và Pharmacity mở rộng dịch vụ tư vấn sức khỏe trực tuyến và giao thuốc tận nhà (home delivery).', 0.84),
(71, 'Giá thuốc nhập khẩu có sự biến động lớn do tỷ giá hối đoái và chính sách thương mại của các nước sản xuất.', 0.93),
(71, 'Các công ty dược phẩm tăng cường kiểm soát chất lượng nguyên liệu đầu vào và quy trình sản xuất thuốc.', 0.92),
