INSERT INTO topic_documents (topic_id, text, weight) VALUES
(80, 'Việt Nam đặt mục tiêu đón 15 triệu lượt khách quốc tế trong năm nay, tập trung vào thị trường khách du lịch cao cấp.', 0.95),
(80, 'Các công ty lữ hành tung ra hàng loạt tour du lịch hè giá rẻ và các gói combo nghỉ dưỡng trọn gói để kích cầu du lịch nội địa.', 0.92),
(80, 'Ngành du lịch Việt Nam chứng kiến sự phục hồi mạnh mẽ sau đại dịch, với lượng khách và doanh thu tăng trưởng ấn tượng.', 0.90),
(80, 'Chính phủ ban hành các chính sách mới nhằm đơn giản hóa thủ tục xuất nhập cảnh và tăng cường quảng bá du lịch quốc gia.', 0.88),
(80, 'Các hãng vận tải hành khách đường bộ và đường sắt nâng cấp chất lượng dịch vụ, tăng cường số lượng chuyến trong mùa cao điểm.', 0.87),
(80, 'Xu hướng du lịch bền vững và du lịch trải nghiệm văn hóa đang được các công ty lữ hành khai thác mạnh mẽ.', 0.89),
(80, 'Thị trường vận tải hàng không nội địa cạnh tranh gay gắt về giá vé giữa các hãng hàng không giá rẻ và truyền thống.', 0.93),
(80, 'Các địa phương đầu tư vào cơ sở hạ tầng du lịch, phát triển các khu du lịch sinh thái và nghỉ dưỡng ven biển.', 0.86),
(80, 'Vấn đề thiếu hụt nguồn nhân lực chất lượng cao trong ngành du lịch và lữ hành sau đại dịch.', 0.91),
(80, 'Công nghệ du lịch (Travel Tech) được áp dụng rộng rãi trong việc đặt tour, đặt phòng và quản lý hành khách.', 0.85),
(80, 'Các công ty lữ hành quốc tế tăng cường đưa khách châu Âu và Bắc Mỹ đến Việt Nam thông qua các tuyến bay thẳng mới.', 0.94),
(80, 'Chính sách mở cửa biên giới và nới lỏng quy định nhập cảnh đã thúc đẩy mạnh mẽ sự phát triển của dịch vụ lữ hành.', 0.84),
(80, 'Nhu cầu về các tour du lịch nghỉ dưỡng dài ngày và du lịch chữa lành (wellness tourism) tăng cao.', 0.83),
(80, 'Việc liên kết giữa các doanh nghiệp lữ hành, khách sạn và hàng không để tạo ra các sản phẩm du lịch hấp dẫn.', 0.92),
(80, 'Thị trường du lịch MICE (Meetings, Incentives, Conferences, and Exhibitions) có sự phục hồi và tăng trưởng mạnh mẽ.', 0.79);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(81, 'Chính phủ nới lỏng chính sách visa điện tử cho khách du lịch, kéo dài thời gian lưu trú lên đến 90 ngày cho một số quốc gia.', 0.95),
(81, 'Các quốc gia châu Âu siết chặt quy định cấp visa Schengen, yêu cầu chứng minh tài chính và lịch trình du lịch rõ ràng hơn.', 0.94),
(81, 'Việt Nam và Hàn Quốc ký kết thỏa thuận miễn thị thực mới cho công dân mang hộ chiếu công vụ, tạo điều kiện thuận lợi cho giao thương.', 0.92),
(81, 'Quy định cấp visa lao động (work visa) cho chuyên gia nước ngoài được đơn giản hóa để thu hút nguồn nhân lực chất lượng cao.', 0.90),
(81, 'Chính sách miễn thị thực đơn phương cho công dân một số nước Tây Âu được kéo dài thời hạn, thúc đẩy du lịch châu Âu đến Việt Nam.', 0.88),
(81, 'Các cơ quan ngoại giao tăng cường xử lý hồ sơ xin visa du lịch, giảm thời gian chờ đợi và nâng cao chất lượng dịch vụ.', 0.91),
(81, 'Thị thực điện tử (e-Visa) của Việt Nam được áp dụng cho công dân của gần 100 quốc gia và vùng lãnh thổ.', 0.87),
(81, 'Cảnh báo về các dịch vụ làm visa giả, không uy tín, người dân cần cẩn trọng khi nộp hồ sơ qua các trung gian không chính thức.', 0.93),
(81, 'Nghiên cứu về tác động của chính sách nới lỏng visa đối với doanh thu và sự phát triển của ngành du lịch Việt Nam.', 0.86),
(81, 'Các nước Đông Nam Á hợp tác nghiên cứu khả năng áp dụng "Visa chung" để thu hút khách du lịch quốc tế đến toàn khu vực.', 0.89),
(81, 'Quy định mới về thị thực cho người nước ngoài đầu tư và kinh doanh tại Việt Nam được ban hành, tạo hành lang pháp lý rõ ràng.', 0.84),
(81, 'Các nước Bắc Mỹ tăng cường kiểm tra hồ sơ xin thị thực đối với du học sinh và người lao động nhập cư.', 0.81),
(81, 'Chính sách miễn thị thực 5 năm cho người gốc Việt Nam và thân nhân được triển khai rộng rãi, thu hút kiều bào về nước.', 0.90),
(81, 'Bộ Ngoại giao cung cấp hướng dẫn chi tiết về các loại thị thực, thời hạn và điều kiện cấp visa cho người nước ngoài.', 0.88),
(81, 'Thủ tục xin cấp lại thị thực du lịch cho khách quốc tế bị chậm trễ do lượng hồ sơ tăng đột biến.', 0.85),
(81, 'Các quốc gia khác (Nhật Bản, Úc) mở rộng chính sách visa làm việc kỳ nghỉ (Working Holiday Visa) cho công dân Việt Nam.', 0.87),
(81, 'Phân tích sự khác biệt giữa visa du lịch, visa công tác và visa thăm thân, tránh nhầm lẫn khi nộp hồ sơ.', 0.79),
(81, 'Việc áp dụng công nghệ sinh trắc học và phỏng vấn trực tuyến trong quy trình xét duyệt visa tại các đại sứ quán.', 0.83),
(81, 'Các công ty lữ hành cung cấp dịch vụ tư vấn và hỗ trợ làm visa cho khách đoàn du lịch nước ngoài.', 0.82),
(81, 'Quy định về thời gian xử lý hồ sơ visa khẩn cấp và các khoản phí liên quan được công bố rõ ràng.', 0.78),
(81, 'Chính sách miễn visa song phương giữa Việt Nam và các nước ASEAN tiếp tục được duy trì ổn định.', 0.91),
(81, 'Các nước Trung Đông đầu tư vào chương trình thị thực vàng (Golden Visa) để thu hút nhà đầu tư giàu có.', 0.90),
(81, 'Nhiều người Việt Nam quan tâm đến thị thực định cư diện tay nghề tại các quốc gia phát triển.', 0.86),
(81, 'Chính phủ Việt Nam cân nhắc mở rộng danh sách các quốc gia được miễn thị thực đơn phương.', 0.89),
(81, 'Các trung tâm tiếp nhận hồ sơ thị thực (VFS Global, TLS Contact) tăng cường dịch vụ tại các thành phố lớn.', 0.84),
(81, 'Việc sử dụng hộ chiếu điện tử (e-passport) và ảnh visa theo tiêu chuẩn ICAO được khuyến khích.', 0.81),
(81, 'Thị thực điện tử của Việt Nam được đánh giá là một trong những dịch vụ visa tiện lợi nhất khu vực.', 0.92),
(81, 'Các quy định về thời gian tối thiểu còn hiệu lực của hộ chiếu khi xin cấp thị thực.', 0.76),
(81, 'Bộ Ngoại giao cảnh báo về việc lợi dụng visa du lịch để nhập cảnh bất hợp pháp vào nước thứ ba.', 0.95),
(81, 'Chính sách miễn thị thực cho các thuyền viên nước ngoài làm việc trên tàu du lịch quốc tế.', 0.83);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(82, 'Vietnam Airlines công bố kế hoạch tái cơ cấu đội bay, thay thế các máy bay cũ bằng các dòng máy bay thân rộng hiện đại hơn.', 0.95),
(82, 'Vietjet Air mở thêm đường bay thẳng đến Ấn Độ và Úc, tăng cường mạng lưới bay quốc tế và thu hút khách du lịch.', 0.92),
(82, 'Các hãng hàng không quốc tế tăng cường tần suất bay đến Việt Nam, cạnh tranh gay gắt về giá vé và dịch vụ.', 0.91),
(82, 'Bamboo Airways tập trung vào các đường bay nội địa và các đường bay ngách quốc tế, tìm kiếm phân khúc thị trường riêng.', 0.88),
(82, 'Giá vé máy bay nội địa tăng cao trong mùa hè do nhu cầu du lịch bùng nổ và giá nhiên liệu bay tăng.', 0.93),
(82, 'Vietnam Airlines và Vietjet Air cạnh tranh về dịch vụ trên không, với các gói dịch vụ cao cấp và các chương trình khuyến mãi.', 0.87),
(82, 'Các hãng hàng không áp dụng công nghệ làm thủ tục trực tuyến (online check-in) và sử dụng thẻ lên máy bay điện tử (boarding pass).', 0.86),
(82, 'Cảnh báo về tình trạng chuyến bay bị hoãn, hủy liên tục do thời tiết xấu và sự cố kỹ thuật của máy bay.', 0.94),
(82, 'Nghiên cứu về việc sử dụng nhiên liệu hàng không bền vững (SAF) để giảm lượng khí thải carbon của các hãng hàng không.', 0.89),
(82, 'Các hãng hàng không giá rẻ (LCC) mở rộng dịch vụ bán thêm (ancillary revenue) như hành lý ký gửi và chọn chỗ ngồi.', 0.85),
(82, 'Chính phủ Việt Nam hỗ trợ các hãng hàng không trong nước về chính sách thuế và vốn vay để vượt qua khó khăn tài chính.', 0.90),
(82, 'Vietnam Airlines tăng cường hợp tác với các hãng hàng không trong liên minh SkyTeam để mở rộng mạng lưới bay toàn cầu.', 0.84),
(82, 'Các hãng hàng không quốc tế như Emirates và Qatar Airways giới thiệu các dịch vụ hạng nhất (First Class) và thương gia (Business Class) sang trọng.', 0.81),
(82, 'Vietjet Air ký kết hợp đồng mua thêm máy bay mới từ Airbus/Boeing để mở rộng đội bay và tăng khả năng vận tải.', 0.88),
(82, 'Các hãng hàng không nội địa đối mặt với thách thức về thiếu hụt phi công và nhân viên kỹ thuật trình độ cao.', 0.92),
(82, 'Thị trường vận tải hàng hóa hàng không (Air Cargo) có sự tăng trưởng mạnh mẽ, đóng góp vào doanh thu của các hãng.', 0.85),
(82, 'Các hãng hàng không áp dụng chính sách hoàn hủy vé linh hoạt, tạo điều kiện cho hành khách thay đổi kế hoạch bay.', 0.83),
(82, 'Việc sử dụng máy bay thân rộng (wide-body aircraft) trên các tuyến bay nội địa trong mùa cao điểm để tăng tải khách.', 0.79),
(82, 'Các hãng hàng không lớn đầu tư vào ứng dụng di động, cho phép hành khách quản lý chuyến bay và nhận thông báo theo thời gian thực.', 0.86),
(82, 'Chính sách giá vé máy bay được điều chỉnh theo khung giờ và ngày bay, tạo sự khác biệt về giá giữa các chuyến bay.', 0.77),
(82, 'Các hãng hàng không tăng cường biện pháp an ninh, kiểm soát chặt chẽ hành lý và hành khách trước khi lên máy bay.', 0.94),
(82, 'Vietnam Airlines được vinh danh là hãng hàng không 4 sao quốc tế, nâng cao uy tín của hàng không Việt Nam.', 0.91),
(82, 'Các hãng hàng không giới thiệu các gói ưu đãi cho khách hàng thường xuyên (Loyalty Programs) và tích lũy dặm bay.', 0.84),
(82, 'Sự khác biệt trong chính sách hành lý ký gửi và hành lý xách tay giữa các hãng hàng không giá rẻ và truyền thống.', 0.82),
(82, 'Các hãng hàng không thuê ngoài dịch vụ bảo dưỡng và sửa chữa máy bay để tiết kiệm chi phí vận hành.', 0.76),
(82, 'Vietjet Air đẩy mạnh chiến lược quảng bá thương hiệu thông qua các hoạt động giải trí và sự kiện văn hóa.', 0.88),
(82, 'Nghiên cứu về việc sử dụng công nghệ AI để dự báo nhu cầu bay và tối ưu hóa lịch trình chuyến bay.', 0.89),
(82, 'Các hãng hàng không đối mặt với vấn đề bồi thường cho hành khách khi chuyến bay bị chậm hoặc hủy chuyến.', 0.93),
(82, 'Giá nhiên liệu bay (Jet Fuel) tăng cao là thách thức lớn nhất đối với lợi nhuận của các hãng hàng không.', 0.95),
(82, 'Các hãng hàng không hợp tác với các công ty du lịch để cung cấp các gói tour du lịch trọn gói hấp dẫn.', 0.80);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(83, 'Dự án sân bay Long Thành đạt tiến độ xây dựng giai đoạn 1, cam kết hoàn thành đường băng và nhà ga đúng thời hạn.', 0.95),
(83, 'Máy bay Airbus A350 và Boeing 787 được đưa vào khai thác thương mại rộng rãi, nâng cao chất lượng dịch vụ bay đường dài.', 0.92),
(83, 'Các giải pháp tăng cường an ninh tại khu vực kiểm soát sân bay, áp dụng công nghệ quét hành lý và nhận diện khuôn mặt.', 0.94),
(83, 'Sân bay Tân Sơn Nhất và Nội Bài tăng cường năng lực khai thác, mở thêm nhà ga hành khách T3 và các khu vực đỗ máy bay mới.', 0.91),
(83, 'Nghiên cứu về việc sử dụng máy bay không người lái (drone) để kiểm tra đường băng và cơ sở hạ tầng sân bay.', 0.88),
(83, 'Các sân bay áp dụng công nghệ quản lý hành lý tự động (automated baggage handling) để giảm thời gian chờ đợi.', 0.87),
(83, 'Chính phủ phê duyệt kế hoạch nâng cấp và mở rộng các sân bay địa phương, phục vụ phát triển du lịch và kinh tế vùng.', 0.90),
(83, 'Công nghệ máy bay mới tập trung vào việc giảm tiếng ồn, tiết kiệm nhiên liệu và giảm phát thải carbon.', 0.89),
(83, 'Cảnh báo về tình trạng ùn tắc tại khu vực làm thủ tục và kiểm tra an ninh tại các sân bay lớn trong mùa cao điểm.', 0.93),
(83, 'Các sân bay đầu tư vào hệ thống dẫn đường và kiểm soát không lưu (ATC) hiện đại để đảm bảo an toàn bay.', 0.85),
(83, 'Máy bay thân rộng (Boeing 777, Airbus A380) được các hãng hàng không quốc tế tăng cường sử dụng trên các chặng bay dài.', 0.86),
(83, 'Các quy định mới về an toàn hàng không, yêu cầu kiểm tra định kỳ và bảo dưỡng nghiêm ngặt các bộ phận máy bay.', 0.92),
(83, 'Sân bay Long Thành được thiết kế theo mô hình sân bay sinh thái, sử dụng năng lượng mặt trời và vật liệu thân thiện môi trường.', 0.84),
(83, 'Việc sử dụng cửa lên máy bay tự động (self-boarding gates) bằng công nghệ nhận diện sinh trắc học.', 0.81),
(83, 'Các hãng sản xuất máy bay (Boeing, Airbus) cạnh tranh về việc phát triển máy bay chở hàng chuyên dụng (cargo aircraft).', 0.88),
(83, 'An ninh hàng không tăng cường kiểm soát các vật phẩm nguy hiểm và chất lỏng trong hành lý xách tay.', 0.95),
(83, 'Sân bay Vân Đồn và Cam Ranh mở rộng đường bay thẳng quốc tế, thu hút khách du lịch từ Đông Bắc Á và Nga.', 0.83),
(83, 'Các công nghệ khử trùng và lọc không khí trên máy bay được cải tiến để đảm bảo sức khỏe hành khách.', 0.79),
(83, 'Chính sách đầu tư và huy động vốn cho các dự án xây dựng sân bay mới và nâng cấp cơ sở hạ tầng hàng không.', 0.90),
(83, 'Các sân bay áp dụng hệ thống giao thông thông minh để quản lý phương tiện ra vào và khu vực đỗ xe.', 0.80),
(83, 'Máy bay thế hệ mới được trang bị hệ thống giải trí trên chuyến bay (IFE) và kết nối Wi-Fi tốc độ cao.', 0.87),
(83, 'Các quy định về giới hạn tiếng ồn và ô nhiễm không khí tại các khu vực lân cận sân bay.', 0.91),
(83, 'Nghiên cứu về việc sử dụng vật liệu composite nhẹ và bền hơn trong cấu tạo thân máy bay.', 0.86),
(83, 'Sân bay Côn Đảo và Phú Quốc được nâng cấp để tiếp nhận các dòng máy bay lớn hơn, phục vụ du lịch biển đảo.', 0.78),
(83, 'Công nghệ kiểm soát không lưu được chuyển đổi từ radar sang hệ thống giám sát tự động ADS-B.', 0.82),
(83, 'Các giải pháp chống chim và vật thể lạ (FOD) trên đường băng được các sân bay áp dụng thường xuyên.', 0.94),
(83, 'Máy bay tư nhân và máy bay phản lực cỡ nhỏ có sự tăng trưởng về nhu cầu sử dụng tại Việt Nam.', 0.85),
(83, 'Các sân bay Việt Nam được đánh giá cao về chất lượng dịch vụ và cơ sở hạ tầng trong khu vực ASEAN.', 0.83),
(83, 'Bộ Giao thông Vận tải tăng cường kiểm tra an toàn kỹ thuật đối với các loại máy bay đang khai thác.', 0.90),
(83, 'Các hãng hàng không giới thiệu các loại ghế ngồi thông minh và tiện nghi hơn trên máy bay thân rộng.', 0.77);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(84, 'Việt Nam tăng cường đào tạo nguồn nhân lực phi công trẻ, hợp tác với các trường bay quốc tế để đáp ứng nhu cầu mở rộng đội bay.', 0.95),
(84, 'Các quy định mới về giờ bay và nghỉ ngơi của phi công được siết chặt để đảm bảo an toàn bay và sức khỏe nghề nghiệp.', 0.94),
(84, 'Thông tin về các dòng chuyên cơ hạng sang (Gulfstream G650, Bombardier Global 7500) được giới siêu giàu ưa chuộng cho các chuyến bay cá nhân.', 0.92),
(84, 'Các hãng hàng không triển khai chính sách thu hút và giữ chân phi công giỏi bằng chế độ đãi ngộ và lương thưởng hấp dẫn.', 0.88),
(84, 'Thị trường thuê và mua bán máy bay cá nhân (private jet) có sự tăng trưởng tại Việt Nam, phục vụ giới doanh nhân và người nổi tiếng.', 0.90),
(84, 'Các vấn đề liên quan đến sức khỏe tinh thần và thể chất của phi công, tiếp viên sau các chuyến bay dài và căng thẳng.', 0.87),
(84, 'Yêu cầu về bằng cấp, kinh nghiệm và các bài kiểm tra định kỳ nghiêm ngặt đối với phi công và cơ phó.', 0.91),
(84, 'Sự khác biệt về điều kiện làm việc và chế độ lương thưởng giữa phi công người Việt và phi công nước ngoài.', 0.86),
(84, 'Các hãng hàng không tổ chức các khóa huấn luyện kỹ năng mềm và xử lý tình huống khẩn cấp cho đội ngũ tiếp viên hàng không.', 0.89),
(84, 'Cảnh báo về các trường hợp phi công vi phạm quy định an toàn bay và bị đình chỉ bay, gây ảnh hưởng đến uy tín hãng.', 0.93),
(84, 'Các công ty cung cấp dịch vụ quản lý và vận hành chuyên cơ, đảm bảo các tiêu chuẩn an toàn và bảo mật cao nhất.', 0.85),
(84, 'Nghiên cứu về việc sử dụng công nghệ mô phỏng (simulator) trong đào tạo phi công, tăng cường kỹ năng thực hành.', 0.84),
(84, 'Các hãng hàng không tổ chức cuộc thi tìm kiếm gương mặt đại diện là các tiếp viên hàng không có hình ảnh đẹp.', 0.81),
(84, 'Giá thuê chuyên cơ cho các chuyến bay công tác ngắn ngày có xu hướng tăng do nhu cầu di chuyển nhanh chóng của doanh nhân.', 0.88),
(84, 'Các phi công Việt Nam tham gia vào các khóa huấn luyện bay quốc tế để nâng cao trình độ và khả năng làm việc đa quốc gia.', 0.92),
(84, 'Các quy định về đồng phục, tác phong và thái độ phục vụ của đội ngũ tiếp viên hàng không trên các chuyến bay.', 0.83),
(84, 'Nhu cầu về phi công lái máy bay trực thăng và máy bay vận tải chuyên dụng có sự tăng trưởng ổn định.', 0.79),
(84, 'Các chuyên cơ được trang bị nội thất sang trọng, phòng ngủ, phòng họp và hệ thống giải trí hiện đại.', 0.86),
(84, 'Việc sử dụng công nghệ AI để hỗ trợ phi công trong việc ra quyết định và dự báo thời tiết xấu.', 0.80),
(84, 'Các hãng hàng không tổ chức các chương trình đào tạo chuyển loại (type rating) cho phi công chuyển đổi dòng máy bay.', 0.77),
(84, 'Lực lượng tiếp viên hàng không đối mặt với rủi ro về sức khỏe do thay đổi múi giờ và môi trường làm việc trên cao.', 0.91),
(84, 'Các quy định về bảo hiểm nghề nghiệp và bảo hiểm y tế cho phi công và tiếp viên hàng không.', 0.84),
(84, 'Chuyên cơ được sử dụng cho mục đích vận chuyển y tế khẩn cấp và các nhiệm vụ nhân đạo.', 0.89),
(84, 'Các hãng hàng không tổ chức các sự kiện vinh danh và tri ân đội ngũ phi công, tiếp viên có thành tích xuất sắc.', 0.76),
(84, 'Giá mua bán máy bay cá nhân đã qua sử dụng (second-hand) có sự biến động lớn trên thị trường quốc tế.', 0.82),
(84, 'Các phi công được khuyến khích tham gia vào các hoạt động tình nguyện và cộng đồng của hãng hàng không.', 0.78),
(84, 'Nghiên cứu về tiêu chuẩn thể lực và tâm lý khắt khe đối với các ứng viên phi công trẻ.', 0.93),
(84, 'Các hãng hàng không cung cấp các khóa đào tạo ngoại ngữ chuyên ngành cho đội ngũ phi hành đoàn.', 0.85),
(84, 'Việc sử dụng chuyên cơ cho các chuyến bay ngoại giao và vận chuyển lãnh đạo cấp cao của quốc gia.', 0.90),
(84, 'Các hãng hàng không đối mặt với vấn đề bồi thường cho tiếp viên khi xảy ra tai nạn hoặc sự cố trên chuyến bay.', 0.94);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(86, 'Các trường đại học công lập đồng loạt tăng học phí theo lộ trình tự chủ tài chính đã được Chính phủ phê duyệt.', 0.95),
(86, 'Chính sách miễn giảm học phí cho học sinh vùng khó khăn và học sinh thuộc diện hộ nghèo, hộ cận nghèo được triển khai đầy đủ.', 0.92),
(86, 'Ngân hàng Chính sách Xã hội triển khai gói vay tín dụng ưu đãi cho sinh viên, hỗ trợ chi phí học tập và sinh hoạt.', 0.91),
(86, 'Học phí tại các trường đại học tư thục tăng mạnh, tập trung đầu tư vào cơ sở vật chất và chất lượng giảng dạy quốc tế.', 0.88),
(86, 'Phụ huynh lo lắng về việc học phí tăng cao gây áp lực lên tài chính gia đình, đặc biệt là gia đình có nhiều con đi học.', 0.93),
(86, 'Các trường mầm non và phổ thông tư thục công bố mức học phí cả năm, có sự khác biệt lớn giữa các khu vực.', 0.86),
(86, 'Chính sách học bổng và hỗ trợ tài chính được các trường đại học công bố để thu hút học sinh giỏi và hỗ trợ sinh viên nghèo.', 0.89),
(86, 'Bộ Giáo dục và Đào tạo kiểm soát chặt chẽ việc tăng học phí của các trường, đảm bảo đúng quy định và lộ trình.', 0.90),
(86, 'Nhiều trường đại học công bố các mức học phí khác nhau cho các ngành đào tạo đại trà và ngành chất lượng cao.', 0.85),
(86, 'Học sinh, sinh viên được vay vốn ưu đãi để mua máy tính xách tay và thiết bị học tập trong thời đại công nghệ.', 0.84),
(86, 'Các trường quốc tế công bố mức học phí cao, đi kèm với các chương trình học theo tiêu chuẩn quốc tế và giáo viên nước ngoài.', 0.92),
(86, 'Nghiên cứu về việc sử dụng học phí để đầu tư vào cơ sở hạ tầng, phòng thí nghiệm và thư viện của các trường đại học.', 0.87),
(86, 'Các trường đại học cam kết không tăng học phí đột ngột, đảm bảo ổn định tài chính cho sinh viên trong suốt khóa học.', 0.83),
(86, 'Việc hoàn trả học phí cho sinh viên nghỉ học giữa chừng được quy định rõ ràng trong quy chế đào tạo.', 0.81),
(86, 'Chính sách miễn giảm học phí cho sinh viên học các ngành đặc thù, khó tuyển sinh nhưng xã hội có nhu cầu cao.', 0.88),
(86, 'Các trường trung cấp và cao đẳng nghề giữ ổn định mức học phí để thu hút học sinh, sinh viên theo học nghề.', 0.79),
(86, 'Phụ huynh kiến nghị về việc minh bạch hóa các khoản thu ngoài học phí tại các trường phổ thông công lập.', 0.94),
(86, 'Chính sách tín dụng sinh viên được mở rộng đối tượng và nâng cao hạn mức vay để hỗ trợ tốt hơn cho người học.', 0.86),
(86, 'Các trường đại học top đầu công bố mức học phí cao nhất, đi kèm với cam kết về chất lượng đầu ra và cơ hội việc làm.', 0.91),
(86, 'Học phí của các chương trình liên kết quốc tế tại Việt Nam có sự chênh lệch lớn giữa các trường.', 0.82),
(86, 'Các trường đại học áp dụng các phương thức thu học phí đa dạng (trực tuyến, qua ngân hàng, qua ví điện tử).', 0.80),
(86, 'Nghiên cứu về mối quan hệ giữa mức học phí và chất lượng đào tạo đại học tại Việt Nam.', 0.87),
(86, 'Chính sách miễn học phí cho các học sinh, sinh viên là con liệt sĩ, thương binh và người có công với cách mạng.', 0.85),
(86, 'Các trường đại học công bố chi tiết về nguồn thu, chi từ học phí để sinh viên và phụ huynh theo dõi.', 0.78),
(86, 'Học phí chương trình đào tạo sau đại học (Thạc sĩ, Tiến sĩ) có sự tăng nhẹ so với các năm trước.', 0.89),
(86, 'Các quỹ học bổng doanh nghiệp được thành lập để hỗ trợ sinh viên có hoàn cảnh khó khăn và có thành tích tốt.', 0.83),
(86, 'Phụ huynh tìm hiểu các phương thức tiết kiệm và lập kế hoạch tài chính cho con cái học đại học.', 0.76),
(86, 'Các trường đại học tư thục tăng cường các dịch vụ hỗ trợ sinh viên tìm việc làm để giảm gánh nặng tài chính.', 0.88),
(86, 'Chính sách hỗ trợ học phí cho giáo viên mầm non và giáo viên tiểu học tại các vùng khó khăn.', 0.90),
(86, 'Việc áp dụng học phí theo tín chỉ tại các trường đại học, cho phép sinh viên chủ động về chi phí học tập.', 0.81);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(87, 'Tình trạng thiếu giáo viên nghiêm trọng tại các trường học vùng cao, đặc biệt là giáo viên bộ môn tiếng Anh và Tin học.', 0.95),
(87, 'Chương trình "Mang sách đến vùng cao" được các tổ chức tình nguyện tổ chức, cung cấp sách vở và đồ dùng học tập cho học sinh.', 0.92),
(87, 'Xây dựng trường nội trú dân nuôi cho học sinh dân tộc thiểu số, đảm bảo các em được ăn ở và học tập ổn định.', 0.91),
(87, 'Các thách thức trong việc giảng dạy tại vùng cao: cơ sở vật chất thiếu thốn, đường sá đi lại khó khăn và rào cản ngôn ngữ.', 0.94),
(87, 'Bộ Giáo dục và Đào tạo triển khai các chính sách ưu đãi cho giáo viên công tác tại vùng sâu, vùng xa, thu hút giáo viên trẻ tình nguyện.', 0.88),
(87, 'Tình trạng học sinh bỏ học giữa chừng tại vùng cao có xu hướng giảm nhờ các chương trình hỗ trợ và tuyên truyền của địa phương.', 0.87),
(87, 'Các trường học vùng cao được đầu tư xây dựng phòng học kiên cố, thay thế các lớp học tạm bợ và tranh tre.', 0.89),
(87, 'Chương trình xóa mù chữ và phổ cập giáo dục cho người lớn tại các xã vùng cao và dân tộc thiểu số.', 0.86),
(87, 'Nhu cầu về giáo viên dạy tiếng dân tộc thiểu số để hỗ trợ học sinh tiếp cận kiến thức trong giai đoạn đầu.', 0.90),
(87, 'Các trường học vùng cao được trang bị thêm máy tính và thiết bị kết nối Internet để tiếp cận giáo dục số.', 0.85),
(87, 'Các tổ chức phi chính phủ hỗ trợ xây dựng nhà bán trú và bếp ăn cho học sinh vùng cao, đảm bảo dinh dưỡng.', 0.92),
(87, 'Nghiên cứu về việc sử dụng phương pháp giảng dạy linh hoạt, phù hợp với văn hóa và điều kiện sống của học sinh vùng cao.', 0.84),
(87, 'Chính sách hỗ trợ gạo, sách giáo khoa và học bổng cho học sinh dân tộc thiểu số có thành tích học tập tốt.', 0.81),
(87, 'Các sinh viên sư phạm được khuyến khích về công tác tại các trường học vùng cao sau khi tốt nghiệp.', 0.88),
(87, 'Tình trạng thiếu nước sạch và vệ sinh môi trường tại các điểm trường lẻ ở vùng núi xa xôi.', 0.93),
(87, 'Các trường vùng cao tăng cường tổ chức các hoạt động ngoại khóa, giúp học sinh phát triển kỹ năng sống và văn hóa.', 0.80),
(87, 'Việc đào tạo và bồi dưỡng giáo viên vùng cao về các phương pháp giáo dục hiện đại và kỹ năng công nghệ.', 0.86),
(87, 'Chính sách luân chuyển giáo viên giữa các vùng miền để nâng cao chất lượng giáo dục vùng khó khăn.', 0.87),
(87, 'Phụ huynh học sinh vùng cao tích cực tham gia vào việc xây dựng và sửa chữa cơ sở vật chất cho trường học.', 0.79),
(87, 'Các dự án xây dựng cầu và đường đến các điểm trường, giúp học sinh đi học dễ dàng và an toàn hơn.', 0.85),
(87, 'Nhu cầu về các thiết bị dạy học tối thiểu (bảng, bàn ghế, đồ dùng thí nghiệm) tại các trường vùng cao.', 0.77),
(87, 'Các chương trình giáo dục đặc biệt cho trẻ em khuyết tật và trẻ em có hoàn cảnh đặc biệt tại vùng núi.', 0.89),
(87, 'Giáo viên vùng cao đối mặt với áp lực về công việc và cuộc sống cá nhân do điều kiện sinh hoạt khó khăn.', 0.91),
(87, 'Chính sách hỗ trợ về nhà ở và tiền lương cho giáo viên công tác lâu năm tại các huyện nghèo.', 0.90),
(87, 'Các trường đại học mở các khóa đào tạo từ xa cho giáo viên vùng cao, nâng cao trình độ chuyên môn.', 0.82),
(87, 'Nghiên cứu về ảnh hưởng của ngôn ngữ mẹ đẻ và ngôn ngữ phổ thông đến kết quả học tập của học sinh dân tộc thiểu số.', 0.86),
(87, 'Các tổ chức tôn giáo và cộng đồng tham gia vào việc hỗ trợ giáo dục và đời sống của học sinh vùng cao.', 0.76),
(87, 'Chính sách miễn giảm thuế và hỗ trợ vật liệu xây dựng cho các dự án trường học tại vùng núi.', 0.83),
(87, 'Việc áp dụng mô hình trường học gắn với cuộc sống và sản xuất tại các vùng có điều kiện kinh tế khó khăn.', 0.88),
(87, 'Các giải pháp chống thất học cho học sinh vùng cao sau mỗi mùa thiên tai và dịch bệnh.', 0.94);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(88, 'Đội tuyển Việt Nam giành huy chương Vàng Olympic Toán học Quốc tế (IMO), tiếp tục khẳng định vị thế trên đấu trường học thuật.', 0.95),
(88, 'Công tác tuyển chọn và bồi dưỡng học sinh giỏi tham dự Olympic được thực hiện chuyên sâu, tập trung vào các môn khoa học cơ bản.', 0.92),
(88, 'Kỳ thi Olympic Tin học sinh viên toàn quốc thu hút hàng nghìn thí sinh tham gia, tập trung vào kỹ năng lập trình và thuật toán.', 0.91),
(88, 'Các học sinh đạt giải cao tại kỳ thi Olympic Quốc tế được tuyển thẳng vào các trường đại học hàng đầu trong nước và quốc tế.', 0.88),
(88, 'Nghiên cứu về các phương pháp giảng dạy và tài liệu bồi dưỡng học sinh giỏi Olympic Khoa học Tự nhiên.', 0.87),
(88, 'Thành tích của đội tuyển Olympic Hóa học và Vật lý Quốc tế đạt mức cao kỷ lục, với nhiều huy chương Vàng và Bạc.', 0.93),
(88, 'Các trường chuyên, lớp chọn đầu tư vào cơ sở vật chất và đội ngũ giáo viên chuyên trách để bồi dưỡng học sinh Olympic.', 0.86),
(88, 'Các kỳ thi Olympic cấp khu vực và quốc gia được tổ chức để phát hiện và nuôi dưỡng các tài năng trẻ trong lĩnh vực khoa học.', 0.85),
(88, 'Cộng đồng học sinh, sinh viên quan tâm đến các bài toán và thử thách trong kỳ thi Olympic Toán học không chuyên.', 0.84),
(88, 'Chính sách khen thưởng và đãi ngộ đặc biệt đối với học sinh và giáo viên có thành tích cao tại các kỳ thi Olympic.', 0.90),
(88, 'Việc sử dụng các bài thi Olympic Quốc tế làm tài liệu tham khảo cho việc đổi mới phương pháp giảng dạy trong nước.', 0.89),
(88, 'Các trường đại học quốc tế cung cấp học bổng toàn phần cho các thí sinh đạt giải cao tại các kỳ thi Olympic.', 0.92),
(88, 'Quá trình chuẩn bị của đội tuyển Olympic Việt Nam cho kỳ thi Olympic Hóa học Quốc tế, tập trung vào thực hành thí nghiệm.', 0.83),
(88, 'Các vấn đề về áp lực tâm lý và sức khỏe đối với học sinh tham dự các kỳ thi Olympic Quốc tế kéo dài.', 0.94),
(88, 'Kỳ thi Olympic Tiếng Anh và Olympic Khoa học xã hội cũng được quan tâm và tổ chức ở cấp độ quốc gia.', 0.81),
(88, 'Các trường chuyên tổ chức các kỳ thi thử Olympic để đánh giá năng lực và điều chỉnh chiến lược bồi dưỡng.', 0.79),
(88, 'Các học sinh đạt giải Olympic Quốc gia được ưu tiên trong việc xét tuyển vào các ngành đào tạo trọng điểm của quốc gia.', 0.88),
(88, 'Nghiên cứu về vai trò của công nghệ thông tin và AI trong việc hỗ trợ học sinh ôn luyện cho các kỳ thi Olympic.', 0.86),
(88, 'Các thầy cô giáo có kinh nghiệm bồi dưỡng Olympic được cử đi tập huấn tại các trung tâm khoa học tiên tiến.', 0.85),
(88, 'Thành tích của đội tuyển Olympic Sinh học Quốc tế đạt được các huy chương, là động lực cho ngành y sinh học.', 0.91),
(88, 'Các chương trình hợp tác quốc tế trong việc trao đổi kinh nghiệm và tài liệu bồi dưỡng học sinh Olympic.', 0.82),
(88, 'Kỳ thi Olympic Toán học và Tin học dành cho học sinh tiểu học và trung học cơ sở được tổ chức rộng rãi.', 0.77),
(88, 'Các học sinh đạt giải Olympic được tạo điều kiện học tập tại nước ngoài và trở về phục vụ đất nước.', 0.90),
(88, 'Các hội thảo khoa học và chuyên đề được tổ chức để chia sẻ kinh nghiệm giảng dạy và bồi dưỡng Olympic.', 0.76),
(88, 'Việc áp dụng các tiêu chuẩn và yêu cầu của kỳ thi Olympic Quốc tế vào các đề thi học sinh giỏi quốc gia.', 0.83),
(88, 'Các giải pháp nhằm giảm tình trạng học sinh giỏi Olympic sau này không theo đúng chuyên ngành đã đạt giải.', 0.89),
(88, 'Các trường đại học tổ chức các khóa bồi dưỡng hè chuyên sâu cho học sinh có năng khiếu khoa học.', 0.84),
(88, 'Đội tuyển Olympic Tin học tập trung vào các thuật toán AI và Machine Learning trong các bài thi thực hành.', 0.88),
(88, 'Phân tích điểm số và thành tích của các quốc gia tham dự kỳ thi Olympic Quốc tế, so sánh với Việt Nam.', 0.93),
(88, 'Các gương mặt học sinh tiêu biểu đạt huy chương Olympic được Thủ tướng Chính phủ tặng bằng khen và phần thưởng.', 0.95);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(85, 'Bộ Giáo dục và Đào tạo công bố quy chế thi tốt nghiệp THPT mới, tập trung vào đánh giá năng lực toàn diện của học sinh.', 0.95),
(85, 'Xu hướng học trực tuyến (E-learning) tiếp tục phát triển mạnh mẽ sau đại dịch, các trường đầu tư vào nền tảng công nghệ giáo dục.', 0.92),
(85, 'Trường đại học X đạt chuẩn kiểm định chất lượng quốc tế, nâng cao vị thế và thu hút sinh viên nước ngoài.', 0.91),
(85, 'Chính phủ ban hành chính sách ưu tiên phát triển giáo dục nghề nghiệp, tập trung đào tạo nguồn nhân lực chất lượng cao.', 0.88),
(85, 'Các trường học trên cả nước tăng cường giáo dục kỹ năng sống, đạo đức và giá trị cho học sinh, sinh viên.', 0.89),
(85, 'Vấn đề thiếu giáo viên trầm trọng tại các cấp học, đặc biệt là giáo viên môn tích hợp và giáo viên phổ thông vùng khó.', 0.93),
(85, 'Các trường đại học đẩy mạnh mô hình tự chủ, tăng cường hợp tác với doanh nghiệp để nâng cao chất lượng đào tạo và nghiên cứu.', 0.87),
(85, 'Nhu cầu về giáo dục mầm non chất lượng cao tăng cao, các trường tư thục và quốc tế mở rộng quy mô hoạt động.', 0.85),
(85, 'Bộ GD&ĐT lấy ý kiến về việc điều chỉnh khung thời gian năm học, giảm áp lực học tập cho học sinh phổ thông.', 0.90),
(85, 'Các hội thảo khoa học và sự kiện học thuật quốc tế được tổ chức thường xuyên, tạo cơ hội giao lưu và hợp tác giáo dục.', 0.84),
(85, 'Việc áp dụng công nghệ trí tuệ nhân tạo (AI) trong quản lý giáo dục và cá nhân hóa trải nghiệm học tập cho sinh viên.', 0.94),
(85, 'Các trường đại học Việt Nam tăng cường công bố nghiên cứu trên các tạp chí khoa học uy tín quốc tế.', 0.86),
(85, 'Chính sách hỗ trợ học sinh, sinh viên có hoàn cảnh khó khăn và khuyến khích tài năng trẻ trong lĩnh vực khoa học.', 0.83),
(85, 'Thị trường giáo dục ngoài công lập và các trung tâm luyện thi có sự cạnh tranh gay gắt về chất lượng và chi phí.', 0.92),
(85, 'Các trường học tăng cường hoạt động giáo dục thể chất và y tế học đường, đảm bảo sức khỏe toàn diện cho học sinh.', 0.79);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(90, 'Chương trình học bổng Chính phủ Australia (AAS) cho sinh viên Việt Nam, tập trung vào các lĩnh vực ưu tiên phát triển.', 0.95),
(90, 'Quỹ khuyến học Việt Nam trao tặng 500 suất học bổng cho học sinh nghèo vượt khó, khuyến khích tinh thần hiếu học.', 0.92),
(90, 'Hướng dẫn nộp hồ sơ xin học bổng Erasmus Mundus, cung cấp cơ hội học tập tại nhiều trường đại học châu Âu.', 0.91),
(90, 'Các trường đại học Mỹ công bố các suất học bổng toàn phần và bán phần cho sinh viên quốc tế có thành tích học tập xuất sắc.', 0.88),
(90, 'Các công ty, tập đoàn lớn (VinGroup, FPT) thành lập quỹ học bổng riêng, hỗ trợ sinh viên các ngành công nghệ và kỹ thuật.', 0.89),
(90, 'Học bổng Chính phủ Hàn Quốc (KGSP) thu hút số lượng lớn sinh viên Việt Nam nộp hồ sơ, cạnh tranh gay gắt.', 0.93),
(90, 'Các tổ chức phi chính phủ hỗ trợ học bổng cho học sinh, sinh viên dân tộc thiểu số và vùng khó khăn.', 0.87),
(90, 'Sinh viên cần chuẩn bị hồ sơ xin học bổng bao gồm thư giới thiệu, bảng điểm và bài luận cá nhân ấn tượng.', 0.86),
(90, 'Học bổng sau đại học (Thạc sĩ, Tiến sĩ) tập trung vào các lĩnh vực nghiên cứu khoa học và công nghệ cao.', 0.90),
(90, 'Các trường đại học Việt Nam công bố các suất học bổng khuyến khích học tập và hỗ trợ tài năng đầu vào.', 0.85),
(90, 'Chương trình học bổng Fulbright (Mỹ) và Chevening (Anh) tiếp tục là mục tiêu của nhiều học giả Việt Nam.', 0.94),
(90, 'Các quỹ khuyến học địa phương tổ chức lễ trao học bổng cho học sinh đạt thành tích cao trong các kỳ thi.', 0.84),
(90, 'Nghiên cứu về tác động của các chương trình học bổng đối với việc phát triển nguồn nhân lực chất lượng cao.', 0.81),
(90, 'Các trường đại học quốc tế mở các khóa đào tạo kỹ năng viết luận và phỏng vấn xin học bổng cho sinh viên.', 0.88),
(90, 'Học bổng dành cho du học sinh Việt Nam tại Canada và Úc, tập trung vào các ngành STEM và kinh doanh.', 0.92),
(90, 'Chính sách học bổng dành cho con em người lao động trong ngành giáo dục và y tế.', 0.80),
(90, 'Các công ty công nghệ trao học bổng cho sinh viên xuất sắc chuyên ngành Khoa học Máy tính và Trí tuệ Nhân tạo.', 0.86),
(90, 'Điều kiện và tiêu chí xét duyệt học bổng (GPA, hoạt động ngoại khóa, kinh nghiệm nghiên cứu) được công bố minh bạch.', 0.79),
(90, 'Các tổ chức tôn giáo và cá nhân hảo tâm đóng góp vào các quỹ khuyến học, hỗ trợ giáo dục vùng xa.', 0.93),
(90, 'Học bổng toàn phần bao gồm học phí, sinh hoạt phí và vé máy bay khứ hồi cho sinh viên du học.', 0.95),
(90, 'Các trường đại học mở các cổng thông tin và dịch vụ tư vấn chuyên sâu về học bổng cho sinh viên.', 0.83),
(90, 'Nhu cầu về các suất học bổng tiếng Anh và học bổng trại hè quốc tế cho học sinh phổ thông.', 0.82),
(90, 'Các chương trình học bổng chính phủ Trung Quốc và Đài Loan thu hút nhiều sinh viên học tiếng Trung.', 0.87),
(90, 'Học bổng dành cho các nhà khoa học nữ và các lĩnh vực nghiên cứu khoa học cơ bản.', 0.90),
(90, 'Các trường đại học áp dụng mô hình học bổng theo nhu cầu tài chính (need-based scholarship) để hỗ trợ sinh viên nghèo.', 0.77),
(90, 'Phân tích sự khác biệt giữa học bổng dựa trên thành tích (merit-based) và học bổng dựa trên nhu cầu.', 0.84),
(90, 'Các quỹ khuyến học tổ chức chương trình "Tiếp sức đến trường" hỗ trợ sinh viên nhập học đại học.', 0.89),
(90, 'Chính sách học bổng cho sinh viên các trường nghề và các ngành nghề truyền thống.', 0.76),
(90, 'Các trường đại học châu Âu mở rộng học bổng cho sinh viên các nước đang phát triển.', 0.91),
(90, 'Học bổng dành cho sinh viên có đóng góp tích cực vào các hoạt động cộng đồng và xã hội.', 0.85);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(91, 'Tăng cường thời lượng môn Lịch sử trong chương trình phổ thông, đưa Lịch sử trở thành môn học bắt buộc và có trọng tâm hơn.', 0.95),
(91, 'Đổi mới phương pháp dạy và học môn Tiếng Anh, tập trung vào phát triển kỹ năng giao tiếp và nghe nói.', 0.92),
(91, 'Tranh cãi về độ khó của đề thi môn Toán trong kỳ thi quốc gia, gây áp lực lớn cho học sinh và phụ huynh.', 0.93),
(91, 'Chương trình giảng dạy môn Khoa học Tự nhiên (Lý, Hóa, Sinh) được thiết kế theo hướng tích hợp, giảm tải kiến thức hàn lâm.', 0.88),
(91, 'Môn Ngữ Văn được đổi mới, khuyến khích học sinh viết và cảm thụ văn học theo hướng cá nhân, sáng tạo.', 0.89),
(91, 'Bộ GD&ĐT đề xuất đưa môn Giáo dục Kinh tế và Pháp luật thành môn học bắt buộc ở cấp THPT.', 0.87),
(91, 'Các trường học áp dụng phương pháp dạy học STEM/STEAM, tích hợp kiến thức nhiều môn học vào các dự án thực tế.', 0.90),
(91, 'Nhu cầu về giáo viên dạy các môn học mới như Tin học và Công nghệ trong chương trình phổ thông mới tăng cao.', 0.91),
(91, 'Phụ huynh và chuyên gia giáo dục bày tỏ ý kiến về việc phân hóa học sinh quá sớm qua việc chọn môn học ở cấp THPT.', 0.94),
(91, 'Các trường học tăng cường sử dụng thiết bị thí nghiệm và thực hành cho các môn Khoa học Tự nhiên.', 0.85),
(91, 'Môn Đạo đức/Giáo dục Công dân được chú trọng, tăng cường giáo dục về trách nhiệm công dân và kỹ năng sống.', 0.84),
(91, 'Các tổ chức giáo dục quốc tế đánh giá cao việc đổi mới chương trình giảng dạy môn Toán và Khoa học của Việt Nam.', 0.88),
(91, 'Nghiên cứu về việc áp dụng các phương pháp học tập chủ động và tự học trong các môn học xã hội.', 0.81),
(91, 'Môn Ngoại ngữ thứ hai (Tiếng Trung, Tiếng Nhật, Tiếng Hàn) được đưa vào giảng dạy thí điểm tại một số trường.', 0.86),
(91, 'Các trường đại học thay đổi chương trình đào tạo, tăng cường các môn học liên ngành và thực tế.', 0.92),
(91, 'Giá trị của việc học thuộc lòng và học hiểu trong môn Lịch sử và các môn học xã hội khác.', 0.79),
(91, 'Bộ sách giáo khoa mới cung cấp nhiều tài liệu tham khảo và học liệu bổ trợ cho giáo viên và học sinh.', 0.83),
(91, 'Chương trình giảng dạy môn Thể dục được đổi mới, tập trung vào việc rèn luyện sức khỏe và thể chất toàn diện.', 0.77),
(91, 'Các trường học tổ chức các câu lạc bộ khoa học (Toán, Lý, Hóa) để nuôi dưỡng đam mê cho học sinh.', 0.89),
(91, 'Sự khác biệt trong việc dạy và học môn Ngữ Văn giữa các trường chuyên và trường phổ thông bình thường.', 0.80),
(91, 'Các trường đại học tăng cường các môn học về khởi nghiệp và đổi mới sáng tạo cho sinh viên.', 0.91),
(91, 'Việc áp dụng các bài kiểm tra đánh giá năng lực tư duy (Tư duy Toán, Tư duy Đọc hiểu) trong các môn học.', 0.85),
(91, 'Các giáo viên được tập huấn chuyên sâu về chương trình và phương pháp giảng dạy các môn học mới.', 0.90),
(91, 'Nghiên cứu về việc giảm tải chương trình học cho học sinh, tránh tình trạng học thêm và áp lực.', 0.94),
(91, 'Môn Tin học được giảng dạy từ cấp tiểu học, tập trung vào kỹ năng lập trình cơ bản và tư duy máy tính.', 0.87),
(91, 'Tranh cãi về việc lựa chọn sách giáo khoa và tài liệu giảng dạy cho các môn học tích hợp.', 0.95),
(91, 'Các trường học giới thiệu các môn học tự chọn về nghệ thuật, âm nhạc và thủ công truyền thống.', 0.76),
(91, 'Bộ GD&ĐT công bố đề thi minh họa cho các môn học trong kỳ thi tốt nghiệp THPT năm nay.', 0.82),
(91, 'Các trường học tăng cường việc sử dụng công nghệ và thiết bị thông minh trong giảng dạy các môn học.', 0.88),
(91, 'Các chuyên gia kiến nghị về việc nâng cao chất lượng giáo viên dạy các môn học cơ bản tại vùng khó khăn.', 0.84);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(92, 'Học sinh cả nước náo nức chào đón lễ khai giảng năm học mới, nhiều trường tổ chức lễ khai giảng ngắn gọn, trang trọng.', 0.95),
(92, 'Bộ trưởng Bộ GD&ĐT gửi thư chúc mừng nhân dịp khai giảng, nhấn mạnh tầm quan trọng của việc học tập và rèn luyện.', 0.93),
(92, 'Tổ chức lễ bế giảng giản dị nhưng ý nghĩa, tập trung vào việc tổng kết thành tích và tri ân thầy cô giáo.', 0.92),
(92, 'Lễ khai giảng tại các trường học vùng cao được tổ chức với sự tham gia của các cấp lãnh đạo địa phương và cộng đồng.', 0.88),
(92, 'Các trường học sáng tạo các tiết mục văn nghệ và đồng diễn thể dục trong lễ khai giảng, tạo không khí sôi động.', 0.89),
(92, 'Quy định về việc tổ chức lễ khai giảng: không bắn pháo hoa, không thả bóng bay để bảo vệ môi trường và an toàn.', 0.94),
(92, 'Các trường học tổ chức lễ chào đón học sinh đầu cấp (lớp 1, lớp 6, lớp 10) với các hoạt động truyền thống.', 0.87),
(92, 'Không khí chuẩn bị cho lễ khai giảng tại các trường học, tập trung vào việc vệ sinh trường lớp và trang trí cờ hoa.', 0.86),
(92, 'Các trường đại học tổ chức lễ khai giảng với quy mô lớn, chào đón tân sinh viên và trao học bổng đầu vào.', 0.90),
(92, 'Lễ bế giảng năm học có sự tham gia của phụ huynh, cùng chia sẻ niềm vui với thành tích học tập của con em.', 0.85),
(92, 'Các trường học tổ chức lễ tri ân và trưởng thành cho học sinh cuối cấp, đánh dấu bước ngoặt quan trọng.', 0.91),
(92, 'Các sự kiện truyền thống như Hội trại, Ngày hội Văn hóa được tổ chức xen kẽ trong năm học.', 0.84),
(92, 'Việc áp dụng công nghệ trình chiếu và âm thanh hiện đại trong lễ khai giảng và bế giảng.', 0.81),
(92, 'Các trường học tổ chức các hoạt động quyên góp, từ thiện trong lễ khai giảng để hỗ trợ học sinh có hoàn cảnh khó khăn.', 0.88),
(92, 'Lễ khai giảng trực tuyến được tổ chức trong trường hợp dịch bệnh hoặc thời tiết xấu, đảm bảo đúng thời gian quy định.', 0.93),
(92, 'Các trường học công bố kế hoạch năm học và các mục tiêu giáo dục trong lễ khai giảng.', 0.80),
(92, 'Các quy định về trang phục và tác phong của học sinh, giáo viên trong các buổi lễ trang trọng.', 0.79),
(92, 'Nghiên cứu về vai trò của các buổi lễ truyền thống đối với việc giáo dục tình yêu trường lớp cho học sinh.', 0.86),
(92, 'Các trường học tổ chức lễ tuyên dương giáo viên và học sinh đạt thành tích cao trong năm học.', 0.90),
(92, 'Các hoạt động văn hóa, thể thao được tổ chức sôi nổi trước và sau lễ khai giảng.', 0.77),
(92, 'Lễ khai giảng tại các trường quốc tế có sự khác biệt về nội dung và hình thức tổ chức.', 0.85),
(92, 'Các trường học tăng cường công tác đảm bảo an ninh, trật tự trong quá trình diễn ra lễ khai giảng.', 0.94),
(92, 'Các trường học tổ chức các buổi gặp mặt phụ huynh đầu năm học để thông báo về chương trình giảng dạy.', 0.82),
(92, 'Lễ bế giảng là dịp để học sinh thể hiện tài năng và sự sáng tạo qua các tiết mục nghệ thuật.', 0.83),
(92, 'Các trường học tổ chức lễ ra quân và lễ phát động các phong trào thi đua trong năm học mới.', 0.76),
(92, 'Không khí và cảm xúc của học sinh lớp 1 trong ngày đầu tiên đến trường dự lễ khai giảng.', 0.88),
(92, 'Các trường đại học tổ chức các hoạt động chào đón sinh viên quốc tế trong lễ khai giảng.', 0.91),
(92, 'Chính quyền địa phương tham dự lễ khai giảng để động viên và chỉ đạo công tác giáo dục.', 0.84),
(92, 'Các trường học tổ chức lễ trồng cây và các hoạt động bảo vệ môi trường trong dịp khai giảng.', 0.78),
(92, 'Các buổi lễ chia tay học sinh cuối cấp được tổ chức xúc động và ý nghĩa, ghi lại kỷ niệm học trò.', 0.92);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(93, 'Tổ chức ngày hội tư vấn tuyển sinh và hướng nghiệp tại TP.HCM, thu hút hàng chục nghìn học sinh và phụ huynh tham gia.', 0.95),
(93, 'Học sinh THPT cần chuẩn bị gì cho kỳ thi đánh giá năng lực, tập trung vào kỹ năng tư duy và giải quyết vấn đề.', 0.92),
(93, 'Các ngành nghề "hot" và tiềm năng trong 5 năm tới, tập trung vào công nghệ, AI, và y tế.', 0.91),
(93, 'Các trường đại học giới thiệu các ngành học mới, đáp ứng nhu cầu nhân lực của cuộc cách mạng công nghiệp 4.0.', 0.88),
(93, 'Tầm quan trọng của việc tư vấn hướng nghiệp sớm cho học sinh THCS, giúp các em có định hướng rõ ràng hơn.', 0.90),
(93, 'Các công ty, doanh nghiệp hợp tác với trường học tổ chức các buổi tham quan và thực tế nghề nghiệp cho học sinh.', 0.87),
(93, 'Nhu cầu nhân lực các ngành kỹ thuật, công nghệ thông tin và logistics được dự báo sẽ tăng mạnh trong tương lai.', 0.89),
(93, 'Học sinh được khuyến khích sử dụng các công cụ trắc nghiệm tính cách và năng lực để hỗ trợ chọn ngành.', 0.86),
(93, 'Các trường nghề và cao đẳng tăng cường quảng bá các ngành học có nhu cầu tuyển dụng cao và thời gian đào tạo ngắn.', 0.93),
(93, 'Chính sách hỗ trợ học sinh có năng khiếu đặc biệt trong việc chọn trường, chọn ngành phù hợp với sở thích.', 0.85),
(93, 'Các chuyên gia tư vấn hướng nghiệp phân tích về sự khác biệt giữa việc chọn ngành theo đam mê và theo nhu cầu xã hội.', 0.94),
(93, 'Các trường THPT tổ chức các buổi hội thảo chuyên đề về tâm lý học và chọn nghề cho phụ huynh học sinh.', 0.84),
(93, 'Việc sử dụng mạng xã hội và các nền tảng trực tuyến để cung cấp thông tin hướng nghiệp đa dạng và cập nhật.', 0.81),
(93, 'Học sinh THPT tham gia các lớp học thử và trải nghiệm ngành nghề tại các trường đại học và cao đẳng.', 0.88),
(93, 'Các trường đại học công bố thông tin chi tiết về chương trình đào tạo, cơ hội việc làm của từng ngành học.', 0.92),
(93, 'Nhu cầu về các ngành nghề liên quan đến du lịch, khách sạn và dịch vụ cao cấp sau đại dịch phục hồi.', 0.79),
(93, 'Các trường THPT tăng cường vai trò của giáo viên chủ nhiệm trong việc tư vấn và định hướng nghề nghiệp cho học sinh.', 0.83),
(93, 'Thị trường lao động thay đổi, đòi hỏi sinh viên cần có thêm các kỹ năng mềm và khả năng thích ứng cao.', 0.90),
(93, 'Các trường đại học giới thiệu các chương trình đào tạo liên kết quốc tế, tăng cơ hội việc làm toàn cầu.', 0.87),
(93, 'Học sinh cần tìm hiểu kỹ về học phí, điều kiện học tập và cơ hội thực tập của các trường đại học trước khi đăng ký.', 0.77),
(93, 'Phân tích về điểm chuẩn và tỷ lệ chọi của các ngành học để giúp học sinh có cái nhìn thực tế hơn.', 0.91),
(93, 'Các trường đại học tổ chức các bài kiểm tra đánh giá tư duy và năng lực để sàng lọc thí sinh đầu vào.', 0.85),
(93, 'Các chuyên gia khuyến nghị học sinh không nên chạy theo các ngành "hot" mà không phù hợp với năng lực bản thân.', 0.93),
(93, 'Các trường THPT tăng cường tổ chức các buổi tọa đàm với cựu học sinh thành đạt để truyền cảm hứng hướng nghiệp.', 0.82),
(93, 'Nhu cầu về các ngành nghề bảo vệ môi trường và phát triển bền vững có sự tăng trưởng ổn định.', 0.76),
(93, 'Việc sử dụng các công cụ công nghệ để tạo ra hồ sơ năng lực (portfolio) cá nhân cho học sinh.', 0.88),
(93, 'Các trường đại học giới thiệu các chương trình đào tạo kỹ năng lãnh đạo và quản lý cho sinh viên.', 0.84),
(93, 'Học sinh được tư vấn về các lựa chọn học tập sau tốt nghiệp THPT (Đại học, Cao đẳng, Du học).', 0.81),
(93, 'Các chuyên gia phân tích về sự khác biệt trong nhu cầu nhân lực giữa các vùng kinh tế trọng điểm.', 0.89),
(93, 'Các trường đại học tăng cường liên kết với các doanh nghiệp để đảm bảo đầu ra việc làm cho sinh viên.', 0.95);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(94, 'Các trường đại học công bố chỉ tiêu và phương thức xét tuyển sớm, bao gồm xét tuyển học bạ, kết quả thi đánh giá năng lực.', 0.95),
(94, 'Phân tích về điểm chuẩn và tỷ lệ chọi vào các ngành hot (Công nghệ thông tin, Y khoa) tăng cao kỷ lục.', 0.92),
(94, 'Quy chế xét tuyển thẳng và ưu tiên khu vực được điều chỉnh, đảm bảo công bằng và chính xác cho mọi thí sinh.', 0.91),
(94, 'Bộ GD&ĐT ban hành quy chế tuyển sinh đại học mới, thống nhất các phương thức xét tuyển và tăng quyền tự chủ cho trường.', 0.88),
(94, 'Các trường đại học top đầu tổ chức kỳ thi đánh giá năng lực riêng, thu hút đông đảo thí sinh tham dự.', 0.89),
(94, 'Quy định về việc sử dụng kết quả kỳ thi tốt nghiệp THPT để xét tuyển đại học, chiếm tỷ lệ lớn trong tổng chỉ tiêu.', 0.90),
(94, 'Thí sinh cần lưu ý về thời gian đăng ký, điều chỉnh nguyện vọng và các thủ tục cần thiết trong quy trình xét tuyển.', 0.93),
(94, 'Các trường đại học công bố ngưỡng đảm bảo chất lượng đầu vào (điểm sàn) cho các ngành học khác nhau.', 0.87),
(94, 'Chính sách ưu tiên cộng điểm cho thí sinh thuộc diện chính sách, con em người có công và dân tộc thiểu số.', 0.85),
(94, 'Các chuyên gia tư vấn tuyển sinh phân tích chiến lược nộp hồ sơ, sắp xếp nguyện vọng để tăng cơ hội trúng tuyển.', 0.94),
(94, 'Quy chế tuyển sinh vào các trường công an, quân đội có những yêu cầu đặc thù về sức khỏe và lý lịch.', 0.86),
(94, 'Các trường đại học áp dụng phương thức xét tuyển kết hợp: kết quả thi THPT, học bạ và chứng chỉ ngoại ngữ quốc tế.', 0.81),
(94, 'Tuyển sinh đầu cấp (lớp 1, lớp 6) tại các trường điểm có sự cạnh tranh lớn, áp dụng các tiêu chí xét tuyển đặc biệt.', 0.88),
(94, 'Phân tích về ưu điểm và nhược điểm của phương thức xét tuyển học bạ so với xét tuyển bằng điểm thi.', 0.79),
(94, 'Các trường cao đẳng và trung cấp nghề tuyển sinh quanh năm, đa dạng hóa hình thức xét tuyển để thu hút học viên.', 0.83),
(94, 'Bộ GD&ĐT khuyến nghị các trường đại học công bố quy chế tuyển sinh sớm để thí sinh có thời gian chuẩn bị.', 0.82),
(94, 'Thí sinh cần cẩn thận với các thông tin tuyển sinh giả, không chính xác từ các nguồn không chính thống.', 0.95),
(94, 'Các trường đại học công bố chi tiết về chỉ tiêu xét tuyển cho từng phương thức, giúp thí sinh lựa chọn chiến lược phù hợp.', 0.84),
(94, 'Quy chế tuyển sinh đầu cấp (lớp 10) tại các trường chuyên, trường chất lượng cao có những bài kiểm tra năng lực riêng.', 0.90),
(94, 'Các trường đại học tăng cường ứng dụng công nghệ trong việc tiếp nhận hồ sơ và tra cứu kết quả xét tuyển.', 0.80),
(94, 'Phân tích về ảnh hưởng của việc tăng chỉ tiêu xét tuyển học bạ đối với chất lượng đầu vào của các trường.', 0.77),
(94, 'Các quy định về việc bảo lưu kết quả thi và chuyển trường cho sinh viên đại học.', 0.85),
(94, 'Chính sách xét tuyển cho sinh viên quốc tế và các chương trình liên kết đào tạo nước ngoài.', 0.91),
(94, 'Các trường đại học công bố danh sách thí sinh trúng tuyển sớm và các mốc thời gian xác nhận nhập học.', 0.89),
(94, 'Việc áp dụng các bài kiểm tra phỏng vấn và đánh giá hồ sơ cá nhân trong quy trình xét tuyển.', 0.76),
(94, 'Các trường đại học khu vực phía Nam và phía Bắc tổ chức các kỳ thi đánh giá năng lực độc lập, thu hút thí sinh liên vùng.', 0.88),
(94, 'Quy chế xét tuyển vào các ngành sư phạm và y tế có những tiêu chí đặc biệt về đạo đức và sức khỏe.', 0.93),
(94, 'Các chuyên gia tư vấn về cách tính điểm chuẩn và tỷ lệ chọi dựa trên các phương thức xét tuyển khác nhau.', 0.83),
(94, 'Thí sinh cần tìm hiểu rõ về chính sách học bổng và hỗ trợ tài chính khi chọn trường và ngành học.', 0.87),
(94, 'Bộ GD&ĐT yêu cầu các trường đại học công bố rõ ràng và minh bạch tất cả các thông tin tuyển sinh.', 0.94);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(95, 'Trường X công bố mẫu đồng phục mới nhận nhiều ý kiến trái chiều từ phụ huynh và học sinh về kiểu dáng và chất liệu.', 0.95),
(95, 'Phụ huynh phản ánh về giá bán đồng phục quá cao, yêu cầu nhà trường công khai chi phí và quy trình đấu thầu.', 0.94),
(95, 'Ý nghĩa của việc mặc đồng phục trong môi trường giáo dục: tạo sự bình đẳng, kỷ luật và nét đẹp học đường.', 0.92),
(95, 'Các trường học đổi mới thiết kế đồng phục, thêm thắt các chi tiết hiện đại, phù hợp với xu hướng thời trang học đường.', 0.88),
(95, 'Tranh cãi về việc có nên bắt buộc học sinh mặc đồng phục trong tất cả các ngày trong tuần hay không.', 0.91),
(95, 'Các trường đại học thiết kế đồng phục riêng cho sinh viên, chủ yếu là áo khoác và áo thun sự kiện.', 0.87),
(95, 'Quy định về việc mặc trang phục truyền thống (Áo dài) trong các dịp lễ tết và các buổi lễ quan trọng của trường.', 0.90),
(95, 'Nhu cầu về đồng phục mùa đông, áo ấm và đồng phục thể dục được thiết kế tiện dụng và thoải mái hơn.', 0.85),
(95, 'Các trường học hợp tác với các nhà cung cấp uy tín, cam kết về chất lượng vải, đường may và độ bền của đồng phục.', 0.86),
(95, 'Phụ huynh kiến nghị về việc cho phép học sinh tự mua đồng phục theo mẫu mã quy định, thay vì mua tại trường.', 0.93),
(95, 'Các trường học quy định rõ về trang phục và tác phong của học sinh ngoài đồng phục (giày dép, tóc tai).', 0.89),
(95, 'Nghiên cứu về ảnh hưởng của đồng phục đến tâm lý và sự tự tin của học sinh trong môi trường học tập.', 0.84),
(95, 'Các trường mầm non thiết kế đồng phục đáng yêu, nhiều màu sắc, phù hợp với lứa tuổi trẻ nhỏ.', 0.81),
(95, 'Các trường học tổ chức các buổi trình diễn thời trang đồng phục, lấy ý kiến của học sinh trước khi đưa vào sử dụng.', 0.88),
(95, 'Vấn đề bạo lực học đường liên quan đến việc chế giễu trang phục và đồng phục của học sinh có hoàn cảnh khó khăn.', 0.95),
(95, 'Các trường học tăng cường việc kiểm tra và nhắc nhở học sinh về việc mặc đồng phục đúng quy định.', 0.79),
(95, 'Các trường học cho phép học sinh mặc đồng phục cũ trong thời gian chờ đợi mẫu đồng phục mới.', 0.83),
(95, 'Sự khác biệt trong thiết kế và chất liệu đồng phục giữa các trường công lập và trường quốc tế.', 0.80),
(95, 'Các trường học tăng cường tuyên truyền về văn hóa trang phục học đường, giáo dục học sinh về sự tôn trọng.', 0.87),
(95, 'Nhu cầu về các loại đồng phục có khả năng chống tia UV và thấm hút mồ hôi tốt hơn trong mùa hè.', 0.77),
(95, 'Các trường học tổ chức các hoạt động từ thiện, quyên góp đồng phục cũ cho học sinh vùng cao.', 0.90),
(95, 'Chính sách hỗ trợ đồng phục miễn phí hoặc giảm giá cho học sinh thuộc diện chính sách và hộ nghèo.', 0.91),
(95, 'Các trường học tổ chức cuộc thi thiết kế đồng phục sáng tạo, thu hút sự tham gia của học sinh.', 0.82),
(95, 'Các chuyên gia thời trang đánh giá về tính thẩm mỹ và tiện dụng của các mẫu đồng phục học sinh mới.', 0.89),
(95, 'Các trường học yêu cầu đồng phục phải đảm bảo tính an toàn, không gây dị ứng da cho học sinh.', 0.76),
(95, 'Phụ huynh kiến nghị về việc nên có nhiều mẫu đồng phục khác nhau để học sinh có thêm lựa chọn.', 0.88),
(95, 'Các trường học tăng cường kiểm soát việc bán đồng phục tại các cửa hàng bên ngoài, đảm bảo chất lượng.', 0.84),
(95, 'Việc sử dụng logo và màu sắc đặc trưng của trường trên đồng phục, tạo nên nét riêng biệt.', 0.83),
(95, 'Các trường đại học giới thiệu các mẫu đồng phục mang tính chuyên nghiệp, phù hợp với môi trường làm việc.', 0.92),
(95, 'Các quy định về đồng phục dành cho giáo viên và nhân viên nhà trường, tạo sự thống nhất trong trang phục.', 0.78);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(96, 'Tăng cường giám sát an toàn thực phẩm tại các trường mẫu giáo, đảm bảo nguồn gốc và chất lượng bữa ăn cho trẻ.', 0.95),
(96, 'Chính sách phổ cập giáo dục mầm non 5 tuổi được triển khai rộng rãi, chuẩn bị cho trẻ vào lớp 1.', 0.92),
(96, 'Phương pháp giáo dục sớm Montessori và Reggio Emilia được áp dụng rộng rãi tại các trường mầm non tư thục.', 0.91),
(96, 'Các trường mầm non đối mặt với tình trạng thiếu giáo viên, đặc biệt là giáo viên có trình độ chuyên môn cao.', 0.93),
(96, 'Các trường mầm non tăng cường các hoạt động giáo dục kỹ năng sống, phát triển cảm xúc và xã hội cho trẻ.', 0.88),
(96, 'Nhu cầu về trường mầm non công lập chất lượng cao tăng cao, nhiều phụ huynh khó khăn trong việc xin học cho con.', 0.90),
(96, 'Chính phủ ban hành các chính sách hỗ trợ phát triển thể chất và tinh thần cho trẻ em dưới 6 tuổi.', 0.87),
(96, 'Các trường mầm non áp dụng công nghệ vào quản lý và thông báo hoạt động của trẻ cho phụ huynh (sử dụng ứng dụng).', 0.86),
(96, 'Các quy định về an toàn, phòng chống cháy nổ và tai nạn thương tích tại các trường mầm non.', 0.94),
(96, 'Các trường mầm non tổ chức các hoạt động ngoại khóa, tham quan dã ngoại để trẻ khám phá thế giới xung quanh.', 0.85),
(96, 'Nghiên cứu về việc sử dụng các trò chơi và hoạt động nghệ thuật để kích thích sự sáng tạo của trẻ mầm non.', 0.84),
(96, 'Các trường mầm non quốc tế giới thiệu các chương trình học bằng tiếng Anh, chuẩn bị cho trẻ học tập quốc tế.', 0.92),
(96, 'Tình trạng lạm dụng và bạo hành trẻ em tại các cơ sở giữ trẻ không phép được cảnh báo nghiêm trọng.', 0.95),
(96, 'Các trường mầm non công bố chi tiết về chương trình dinh dưỡng và thực đơn hàng ngày của trẻ.', 0.81),
(96, 'Các chính sách hỗ trợ học phí và miễn giảm học phí cho trẻ mầm non thuộc diện gia đình khó khăn.', 0.88),
(96, 'Việc đào tạo và bồi dưỡng giáo viên mầm non về tâm lý học trẻ em và phương pháp giáo dục hiện đại.', 0.87),
(96, 'Các trường mầm non tăng cường hợp tác với phụ huynh trong việc giáo dục và chăm sóc trẻ tại nhà.', 0.80),
(96, 'Nhu cầu về các sản phẩm đồ chơi giáo dục và học liệu an toàn, thân thiện với môi trường cho trẻ.', 0.79),
(96, 'Các trường mầm non áp dụng mô hình trường học xanh, sạch, đẹp, tạo không gian vui chơi an toàn cho trẻ.', 0.86),
(96, 'Bộ GD&ĐT ban hành quy định về tiêu chuẩn cơ sở vật chất và diện tích lớp học tối thiểu cho trường mầm non.', 0.91),
(96, 'Các trường mầm non tổ chức các buổi khám sức khỏe định kỳ và tiêm chủng cho trẻ em.', 0.83),
(96, 'Chính sách hỗ trợ xây dựng nhà trẻ, mẫu giáo tại các khu công nghiệp, phục vụ con em công nhân.', 0.90),
(96, 'Các chuyên gia khuyến nghị về việc giới hạn thời gian trẻ sử dụng thiết bị điện tử tại trường mầm non.', 0.89),
(96, 'Các trường mầm non tăng cường giáo dục về an toàn giao thông và phòng chống tai nạn cho trẻ.', 0.82),
(96, 'Việc sử dụng các bài hát, câu chuyện và hoạt động kể chuyện để phát triển ngôn ngữ cho trẻ mầm non.', 0.77),
(96, 'Các trường mầm non tổ chức lễ ra trường và chia tay cho trẻ 5 tuổi chuẩn bị vào lớp 1.', 0.88),
(96, 'Phụ huynh quan tâm đến các chương trình ngoại ngữ sớm và các lớp học năng khiếu tại trường mầm non.', 0.76),
(96, 'Các quy định về số lượng trẻ tối đa trong một lớp học mầm non, đảm bảo chất lượng chăm sóc.', 0.93),
(96, 'Các trường mầm non tăng cường tuyển dụng nhân viên y tế và dinh dưỡng chuyên trách.', 0.85),
(96, 'Bộ GD&ĐT yêu cầu các trường mầm non công khai thông tin về giấy phép hoạt động và chất lượng giáo dục.', 0.94);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(97, 'Du học sinh Việt Nam tại Hàn Quốc tăng đột biến, tập trung vào các ngành công nghệ, giải trí và kinh doanh.', 0.95),
(97, 'Cảnh báo về các trung tâm tư vấn du học lừa đảo, không có giấy phép và đưa ra thông tin không chính xác về trường học.', 0.94),
(97, 'Chính sách định cư sau tốt nghiệp cho du học sinh tại Canada được nới lỏng, thu hút lượng lớn sinh viên quốc tế.', 0.92),
(97, 'Các chương trình du học hè và du học ngắn hạn cho học sinh THPT được tổ chức sôi nổi, giúp trải nghiệm môi trường quốc tế.', 0.88),
(97, 'Du học Mỹ tiếp tục là lựa chọn hàng đầu, với các suất học bổng hấp dẫn và chất lượng giáo dục hàng đầu thế giới.', 0.91),
(97, 'Chi phí du học tại các quốc gia (Úc, New Zealand, Anh) có sự khác biệt lớn về học phí và sinh hoạt phí.', 0.90),
(97, 'Các trường đại học quốc tế tổ chức các buổi hội thảo tuyển sinh và phỏng vấn trực tiếp tại Việt Nam.', 0.87),
(97, 'Điều kiện nhập học du học: yêu cầu về điểm GPA, chứng chỉ IELTS/TOEFL và các hoạt động ngoại khóa.', 0.86),
(97, 'Các chương trình du học nghề (Vocational Training) tại Đức, Nhật Bản được nhiều học sinh lựa chọn.', 0.89),
(97, 'Vấn đề xin visa du học có sự phức tạp, đòi hỏi chứng minh tài chính và mục đích học tập rõ ràng.', 0.93),
(97, 'Các công ty tư vấn du học uy tín cung cấp dịch vụ trọn gói từ chọn trường, xin visa đến hỗ trợ sau nhập học.', 0.85),
(97, 'Nhu cầu về các chương trình du học liên kết và chuyển tiếp giữa các trường đại học Việt Nam và quốc tế.', 0.84),
(97, 'Các du học sinh Việt Nam gặp khó khăn trong việc tìm kiếm việc làm thêm và hòa nhập với văn hóa địa phương.', 0.81),
(97, 'Các trường đại học tại châu Âu cung cấp các chương trình đào tạo bằng tiếng Anh với chi phí hợp lý.', 0.88),
(97, 'Chính sách bảo hiểm y tế và hỗ trợ tâm lý cho du học sinh trong quá trình học tập tại nước ngoài.', 0.90),
(97, 'Nghiên cứu về tỷ lệ du học sinh Việt Nam trở về nước làm việc sau khi tốt nghiệp tại các quốc gia phát triển.', 0.79),
(97, 'Các chương trình học bổng chính phủ và học bổng của các trường đại học quốc tế dành cho sinh viên Việt Nam.', 0.91),
(97, 'Thị trường du học Philippines và Singapore thu hút sinh viên học tiếng Anh ngắn hạn và chương trình dự bị.', 0.83),
(97, 'Các chuyên gia tư vấn về việc lựa chọn thành phố và khu vực học tập phù hợp với sở thích và tài chính.', 0.87),
(97, 'Việc sử dụng các nền tảng trực tuyến để tìm kiếm thông tin du học và kết nối với cựu du học sinh.', 0.80),
(97, 'Các trường đại học Việt Nam mở các văn phòng đại diện tại nước ngoài để hỗ trợ du học sinh.', 0.77),
(97, 'Chính sách hỗ trợ vốn vay du học và các gói tín dụng ưu đãi từ các ngân hàng.', 0.89),
(97, 'Các du học sinh Việt Nam đạt thành tích cao trong nghiên cứu khoa học và các cuộc thi học thuật quốc tế.', 0.92),
(97, 'Phân tích về rào cản ngôn ngữ và văn hóa đối với du học sinh Việt Nam trong môi trường học tập mới.', 0.93),
(97, 'Các chương trình du học cho học sinh THCS và THPT, giúp làm quen với môi trường giáo dục quốc tế sớm.', 0.82),
(97, 'Các trường đại học giới thiệu các khóa học dự bị (Foundation Programs) cho sinh viên chưa đủ điều kiện nhập học.', 0.85),
(97, 'Cảnh báo về việc lợi dụng visa du học để làm việc bất hợp pháp tại nước ngoài, gây ảnh hưởng đến cộng đồng.', 0.95),
(97, 'Các trung tâm tư vấn du học cần công khai, minh bạch về chi phí dịch vụ và các khoản phí khác.', 0.78),
(97, 'Nhu cầu về các chương trình du học hè gia đình, kết hợp học tập và du lịch trải nghiệm.', 0.84),
(97, 'Chính sách hoàn thuế và các ưu đãi tài chính cho du học sinh tại các quốc gia phát triển.', 0.86);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(98, 'Học sinh Việt Nam đạt điểm IELTS cao kỷ lục, chứng tỏ chất lượng dạy và học tiếng Anh ngày càng được nâng cao.', 0.95),
(98, 'Bộ GD&ĐT công bố đề án phổ cập tiếng Anh cho học sinh cấp 1, tập trung vào kỹ năng nghe nói và phát âm.', 0.92),
(98, 'Xu hướng học tiếng Trung và tiếng Hàn ngày càng phổ biến, do nhu cầu hợp tác kinh tế và văn hóa.', 0.91),
(98, 'Các trung tâm ngoại ngữ mở các khóa luyện thi chứng chỉ quốc tế (TOEFL, TOEIC, SAT) với cam kết đầu ra cao.', 0.88),
(98, 'Môn ngoại ngữ được đưa vào giảng dạy từ cấp mầm non, giúp trẻ tiếp xúc sớm và phát triển khả năng ngôn ngữ tự nhiên.', 0.89),
(98, 'Các trường đại học sử dụng chứng chỉ ngoại ngữ quốc tế (IELTS, TOEFL) để xét tuyển thẳng và miễn học môn tiếng Anh.', 0.93),
(98, 'Nhu cầu về giáo viên ngoại ngữ chất lượng cao, có chứng chỉ quốc tế và phương pháp giảng dạy hiện đại.', 0.90),
(98, 'Các trường học áp dụng các phương pháp học tiếng Anh thông qua trò chơi, âm nhạc và các hoạt động tương tác.', 0.87),
(98, 'Các kỳ thi chứng chỉ ngoại ngữ quốc tế được tổ chức thường xuyên, đảm bảo tính công bằng và khách quan.', 0.94),
(98, 'Học sinh được khuyến khích học các ngoại ngữ ít phổ biến (Tiếng Pháp, Tiếng Đức, Tiếng Nga) để mở rộng cơ hội nghề nghiệp.', 0.85),
(98, 'Nghiên cứu về vai trò của công nghệ (AI, ứng dụng di động) trong việc hỗ trợ học và luyện thi ngoại ngữ.', 0.84),
(98, 'Các trường đại học tăng cường các chương trình đào tạo bằng tiếng Anh cho sinh viên các ngành kinh tế và kỹ thuật.', 0.92),
(98, 'Các trung tâm ngoại ngữ cảnh báo về các chứng chỉ giả, không có giá trị và các hình thức lừa đảo luyện thi.', 0.95),
(98, 'Các trường THPT tổ chức các câu lạc bộ tiếng Anh và tiếng Hàn, tạo môi trường giao tiếp thực tế cho học sinh.', 0.81),
(98, 'Việc sử dụng giáo viên bản xứ (native speakers) để giảng dạy ngoại ngữ tại các trường học và trung tâm.', 0.88),
(98, 'Các chính sách hỗ trợ tài chính cho học sinh nghèo tham gia các khóa học ngoại ngữ và luyện thi chứng chỉ.', 0.79),
(98, 'Các trường đại học tổ chức các bài kiểm tra đánh giá năng lực ngoại ngữ đầu vào cho tân sinh viên.', 0.83),
(98, 'Thị trường sách giáo khoa và tài liệu học ngoại ngữ có sự cạnh tranh lớn giữa các nhà xuất bản.', 0.80),
(98, 'Các trường học tăng cường việc dạy và học tiếng Anh chuyên ngành cho sinh viên các lĩnh vực đặc thù.', 0.86),
(98, 'Nhu cầu về các chứng chỉ tiếng Anh chuyên biệt (Business English, Academic English) có sự tăng trưởng.', 0.77),
(98, 'Các trung tâm ngoại ngữ cam kết hoàn trả học phí nếu học viên không đạt được mục tiêu chứng chỉ đã đề ra.', 0.91),
(98, 'Chính sách hỗ trợ giáo viên ngoại ngữ tham gia các khóa tập huấn và bồi dưỡng chuyên môn quốc tế.', 0.85),
(98, 'Các trường đại học giới thiệu các khóa học ngoại ngữ ngắn hạn, tập trung vào kỹ năng phỏng vấn và thuyết trình.', 0.82),
(98, 'Các chuyên gia khuyến nghị về việc học ngoại ngữ kết hợp với việc tìm hiểu văn hóa của các quốc gia.', 0.89),
(98, 'Việc sử dụng các kỳ thi chứng chỉ ngoại ngữ làm tiêu chuẩn đánh giá năng lực ngôn ngữ của sinh viên.', 0.76),
(98, 'Các trường học tăng cường việc kiểm tra và đánh giá thường xuyên kỹ năng nói và viết tiếng Anh của học sinh.', 0.88),
(98, 'Các trường đại học mở rộng hợp tác với các tổ chức khảo thí quốc tế (British Council, IDP) để tổ chức thi chứng chỉ.', 0.84),
(98, 'Phân tích về điểm mạnh và điểm yếu trong việc dạy và học tiếng Anh của Việt Nam so với các nước trong khu vực.', 0.93),
(98, 'Các trung tâm ngoại ngữ giới thiệu các khóa học tiếng Trung và tiếng Nhật giao tiếp, tập trung vào ngữ cảnh thực tế.', 0.87),
(98, 'Bộ GD&ĐT yêu cầu các trường học công bố rõ ràng về chuẩn đầu ra ngoại ngữ cho từng cấp học và chương trình đào tạo.', 0.90);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(99, 'Bộ GD&ĐT tăng cường giáo dục kỹ năng sống, kỹ năng tự vệ và kỹ năng giải quyết mâu thuẫn để phòng ngừa bạo lực học đường.', 0.95),
(99, 'Phân tích nguyên nhân và giải pháp cho vấn đề bắt nạt trực tuyến (Cyberbullying) qua mạng xã hội và các ứng dụng chat.', 0.94),
(99, 'Các trường học thành lập tổ tư vấn tâm lý học đường, hỗ trợ học sinh và giáo viên giải quyết các vấn đề tâm lý.', 0.92),
(99, 'Các vụ việc bạo lực học đường giữa học sinh xảy ra nghiêm trọng, yêu cầu sự can thiệp và xử lý kịp thời của nhà trường.', 0.93),
(99, 'Tình trạng bạo lực bằng lời nói và cô lập xã hội cũng là một hình thức bạo lực học đường cần được quan tâm.', 0.88),
(99, 'Các quy định về xử lý kỷ luật đối với học sinh vi phạm bạo lực học đường được siết chặt và công khai.', 0.90),
(99, 'Chính sách hỗ trợ tâm lý và pháp lý cho học sinh là nạn nhân của bạo lực học đường, giúp các em ổn định tinh thần.', 0.87),
(99, 'Các trường học tổ chức các buổi tập huấn cho giáo viên về cách nhận biết và xử lý các hành vi bạo lực học đường.', 0.89),
(99, 'Vai trò của phụ huynh trong việc theo dõi, quan tâm và phát hiện sớm các dấu hiệu bạo lực học đường của con em.', 0.86),
(99, 'Các chuyên gia tâm lý khuyến nghị về việc xây dựng môi trường học đường thân thiện, không bạo lực.', 0.91),
(99, 'Các trường học lắp đặt hệ thống camera giám sát tại các khu vực nhạy cảm để ngăn ngừa bạo lực học đường.', 0.85),
(99, 'Nghiên cứu về các yếu tố tâm lý và xã hội dẫn đến hành vi bạo lực ở học sinh và các biện pháp can thiệp.', 0.84),
(99, 'Các trường học tăng cường các hoạt động ngoại khóa, tạo sân chơi lành mạnh, giảm căng thẳng cho học sinh.', 0.81),
(99, 'Các trường học mở các kênh thông tin tiếp nhận phản ánh, tố giác các vụ việc bạo lực học đường (hộp thư, đường dây nóng).', 0.88),
(99, 'Vấn đề bạo lực giữa giáo viên và học sinh: xử lý nghiêm các trường hợp vi phạm đạo đức nhà giáo.', 0.95),
(99, 'Các tổ chức phi chính phủ và cộng đồng tham gia vào các chiến dịch phòng chống bạo lực học đường.', 0.80),
(99, 'Các trường học tổ chức các buổi tuyên truyền về hậu quả của bạo lực học đường đối với cả nạn nhân và người gây ra.', 0.83),
(99, 'Chính sách đào tạo và nâng cao năng lực cho cán bộ Đoàn, Đội trong việc phòng chống bạo lực học đường.', 0.77),
(99, 'Các trường học tăng cường giáo dục về bình đẳng giới và tôn trọng sự khác biệt giữa các học sinh.', 0.89),
(99, 'Việc sử dụng các ứng dụng di động để học sinh có thể báo cáo ẩn danh các vụ việc bạo lực.', 0.82),
(99, 'Các trường học cần có sự phối hợp chặt chẽ giữa nhà trường, gia đình và chính quyền địa phương để giải quyết triệt để.', 0.94),
(99, 'Các chuyên gia tâm lý khuyến nghị về việc áp dụng các liệu pháp tâm lý và tư vấn cá nhân cho học sinh vi phạm.', 0.85),
(99, 'Các trường học tổ chức các buổi đối thoại giữa học sinh, giáo viên và phụ huynh về các vấn đề bạo lực.', 0.76),
(99, 'Nghiên cứu về vai trò của truyền thông và mạng xã hội trong việc lan truyền và ngăn chặn bạo lực học đường.', 0.88),
(99, 'Các trường học tăng cường giáo dục về luật pháp và các quy định về quyền trẻ em.', 0.90),
(99, 'Phân tích về các hình thức bạo lực học đường ở cấp mầm non và tiểu học, cần có sự can thiệp sớm.', 0.87),
(99, 'Các trường học xây dựng các bộ quy tắc ứng xử văn minh, thân thiện trong môi trường học tập.', 0.79),
(99, 'Các chuyên gia tâm lý cảnh báo về tác động lâu dài của bạo lực học đường đối với sức khỏe tinh thần của trẻ.', 0.93),
(99, 'Các trường học cần có chính sách bảo vệ danh tính và thông tin của học sinh báo cáo bạo lực.', 0.91),
(99, 'Bộ GD&ĐT yêu cầu các trường học tổ chức các buổi sinh hoạt chuyên đề về phòng chống bạo lực học đường hàng tháng.', 0.84);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(100, 'Chương trình giáo dục phổ thông 2018 chính thức được áp dụng toàn bộ ở các cấp học, tập trung vào phát triển năng lực học sinh.', 0.95),
(100, 'Tranh cãi về chất lượng và nội dung của bộ sách giáo khoa mới, với nhiều ý kiến đóng góp từ giáo viên và chuyên gia.', 0.94),
(100, 'Đề xuất thí điểm bỏ thi tốt nghiệp THPT và xét tuyển đại học theo năng lực, giảm áp lực thi cử cho học sinh.', 0.92),
(100, 'Bộ GD&ĐT lấy ý kiến về việc sửa đổi, bổ sung chương trình giáo dục phổ thông theo hướng giảm tải và thực tế hơn.', 0.88),
(100, 'Các trường học thí điểm mô hình dạy học mới, tăng cường hoạt động trải nghiệm và nghiên cứu khoa học cho học sinh.', 0.89),
(100, 'Việc áp dụng phương pháp dạy học tích hợp (STEM, STEAM) trong các môn Khoa học Tự nhiên và Công nghệ.', 0.91),
(100, 'Các chuyên gia giáo dục phân tích về ưu điểm và thách thức của việc triển khai chương trình giáo dục phổ thông mới.', 0.93),
(100, 'Nhu cầu về việc đào tạo, bồi dưỡng giáo viên để đáp ứng yêu cầu của chương trình và sách giáo khoa mới.', 0.90),
(100, 'Chính phủ ban hành các chính sách đầu tư vào cơ sở vật chất, thiết bị dạy học để phục vụ việc đổi mới chương trình.', 0.87),
(100, 'Tranh cãi về việc lựa chọn và sử dụng sách giáo khoa từ nhiều nhà xuất bản khác nhau tại các trường học.', 0.95),
(100, 'Bộ GD&ĐT công bố kế hoạch sửa đổi chương trình đào tạo giáo viên, đảm bảo chất lượng đầu ra đáp ứng yêu cầu mới.', 0.84),
(100, 'Các trường đại học điều chỉnh chương trình đào tạo, tích hợp các môn học liên ngành và kỹ năng mềm.', 0.81),
(100, 'Việc áp dụng công nghệ thông tin và chuyển đổi số trong việc xây dựng và quản lý chương trình giáo dục.', 0.88),
(100, 'Các chuyên gia kiến nghị về việc tăng cường vai trò của môn Lịch sử và Văn hóa trong chương trình giáo dục.', 0.86),
(100, 'Chính sách hỗ trợ tài chính cho giáo viên tự học và tự nghiên cứu để nâng cao năng lực chuyên môn.', 0.92),
(100, 'Các trường học thử nghiệm mô hình trường học số (Digital School), áp dụng các công cụ học tập thông minh.', 0.80),
(100, 'Nghiên cứu về tác động của việc đổi mới chương trình giáo dục đối với kết quả học tập của học sinh.', 0.83),
(100, 'Các trường học tăng cường các hoạt động đánh giá học sinh theo hướng phát triển năng lực, không chỉ đánh giá kiến thức.', 0.77),
(100, 'Chính sách hỗ trợ in ấn và phát hành sách giáo khoa miễn phí cho học sinh vùng sâu, vùng xa.', 0.89),
(100, 'Các trường đại học công bố chương trình đào tạo cử nhân chất lượng cao theo tiêu chuẩn quốc tế.', 0.91),
(100, 'Tranh cãi về việc có nên giảm số lượng môn học bắt buộc ở cấp THPT để học sinh tập trung vào các môn chuyên sâu.', 0.85),
(100, 'Việc sử dụng các bài kiểm tra đánh giá tư duy (ví dụ: SAT, ACT) để thay thế các kỳ thi truyền thống.', 0.82),
(100, 'Các trường học tổ chức các buổi tập huấn cho phụ huynh về mục tiêu và phương pháp của chương trình giáo dục mới.', 0.76),
(100, 'Bộ GD&ĐT yêu cầu các trường học công khai thông tin về chương trình giảng dạy và tài liệu học tập.', 0.88),
(100, 'Các chuyên gia phân tích về sự khác biệt giữa sách giáo khoa và các tài liệu tham khảo bổ sung.', 0.90),
(100, 'Nhu cầu về việc xây dựng các phòng thí nghiệm, phòng thực hành hiện đại cho các môn khoa học.', 0.94),
(100, 'Việc áp dụng mô hình "trường học hạnh phúc" trong chương trình giáo dục, tạo môi trường học tập tích cực.', 0.87),
(100, 'Các trường đại học tăng cường hợp tác với doanh nghiệp để thiết kế chương trình đào tạo sát với nhu cầu thực tế.', 0.93),
(100, 'Các trường học tổ chức các buổi hội thảo chuyên đề về phương pháp dạy học đổi mới và kiểm tra đánh giá.', 0.78),
(100, 'Chính sách hỗ trợ giáo viên tham gia các dự án nghiên cứu khoa học và đổi mới sáng tạo trong giáo dục.', 0.85);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(101, 'Tập đoàn X công bố mở bán giai đoạn 2 dự án khu đô thị sinh thái ven sông, thu hút sự quan tâm của nhà đầu tư.', 0.95),
(101, 'Phân tích về giá trị bất động sản tại các khu đô thị vệ tinh, dự báo tăng trưởng mạnh nhờ hạ tầng giao thông cải thiện.', 0.92),
(101, 'Chủ đầu tư cam kết bàn giao dự án khu đô thị đúng tiến độ, đảm bảo chất lượng xây dựng và tiện ích nội khu.', 0.91),
(101, 'Các dự án khu đô thị mới tập trung phát triển không gian xanh, mặt nước và các tiện ích công cộng (trường học, bệnh viện).', 0.88),
(101, 'Vấn đề pháp lý và giấy phép xây dựng của một số dự án đô thị đang được cơ quan chức năng rà soát và xử lý.', 0.93),
(101, 'Xu hướng phát triển các khu đô thị thông minh (Smart City), ứng dụng công nghệ vào quản lý và vận hành.', 0.89),
(101, 'Các chủ đầu tư áp dụng chính sách ưu đãi, hỗ trợ lãi suất cho người mua nhà tại các dự án khu đô thị đầu năm.', 0.90),
(101, 'Nghiên cứu về tác động của các khu đô thị mới đến mật độ dân số và hạ tầng xã hội của khu vực lân cận.', 0.86),
(101, 'Sự cạnh tranh gay gắt giữa các dự án bất động sản cao cấp và tầm trung tại các thành phố lớn.', 0.87),
(101, 'Các khu đô thị tích hợp mô hình "All-in-one", cung cấp đầy đủ dịch vụ mua sắm, giải trí và giáo dục.', 0.94),
(101, 'Chính phủ thúc đẩy việc phát triển các khu đô thị xanh, bền vững, giảm thiểu tác động đến môi trường.', 0.85),
(101, 'Giá đất nền tại các khu vực quy hoạch làm dự án đô thị tăng mạnh, gây khó khăn cho công tác giải phóng mặt bằng.', 0.92),
(101, 'Các dự án đô thị nghỉ dưỡng ven biển thu hút sự đầu tư của các quỹ đầu tư quốc tế.', 0.83),
(101, 'Vấn đề quản lý và vận hành khu đô thị sau khi bàn giao, đặc biệt là việc duy trì chất lượng tiện ích chung.', 0.79),
(101, 'Các chủ đầu tư công bố báo cáo tài chính và tiến độ sử dụng vốn, tăng tính minh bạch cho dự án.', 0.88);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(102, 'Giá chung cư tại Hà Nội và TP.HCM tiếp tục tăng mạnh, đặc biệt ở các khu vực có hạ tầng giao thông phát triển.', 0.95),
(102, 'Bộ Xây dựng thúc đẩy phát triển nhà ở xã hội, đưa ra các chính sách hỗ trợ vốn vay ưu đãi cho người thu nhập thấp.', 0.92),
(102, 'Cư dân chung cư X kiến nghị về chất lượng dịch vụ quản lý, vận hành và chi phí bảo trì không minh bạch.', 0.91),
(102, 'Thị trường căn hộ chung cư cao cấp ghi nhận sự sụt giảm về nguồn cung, nhưng giá bán vẫn duy trì ở mức cao.', 0.88),
(102, 'Các ngân hàng tăng cường giải ngân gói tín dụng cho người mua nhà ở xã hội, đơn giản hóa thủ tục vay vốn.', 0.90),
(102, 'Nhu cầu thuê căn hộ chung cư tăng cao, đặc biệt là các căn hộ gần khu vực trung tâm và các trường đại học.', 0.87),
(102, 'Tranh chấp giữa chủ đầu tư và cư dân chung cư về diện tích chung riêng, quỹ bảo trì và quyền lợi sở hữu.', 0.93),
(102, 'Các dự án chung cư mới tập trung vào thiết kế thông minh, tối ưu hóa không gian sống và sử dụng năng lượng hiệu quả.', 0.89),
(102, 'Các quy định về PCCC tại chung cư được siết chặt, yêu cầu các tòa nhà phải trang bị đầy đủ hệ thống an toàn.', 0.94),
(102, 'Giá bán căn hộ chung cư cũ tại khu vực nội thành có dấu hiệu tăng nhiệt do nguồn cung căn hộ mới khan hiếm.', 0.86),
(102, 'Phân tích về ảnh hưởng của lãi suất ngân hàng đến quyết định mua và đầu tư vào căn hộ chung cư.', 0.84),
(102, 'Các trường hợp cư dân chung cư tự ý cải tạo, cơi nới căn hộ gây ảnh hưởng đến kết cấu và an toàn tòa nhà.', 0.92),
(102, 'Chính sách hỗ trợ mua nhà lần đầu và các ưu đãi thuế dành cho người mua căn hộ chung cư.', 0.81),
(102, 'Các công ty quản lý chung cư áp dụng công nghệ vào việc thanh toán phí, quản lý cư dân và dịch vụ tiện ích.', 0.88),
(102, 'Nhu cầu về căn hộ chung cư có diện tích lớn, phục vụ cho gia đình đa thế hệ tăng cao.', 0.79),
(102, 'Các dự án chung cư cam kết bàn giao sổ hồng đúng hạn, đảm bảo quyền lợi pháp lý cho người mua.', 0.95),
(102, 'Phân tích về tỷ suất lợi nhuận khi đầu tư cho thuê căn hộ chung cư so với các loại hình bất động sản khác.', 0.83),
(102, 'Các trường hợp mâu thuẫn giữa cư dân và ban quản lý về việc sử dụng các tiện ích chung (hồ bơi, phòng gym).', 0.90),
(102, 'Các trường đại học xây dựng ký túc xá cho sinh viên theo mô hình chung cư mini, đáp ứng nhu cầu nhà ở.', 0.77),
(102, 'Quy định về việc tổ chức Hội nghị nhà chung cư, bầu Ban quản trị và phân chia trách nhiệm rõ ràng.', 0.85),
(102, 'Các dự án nhà ở xã hội được triển khai đồng loạt tại các tỉnh, thành phố lớn, giải quyết vấn đề an sinh xã hội.', 0.91),
(102, 'Cư dân chung cư phản ánh về tiếng ồn, ô nhiễm và mật độ dân số quá tải tại khu vực xung quanh.', 0.82),
(102, 'Các chuyên gia khuyến nghị về việc kiểm tra kỹ lưỡng hợp đồng mua bán và các điều khoản pháp lý của căn hộ chung cư.', 0.93),
(102, 'Các ngân hàng áp dụng chính sách cho vay mua chung cư với thời hạn dài và lãi suất cố định ban đầu.', 0.84),
(102, 'Nhu cầu về các căn hộ chung cư dịch vụ (Serviced Apartments) cho người nước ngoài thuê tại Việt Nam.', 0.76),
(102, 'Các trường hợp chủ đầu tư chậm bàn giao căn hộ, gây thiệt hại và bức xúc cho người mua nhà.', 0.94),
(102, 'Các dự án chung cư được cấp chứng nhận công trình xanh, thân thiện với môi trường và tiết kiệm năng lượng.', 0.86),
(102, 'Phân tích về sự chênh lệch giá giữa các tầng và hướng của căn hộ chung cư trong cùng một dự án.', 0.80),
(102, 'Các thành phố lớn nghiên cứu về chính sách kiểm soát giá trần căn hộ chung cư để ổn định thị trường.', 0.87),
(102, 'Các trường hợp tranh chấp về chỗ đậu xe ô tô và xe máy tại các tầng hầm chung cư.', 0.92);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(103, 'Shophouse tại khu vực du lịch biển có giá thuê tăng vọt, tỷ suất sinh lời cao, thu hút nhà đầu tư cá nhân.', 0.95),
(103, 'Phân tích hiệu quả đầu tư vào shophouse tại các khu đô thị mới, dựa trên mật độ dân cư và khả năng khai thác thương mại.', 0.92),
(103, 'Chủ đầu tư tung ra chính sách ưu đãi cho nhà đầu tư shophouse (hỗ trợ hoàn thiện nội thất, miễn phí quản lý).', 0.91),
(103, 'Thị trường shophouse tại các khu vực ven đô có dấu hiệu trầm lắng do kinh doanh chậm và thiếu dân cư.', 0.88),
(103, 'Nhu cầu thuê shophouse để mở cửa hàng tiện lợi, nhà hàng, và các dịch vụ F&B tại các khu dân cư đông đúc.', 0.90),
(103, 'Các dự án shophouse mặt tiền đường lớn có giá trị cao, thường được giao dịch với mức giá hàng chục tỷ đồng.', 0.87),
(103, 'Vấn đề pháp lý và thời hạn sở hữu của shophouse (sở hữu lâu dài hay sở hữu có thời hạn) ảnh hưởng đến quyết định đầu tư.', 0.93),
(103, 'Các nhà đầu tư F0 tìm hiểu về rủi ro và lợi ích khi đầu tư vào shophouse so với căn hộ chung cư.', 0.89),
(103, 'Giá bán shophouse có sự chênh lệch lớn giữa các tầng, tầng trệt thường có giá cao nhất vì tiềm năng thương mại.', 0.94),
(103, 'Các khu đô thị mới tập trung thiết kế các dãy shophouse đồng bộ, tạo nên khu phố thương mại sầm uất.', 0.86),
(103, 'Phân tích về tỷ lệ lấp đầy và mức tăng trưởng giá thuê shophouse tại các trung tâm thương mại lớn.', 0.84),
(103, 'Các ngân hàng tăng cường cho vay đối với các dự án shophouse có vị trí đắc địa và pháp lý rõ ràng.', 0.92),
(103, 'Các chuyên gia khuyến nghị nhà đầu tư cần nghiên cứu kỹ về quy hoạch và lưu lượng khách hàng của khu vực shophouse.', 0.81),
(103, 'Shophouse trong các khu đô thị du lịch biển có khả năng chuyển đổi thành mô hình khách sạn mini hoặc homestay.', 0.88),
(103, 'Các chủ đầu tư cam kết doanh thu cho thuê shophouse trong thời gian đầu, thu hút nhà đầu tư không chuyên.', 0.79),
(103, 'Thị trường shophouse ngoại ô được kỳ vọng sẽ tăng trưởng khi hạ tầng giao thông kết nối các khu vực này.', 0.95),
(103, 'Các quy định về PCCC và an toàn xây dựng đối với loại hình nhà phố thương mại.', 0.83),
(103, 'Các dự án shophouse được thiết kế với hai mặt tiền, tăng khả năng tiếp cận và kinh doanh.', 0.90),
(103, 'Nhu cầu thuê shophouse để mở các phòng khám, trung tâm ngoại ngữ và dịch vụ giáo dục.', 0.77),
(103, 'Phân tích về chi phí đầu tư ban đầu, chi phí quản lý và chi phí sửa chữa đối với shophouse.', 0.85),
(103, 'Các trường hợp shophouse bị bỏ trống, không có khả năng khai thác thương mại do vị trí không thuận lợi.', 0.91),
(103, 'Chính sách thuế và các loại phí liên quan đến việc sở hữu và kinh doanh shophouse.', 0.82),
(103, 'Các nhà đầu tư cần lưu ý về quy định về chiều cao và số tầng được phép xây dựng đối với shophouse.', 0.93),
(103, 'Các dự án shophouse được áp dụng công nghệ smart-home, tiện lợi cho việc quản lý và kinh doanh.', 0.84),
(103, 'Nhu cầu về shophouse trong các khu công nghiệp, phục vụ nhu cầu ăn uống và dịch vụ của công nhân.', 0.76),
(103, 'Các trường hợp tranh chấp hợp đồng cho thuê và quyền lợi giữa chủ sở hữu và người thuê shophouse.', 0.94),
(103, 'Shophouse được thiết kế theo phong cách kiến trúc đặc trưng (Địa Trung Hải, tân cổ điển), tạo điểm nhấn cho khu đô thị.', 0.86),
(103, 'Phân tích về sự khác biệt giữa shophouse truyền thống và shophouse khối đế chung cư.', 0.80),
(103, 'Các thành phố lớn nghiên cứu chính sách quản lý việc sử dụng lòng đường và vỉa hè trước các shophouse.', 0.87),
(103, 'Các chủ đầu tư tổ chức các sự kiện thu hút khách hàng, tăng lưu lượng giao thông đến khu vực shophouse.', 0.92);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(104, 'Quy hoạch tổng thể phát triển kinh tế-xã hội vùng Đồng bằng sông Cửu Long, ưu tiên phát triển hạ tầng giao thông kết nối.', 0.95),
(104, 'Thủ tướng Chính phủ chỉ đạo đẩy nhanh tiến độ các dự án giao thông trọng điểm quốc gia, bao gồm cao tốc và đường sắt.', 0.93),
(104, 'Phân tích ảnh hưởng của quy hoạch đô thị đến giá đất khu vực, đặc biệt là các vùng được nâng cấp hạ tầng.', 0.92),
(104, 'Các tỉnh, thành phố lớn công bố quy hoạch sử dụng đất, định hướng phát triển công nghiệp và dịch vụ.', 0.88),
(104, 'Việc áp dụng công nghệ BIM (Building Information Modeling) trong quản lý và giám sát các dự án hạ tầng.', 0.89),
(104, 'Các chuyên gia kiến nghị về việc đồng bộ quy hoạch giao thông với quy hoạch đô thị và khu dân cư.', 0.90),
(104, 'Vốn đầu tư công cho các dự án hạ tầng giao thông tăng mạnh, tạo động lực thúc đẩy tăng trưởng kinh tế.', 0.94),
(104, 'Chính phủ thúc đẩy xã hội hóa đầu tư hạ tầng, kêu gọi vốn tư nhân tham gia vào các dự án BOT.', 0.87),
(104, 'Quy hoạch phát triển hệ thống giao thông thông minh (ITS), giảm ùn tắc và tăng cường an toàn giao thông.', 0.86),
(104, 'Các dự án hạ tầng kỹ thuật (cấp thoát nước, điện, viễn thông) được ưu tiên triển khai đồng bộ với dự án giao thông.', 0.85),
(104, 'Các thành phố lớn nghiên cứu về quy hoạch phát triển giao thông công cộng (Metro, xe buýt nhanh) để giảm phương tiện cá nhân.', 0.91),
(104, 'Việc giải phóng mặt bằng gặp nhiều khó khăn, ảnh hưởng đến tiến độ triển khai các dự án quy hoạch hạ tầng.', 0.93),
(104, 'Các chính sách ưu đãi đầu tư vào các khu công nghiệp, khu kinh tế ven biển theo quy hoạch vùng.', 0.83),
(104, 'Phân tích về vai trò của hạ tầng kỹ thuật đối với việc chống biến đổi khí hậu và lũ lụt tại các đô thị.', 0.79),
(104, 'Các dự án hạ tầng giao thông liên kết vùng được ưu tiên, tạo mạng lưới kết nối giữa các tỉnh thành.', 0.88);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(105, 'Khánh thành cầu vượt biển dài nhất Việt Nam, rút ngắn thời gian di chuyển và kết nối kinh tế khu vực ven biển.', 0.95),
(105, 'Vấn đề ùn tắc giao thông tại các cây cầu cửa ngõ thành phố trong giờ cao điểm, yêu cầu mở rộng và xây thêm cầu.', 0.93),
(105, 'Dự án xây dựng cầu mới nhằm giải tỏa áp lực giao thông cho cầu cũ, áp dụng công nghệ xây dựng hiện đại.', 0.92),
(105, 'Tiến độ thi công cầu Thủ Thiêm 2 (TP.HCM) được đẩy nhanh, kỳ vọng sẽ hoàn thành sớm và đưa vào sử dụng.', 0.88),
(105, 'Các dự án cầu dây văng với thiết kế độc đáo, trở thành biểu tượng kiến trúc mới của thành phố.', 0.89),
(105, 'Vốn đầu tư cho các dự án cầu vượt sông, cầu vượt biển được huy động từ nguồn vốn ODA và vốn xã hội hóa.', 0.90),
(105, 'Các chuyên gia cảnh báo về chất lượng và độ bền của một số cây cầu cũ, cần được kiểm định và nâng cấp thường xuyên.', 0.94),
(105, 'Các trường hợp cầu dân sinh xuống cấp tại vùng nông thôn, gây nguy hiểm cho người dân và cần được sửa chữa.', 0.87),
(105, 'Các dự án xây dựng cầu vượt tại các nút giao thông quan trọng, giải quyết triệt để tình trạng kẹt xe.', 0.86),
(105, 'Chính phủ chỉ đạo về việc sử dụng vật liệu xây dựng thân thiện với môi trường trong các dự án cầu đường.', 0.85),
(105, 'Nghiên cứu về giải pháp thiết kế cầu chống động đất và chống biến đổi khí hậu (nước biển dâng).', 0.91),
(105, 'Các cây cầu mới được xây dựng giúp kết nối các khu công nghiệp và cảng biển, thúc đẩy logistics.', 0.93),
(105, 'Các dự án xây dựng cầu dành riêng cho người đi bộ và xe đạp tại các khu vực du lịch và trung tâm thành phố.', 0.81),
(105, 'Vấn đề thu phí sử dụng cầu và các chính sách quản lý thu phí tại các trạm BOT.', 0.88),
(105, 'Các trường hợp chậm tiến độ giải phóng mặt bằng, ảnh hưởng đến thời gian thi công các dự án cầu lớn.', 0.95),
(105, 'Cầu Vàm Cống và cầu Cao Lãnh đóng vai trò quan trọng trong việc kết nối giao thông vùng Đồng bằng sông Cửu Long.', 0.79),
(105, 'Các công nghệ mới trong việc đúc dầm cầu và thi công trụ cầu trong điều kiện địa chất phức tạp.', 0.83),
(105, 'Các trường hợp tai nạn giao thông nghiêm trọng xảy ra trên cầu, yêu cầu tăng cường biện pháp an toàn.', 0.90),
(105, 'Quy định về tải trọng và tốc độ lưu thông trên các cây cầu lớn, đảm bảo an toàn kết cấu.', 0.77),
(105, 'Các dự án xây dựng cầu qua sông Sài Gòn và sông Hồng được ưu tiên triển khai để giảm áp lực cho các cầu hiện hữu.', 0.85),
(105, 'Phân tích về chi phí xây dựng, chi phí bảo trì và tuổi thọ thiết kế của các loại cầu khác nhau.', 0.91),
(105, 'Các chuyên gia kiến nghị về việc xây dựng thêm các cầu đi bộ trên cao tại các khu vực đông dân cư.', 0.82),
(105, 'Các trường hợp công trình cầu bị hư hỏng do lũ lụt và các yếu tố thời tiết cực đoan.', 0.94),
(105, 'Các dự án xây dựng cầu cạn và cầu vượt đường sắt để giải quyết các giao cắt đồng mức nguy hiểm.', 0.84),
(105, 'Việc sử dụng đèn chiếu sáng nghệ thuật để trang trí các cây cầu lớn, tạo điểm nhấn về đêm.', 0.76),
(105, 'Các trường hợp tranh chấp về ranh giới hành chính và quản lý khi xây dựng cầu liên tỉnh.', 0.88),
(105, 'Quy hoạch phát triển hệ thống cầu và đường bộ ven biển, phục vụ du lịch và quốc phòng an ninh.', 0.86),
(105, 'Các trường hợp người dân vi phạm an toàn giao thông khi đi qua các cây cầu nhỏ và cầu tạm.', 0.83),
(105, 'Phân tích về tác động của việc xây cầu mới đến sinh kế và môi trường của người dân ven sông.', 0.92),
(105, 'Chính sách hỗ trợ giải phóng mặt bằng và bồi thường cho người dân bị ảnh hưởng bởi dự án cầu đường.', 0.87);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(106, 'Tuyến Metro số 1 TP.HCM chạy thử nghiệm thành công, dự kiến đưa vào khai thác thương mại trong năm tới.', 0.95),
(106, 'Dự án đường sắt cao tốc Bắc – Nam được trình Quốc hội phê duyệt, kỳ vọng sẽ là động lực phát triển kinh tế quốc gia.', 0.94),
(106, 'Ngành đường sắt nâng cấp hệ thống tín hiệu, đảm bảo an toàn và tăng tốc độ chạy tàu trên tuyến đường sắt quốc gia.', 0.92),
(106, 'Các dự án đường sắt đô thị tại Hà Nội gặp khó khăn về vốn, chậm tiến độ và vướng mắc trong giải phóng mặt bằng.', 0.93),
(106, 'Phân tích về hiệu quả kinh tế và xã hội của việc đầu tư vào đường sắt đô thị so với các loại hình giao thông khác.', 0.88),
(106, 'Các chuyên gia kiến nghị về việc phát triển các tuyến đường sắt kết nối các cảng biển và khu công nghiệp.', 0.90),
(106, 'Chính phủ thúc đẩy việc sử dụng công nghệ tiên tiến, đặc biệt là công nghệ của Nhật Bản và châu Âu trong dự án Metro.', 0.87),
(106, 'Nhu cầu về nhân lực chất lượng cao để quản lý, vận hành và bảo trì hệ thống đường sắt đô thị phức tạp.', 0.89),
(106, 'Giá vé và chính sách trợ giá cho người dân sử dụng dịch vụ đường sắt đô thị (Metro).', 0.86),
(106, 'Các dự án đường sắt đô thị được quy hoạch tích hợp với các tuyến xe buýt và các phương tiện công cộng khác.', 0.91),
(106, 'Các trường hợp tai nạn giao thông tại các đường ngang giao cắt với đường sắt, yêu cầu xây dựng cầu vượt hoặc hầm chui.', 0.95),
(106, 'Ngành đường sắt tăng cường tàu phục vụ du lịch và các tuyến tàu chuyên biệt đến các điểm đến nổi tiếng.', 0.84),
(106, 'Việc cổ phần hóa và xã hội hóa đầu tư vào ngành đường sắt, kêu gọi vốn tư nhân tham gia phát triển.', 0.81),
(106, 'Các dự án xây dựng ga đường sắt trung tâm và các nhà ga vệ tinh tại các thành phố lớn.', 0.88),
(106, 'Đường sắt đô thị là giải pháp quan trọng để giảm ùn tắc giao thông và ô nhiễm môi trường tại các đô thị lớn.', 0.79),
(106, 'Các trường hợp chậm tiến độ thi công do vướng mắc về thủ tục hành chính và quản lý dự án.', 0.92),
(106, 'Chính sách ưu đãi và hỗ trợ vay vốn cho dự án đường sắt cao tốc, đảm bảo nguồn vốn triển khai.', 0.83),
(106, 'Nghiên cứu về việc sử dụng công nghệ tàu điện trên không (Monorail) tại một số khu đô thị mới.', 0.77),
(106, 'Các trường hợp người dân vi phạm hành lang an toàn đường sắt, gây nguy hiểm cho hoạt động vận tải.', 0.90),
(106, 'Các dự án đường sắt kết nối sân bay và cảng biển, tăng cường vai trò vận tải hàng hóa và hành khách.', 0.85),
(106, 'Phân tích về chi phí vận hành và bảo trì hệ thống đường sắt cao tốc so với đường sắt truyền thống.', 0.91),
(106, 'Các chuyên gia khuyến nghị về việc phát triển đồng bộ hạ tầng đường sắt và các trạm trung chuyển.', 0.82),
(106, 'Các dự án đường sắt đô thị tập trung phát triển các khu vực ngoài trung tâm, hình thành các đô thị vệ tinh.', 0.94),
(106, 'Việc sử dụng các toa tàu hiện đại, tiện nghi và dịch vụ chất lượng cao trên tuyến đường sắt Bắc – Nam.', 0.86),
(106, 'Các quy định về an ninh, an toàn và phòng chống khủng bố đối với hệ thống đường sắt đô thị.', 0.76),
(106, 'Ngành đường sắt tăng cường các hoạt động kiểm tra, bảo dưỡng định kỳ các tuyến đường sắt và đầu máy toa xe.', 0.88),
(106, 'Các trường hợp tranh chấp về sở hữu đất đai và bồi thường giải phóng mặt bằng dự án đường sắt.', 0.84),
(106, 'Chính sách phát triển giao thông đa phương thức, kết nối đường sắt với đường bộ và đường thủy.', 0.83),
(106, 'Các trường hợp chậm trễ giờ tàu do sự cố kỹ thuật và các yếu tố khách quan (thời tiết, thiên tai).', 0.92),
(106, 'Các thành phố lớn nghiên cứu về việc xây dựng tuyến đường sắt kết nối khu vực trung tâm và các tỉnh lân cận.', 0.87);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(107, 'Cảng Cái Mép – Thị Vải đón tàu container siêu lớn của thế giới, khẳng định vị thế cảng nước sâu hàng đầu Việt Nam.', 0.95),
(107, 'Việt Nam đầu tư xây dựng cảng trung chuyển quốc tế, mục tiêu trở thành trung tâm logistics hàng hải khu vực.', 0.93),
(107, 'Tình hình thiếu hụt container ảnh hưởng đến vận tải biển quốc tế, đẩy cước phí vận tải tăng cao.', 0.92),
(107, 'Các cảng biển tại khu vực phía Bắc (Hải Phòng) tăng cường năng lực xếp dỡ, giải quyết tình trạng ùn tắc hàng hóa.', 0.88),
(107, 'Chính phủ ban hành các chính sách ưu đãi về thuế và phí để thu hút đầu tư vào các cảng nước sâu.', 0.89),
(107, 'Các chuyên gia kiến nghị về việc phát triển đồng bộ hạ tầng giao thông kết nối cảng biển (đường bộ, đường sắt).', 0.90),
(107, 'Lưu lượng hàng hóa qua các cảng biển Việt Nam tăng trưởng mạnh, đặc biệt là hàng container xuất nhập khẩu.', 0.94),
(107, 'Các cảng biển áp dụng công nghệ số hóa (E-Port) vào quản lý, khai thác và dịch vụ logistics.', 0.87),
(107, 'Quy hoạch phát triển hệ thống cảng biển quốc gia đến năm 2030, tầm nhìn 2050, ưu tiên các cảng lớn.', 0.86),
(107, 'Nhu cầu về nhân lực chất lượng cao trong lĩnh vực logistics, vận tải biển và khai thác cảng.', 0.91),
(107, 'Các cảng biển tăng cường các biện pháp bảo vệ môi trường, xử lý chất thải và chống ô nhiễm dầu.', 0.95),
(107, 'Các trường hợp tàu thuyền vi phạm quy định an toàn hàng hải và gây tai nạn tại khu vực cảng biển.', 0.84),
(107, 'Việc xây dựng các khu công nghiệp và khu kinh tế ven biển, tạo động lực phát triển cho hệ thống cảng.', 0.81),
