INSERT INTO topic_documents (topic_id, text, weight) VALUES
(112, 'Hà Nội thí điểm mở thêm các tuyến xe buýt điện, góp phần giảm thiểu ô nhiễm không khí và tiếng ồn đô thị.', 0.95),
