INSERT INTO topic_documents (topic_id, text, weight) VALUES
(114, 'Tài xế công nghệ đình công phản đối chính sách chiết khấu của hãng, yêu cầu tăng tỷ lệ chia sẻ doanh thu.', 0.95),
(114, 'Đảm bảo an toàn cho tài xế nữ khi hoạt động về đêm, các nền tảng công nghệ tăng cường các tính năng bảo vệ.', 0.92),
(114, 'Phân tích về thu nhập và chi phí (xăng xe, bảo trì) của tài xế xe ôm công nghệ trong bối cảnh giá cả leo thang.', 0.91),
(114, 'Các nền tảng công nghệ tăng cường đào tạo về kỹ năng giao tiếp, ứng xử và an toàn giao thông cho tài xế.', 0.88),
(114, 'Vấn đề bảo hiểm xã hội và bảo hiểm y tế cho tài xế công nghệ, đảm bảo quyền lợi an sinh xã hội.', 0.93),
(114, 'Các trường hợp tài xế công nghệ bị khách hàng hủy chuyến, bùng hàng, gây thiệt hại về thời gian và chi phí.', 0.90),
(114, 'Nhu cầu về việc thành lập công đoàn hoặc hiệp hội bảo vệ quyền lợi hợp pháp cho đội ngũ tài xế công nghệ.', 0.87),
(114, 'Các nền tảng công nghệ áp dụng các chương trình thưởng, hỗ trợ nhiên liệu để giữ chân và thu hút tài xế.', 0.86),
(114, 'Các trường hợp tài xế công nghệ vi phạm luật giao thông, yêu cầu các nền tảng có biện pháp xử lý nghiêm.', 0.94),
(114, 'Phân tích về sự cạnh tranh giữa taxi truyền thống và taxi công nghệ trong việc thu hút khách hàng và tài xế.', 0.85),
(114, 'Các trường hợp khách hàng tố cáo tài xế công nghệ có thái độ phục vụ không tốt, lừa đảo, hoặc gian lận cước phí.', 0.95),
(114, 'Các nền tảng công nghệ tăng cường các tính năng phản hồi và đánh giá dịch vụ, nâng cao chất lượng phục vụ.', 0.81),
(114, 'Việc sử dụng xe máy điện và ô tô điện làm phương tiện chạy taxi, xe ôm công nghệ, giảm chi phí nhiên liệu.', 0.88),
(114, 'Các chuyên gia kiến nghị về việc minh bạch hóa chính sách chiết khấu và cơ chế tính cước phí của các hãng công nghệ.', 0.79),
(114, 'Nhu cầu về các chính sách hỗ trợ vay vốn mua xe máy, ô tô cho tài xế công nghệ hoạt động lâu năm.', 0.83),
(114, 'Các trường hợp tài xế công nghệ gặp tai nạn giao thông trong quá trình hoạt động, cần có chính sách bảo hiểm nghề nghiệp.', 0.92),
(114, 'Phân tích về thời gian làm việc và áp lực công việc đối với tài xế công nghệ, ảnh hưởng đến sức khỏe.', 0.90),
(114, 'Các nền tảng công nghệ mở rộng dịch vụ (giao hàng, đi chợ hộ) để tăng thêm thu nhập cho tài xế.', 0.77),
(114, 'Quy định về việc kiểm tra, đăng ký phương tiện và cấp phép hoạt động cho tài xế công nghệ.', 0.85),
(114, 'Các trường hợp tài xế công nghệ bị cướp giật, hành hung, yêu cầu tăng cường biện pháp an ninh, phòng ngừa.', 0.91),
(114, 'Phân tích về sự khác biệt trong thu nhập và chi phí giữa tài xế taxi công nghệ và xe ôm công nghệ.', 0.82),
(114, 'Các nền tảng công nghệ áp dụng công nghệ AI để tối ưu hóa tuyến đường, giảm chi phí và thời gian cho tài xế.', 0.94),
(114, 'Các chuyên gia khuyến nghị về việc tăng cường giáo dục pháp luật và kỹ năng xử lý tình huống cho tài xế.', 0.84),
(114, 'Nhu cầu về các chương trình phúc lợi, khám sức khỏe định kỳ cho đội ngũ tài xế công nghệ.', 0.76),
(114, 'Các trường hợp tranh chấp về giá cước và phương thức thanh toán giữa khách hàng và tài xế công nghệ.', 0.88),
(114, 'Quy hoạch các khu vực đón trả khách riêng cho xe ôm, taxi công nghệ tại các thành phố lớn.', 0.86),
(114, 'Các trường hợp tài xế công nghệ bị đánh giá thấp, bị khóa tài khoản do vi phạm quy định của nền tảng.', 0.83),
(114, 'Phân tích về vai trò của tài xế công nghệ trong việc cung cấp dịch vụ vận tải linh hoạt và tiện lợi.', 0.92),
(114, 'Các nền tảng công nghệ tăng cường các biện pháp xác thực thông tin tài xế và khách hàng, đảm bảo an toàn.', 0.87),
(114, 'Chính sách hỗ trợ tài xế công nghệ chuyển đổi sang sử dụng xe điện và các phương tiện thân thiện môi trường.', 0.90);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(115, 'Ngành đường sắt tăng cường tàu phục vụ dịp Tết Nguyên đán, tăng tần suất và mở thêm các tuyến tàu phụ.', 0.95),
(115, 'Phát triển vận tải đường thủy nội địa tại Đồng bằng sông Cửu Long, giảm áp lực cho đường bộ và chi phí logistics.', 0.92),
(115, 'Tàu cao tốc đưa khách ra đảo Phú Quốc hoạt động trở lại sau thời gian bảo trì, phục vụ mùa du lịch cao điểm.', 0.91),
(115, 'Các tuyến tàu hỏa Bắc-Nam được nâng cấp toa xe, tăng cường dịch vụ tiện nghi, thu hút khách du lịch.', 0.88),
(115, 'Vấn đề an toàn giao thông đường thủy, đặc biệt là các tuyến phà và tàu chở khách du lịch trên sông.', 0.93),
(115, 'Các doanh nghiệp vận tải đường sắt áp dụng chính sách giảm giá vé, ưu đãi cho hành khách đặt vé sớm.', 0.90),
(115, 'Nhu cầu về vận tải hàng hóa bằng đường sắt tăng mạnh, đặc biệt là hàng hóa xuất nhập khẩu từ Trung Quốc sang Việt Nam.', 0.87),
(115, 'Chính phủ thúc đẩy việc hiện đại hóa đội tàu, phà và các phương tiện vận tải đường thủy nội địa.', 0.86),
(115, 'Các trường hợp tàu hỏa bị chậm giờ, ảnh hưởng đến lịch trình của hành khách và hoạt động vận tải hàng hóa.', 0.94),
(115, 'Các tuyến tàu cao tốc du lịch ven biển được mở mới, kết nối các điểm đến du lịch nổi tiếng.', 0.85),
(115, 'Nghiên cứu về việc sử dụng các loại tàu, phà chạy bằng năng lượng sạch (điện, khí nén) trên các tuyến đường thủy.', 0.81),
(115, 'Các trường hợp tai nạn đường sắt do lỗi kỹ thuật và sự cố trên đường, yêu cầu kiểm định và bảo trì thường xuyên.', 0.95),
(115, 'Việc áp dụng công nghệ số vào việc bán vé, quản lý hành khách và hàng hóa trên tàu hỏa và tàu thủy.', 0.88),
(115, 'Các chuyên gia kiến nghị về việc đầu tư vào hạ tầng (ga, bến tàu) để phục vụ vận tải đa phương thức.', 0.79),
(115, 'Các quy định về an ninh, an toàn và phòng chống cháy nổ trên các phương tiện vận tải đường thủy.', 0.83),
(115, 'Các tuyến tàu hỏa du lịch chuyên biệt (Luxury Train) được đưa vào khai thác, phục vụ khách du lịch cao cấp.', 0.92),
(115, 'Phân tích về chi phí vận tải hàng hóa bằng đường thủy so với đường bộ, ưu điểm về chi phí và tải trọng.', 0.90),
(115, 'Các trường hợp tàu, phà bị mắc cạn, va chạm do luồng lạch không đảm bảo và sự cố kỹ thuật.', 0.77),
(115, 'Chính sách hỗ trợ ngư dân chuyển đổi tàu cá sang tàu phục vụ du lịch và vận tải hành khách.', 0.85),
(115, 'Các trường hợp hành khách vi phạm quy định an toàn khi đi tàu, phà, yêu cầu xử lý nghiêm minh.', 0.91),
(115, 'Phân tích về vai trò của vận tải đường sắt trong việc kết nối các khu công nghiệp và cảng biển.', 0.82),
(115, 'Các dự án nâng cấp và xây dựng mới các tuyến đường sắt tại khu vực miền Trung và Tây Nguyên.', 0.94),
(115, 'Việc sử dụng các loại phà trọng tải lớn để vận chuyển ô tô và xe tải qua các con sông lớn.', 0.84),
(115, 'Các trường hợp tranh chấp về tuyến đường và cạnh tranh không lành mạnh giữa các doanh nghiệp vận tải đường thủy.', 0.76),
(115, 'Quy hoạch phát triển các tuyến tàu hỏa ngoại ô, phục vụ nhu cầu đi lại của người dân đô thị.', 0.88),
(115, 'Các trường hợp tàu chở khách bị hủy chuyến do thời tiết xấu (bão, lũ lụt), ảnh hưởng đến lịch trình du lịch.', 0.86),
(115, 'Các quy định về việc kiểm tra, đăng ký và cấp phép hoạt động cho các phương tiện vận tải đường thủy.', 0.83),
(115, 'Phân tích về sự phục hồi của vận tải hành khách đường sắt sau khi dịch bệnh được kiểm soát.', 0.92),
(115, 'Các trường hợp tàu, phà bị quá tải, chở vượt số lượng hành khách cho phép, gây nguy hiểm.', 0.87),
(115, 'Chính sách đào tạo và bồi dưỡng thuyền viên, lái tàu có trình độ chuyên môn cao.', 0.90);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(116, 'Quốc hội thông qua Luật Đất đai (sửa đổi) với nhiều điểm mới về bồi thường, hỗ trợ và tái định cư, tác động lớn đến thị trường bất động sản.', 0.95),
(116, 'Phiên tòa xét xử vụ án kinh tế ngàn tỷ được dư luận quan tâm, thu hút sự theo dõi của hàng triệu người dân.', 0.93),
(116, 'Doanh nghiệp khởi kiện đối tác vi phạm hợp đồng thương mại, yêu cầu bồi thường thiệt hại và thực hiện nghĩa vụ.', 0.92),
(116, 'Chính phủ ban hành Nghị định hướng dẫn thi hành các điều khoản quan trọng của Bộ luật Lao động mới.', 0.88),
(116, 'Các vụ án hình sự liên quan đến tham nhũng, chức vụ được cơ quan điều tra tích cực mở rộng và khởi tố.', 0.94),
(116, 'Quốc hội xem xét thông qua Luật Bảo vệ quyền lợi người tiêu dùng, tăng cường trách nhiệm của doanh nghiệp.', 0.90),
(116, 'Các tranh chấp pháp lý về sở hữu trí tuệ giữa các công ty công nghệ lớn trên thị trường Việt Nam.', 0.87),
(116, 'Tòa án mở phiên xử phúc thẩm vụ án dân sự liên quan đến thừa kế tài sản và tranh chấp đất đai.', 0.91),
(116, 'Các chuyên gia pháp lý phân tích về các điểm mới trong Luật Dân sự và ảnh hưởng đến giao dịch hàng ngày.', 0.86),
(116, 'Vấn đề áp dụng luật pháp quốc tế trong giải quyết các tranh chấp thương mại và đầu tư nước ngoài tại Việt Nam.', 0.89),
(116, 'Các vụ kiện hành chính giữa người dân và cơ quan nhà nước về quyết định thu hồi đất, cấp phép xây dựng.', 0.85),
(116, 'Chính phủ chỉ đạo các cơ quan tư pháp đẩy nhanh tiến độ xử lý các vụ án kinh tế phức tạp.', 0.93),
(116, 'Hội thảo về việc sửa đổi Luật An toàn thông tin mạng, nhằm bảo vệ dữ liệu cá nhân và an ninh quốc gia.', 0.83),
(116, 'Các vụ án ly hôn có yếu tố nước ngoài và tranh chấp quyền nuôi con được Tòa án giải quyết.', 0.79),
(116, 'Luật mới quy định chi tiết về các hình thức phạt vi phạm hành chính trong lĩnh vực giao thông và môi trường.', 0.88);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(117, 'Chính phủ đề xuất giảm thuế VAT (Thuế giá trị gia tăng) để kích cầu tiêu dùng và hỗ trợ doanh nghiệp.', 0.95),
(117, 'Cục Thuế tăng cường kiểm tra các giao dịch thương mại điện tử (E-commerce), đảm bảo thu đúng, thu đủ thuế.', 0.93),
(117, 'Hướng dẫn quyết toán thuế thu nhập cá nhân hàng năm, các trường hợp được miễn giảm và thủ tục hoàn thuế.', 0.92),
(117, 'Các chính sách ưu đãi thuế thu nhập doanh nghiệp cho các ngành nghề công nghệ cao, nông nghiệp sạch.', 0.88),
(117, 'Việc áp dụng hóa đơn điện tử và các giải pháp số hóa trong công tác quản lý thuế, giảm thủ tục hành chính.', 0.94),
(117, 'Các chuyên gia phân tích về tác động của thuế tối thiểu toàn cầu (Global Minimum Tax) đối với doanh nghiệp FDI tại Việt Nam.', 0.90),
(117, 'Các trường hợp trốn thuế, gian lận thuế lớn bị cơ quan thuế phát hiện và xử lý theo quy định pháp luật.', 0.87),
(117, 'Bộ Tài chính lấy ý kiến về việc điều chỉnh mức thuế suất thuế tiêu thụ đặc biệt đối với một số mặt hàng.', 0.91),
(117, 'Các quy định về thuế chuyển nhượng bất động sản và các biện pháp chống thất thu thuế trong giao dịch nhà đất.', 0.86),
(117, 'Chính sách miễn, giảm, gia hạn thời hạn nộp thuế để hỗ trợ doanh nghiệp phục hồi sản xuất, kinh doanh.', 0.89),
(117, 'Các quy định mới về thuế thu nhập cá nhân đối với người làm việc từ xa (remote work) và freelancer.', 0.85),
(117, 'Cục Thuế cung cấp các dịch vụ hỗ trợ người nộp thuế thông qua cổng thông tin điện tử và ứng dụng di động.', 0.93),
(117, 'Các trường hợp tranh chấp về thuế giữa doanh nghiệp và cơ quan thuế, yêu cầu giải quyết theo quy trình.', 0.83),
(117, 'Việc áp dụng thuế tài sản (Tax on Property) đang được nghiên cứu để tăng nguồn thu ngân sách địa phương.', 0.79),
(117, 'Hướng dẫn mới về thuế suất ưu đãi cho các dự án đầu tư vào khu công nghiệp, khu kinh tế đặc biệt.', 0.88);
INSERT INTO topic_documents (topic_id, text, weight) VALUES
(118, 'Ngân hàng Nhà nước điều chỉnh lãi suất điều hành để kiểm soát lạm phát và ổn định thị trường tiền tệ.', 0.95),
(118, 'Các ngân hàng thương mại đẩy mạnh cho vay tiêu dùng cuối năm, tập trung vào các gói tín dụng ưu đãi.', 0.93),
(118, 'Xu hướng thanh toán không dùng tiền mặt và phát triển ngân hàng số (Digital Banking) tại Việt Nam.', 0.92),
(118, 'Ngân hàng Nhà nước tăng cường kiểm tra và giám sát chất lượng tín dụng, đặc biệt là nợ xấu trong lĩnh vực bất động sản.', 0.88),
(118, 'Các ngân hàng đưa ra các giải pháp tăng cường bảo mật giao dịch, chống lừa đảo trực tuyến và gian lận tài khoản.', 0.94),
(118, 'Thị trường ngoại hối có nhiều biến động, tỷ giá hối đoái USD/VND được Ngân hàng Nhà nước theo dõi sát sao.', 0.90),
(118, 'Các ngân hàng triển khai các chính sách cơ cấu lại nợ, giãn nợ cho các doanh nghiệp gặp khó khăn.', 0.87),
(118, 'Vốn hóa thị trường của các ngân hàng thương mại niêm yết tăng trưởng mạnh, thu hút nhà đầu tư chứng khoán.', 0.91),
(118, 'Các chuyên gia phân tích về tác động của việc tăng trưởng tín dụng đến tăng trưởng kinh tế vĩ mô.', 0.86),
(118, 'Các ngân hàng thương mại mở rộng mạng lưới chi nhánh, phòng giao dịch để phục vụ khách hàng tại các vùng nông thôn.', 0.89),
(118, 'Các quy định mới về an toàn vốn (Basel II, Basel III) được áp dụng tại các ngân hàng thương mại Việt Nam.', 0.85),
(118, 'Thị trường liên ngân hàng hoạt động sôi động, lãi suất qua đêm có xu hướng tăng vào cuối quý.', 0.93),
(118, 'Các ngân hàng đầu tư vào công nghệ AI và Big Data để cá nhân hóa dịch vụ và đánh giá rủi ro tín dụng.', 0.83),
(118, 'Các vụ việc lừa đảo qua điện thoại, email liên quan đến ngân hàng, yêu cầu người dân cảnh giác.', 0.79),
